      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import {ENS} from "ens-contracts/contracts/registry/ENS.sol";
import {IWITController} from "./IWITController.sol";
import {Operable} from "./Operable.sol";
import {IBaseRegistrar} from "ens-contracts/contracts/ethregistrar/IBaseRegistrar.sol";
import {Resolver} from "ens-contracts/contracts/resolvers/Resolver.sol";

contract WhatIsTimeENSController is IWITController, Operable {
    ENS public constant ens = ENS(0x00000000000C2E074eC69A0dFb2997BA6C7d2e1e);
    IBaseRegistrar public constant ENSRegistrar = IBaseRegistrar(0x57f1887a8BF19b14fC0dF6Fd9B2acc9Af147eA85);

    mapping(bytes32 => uint64) private _subnodeExpiries;

    constructor() {
        _initializeOwner(0x420690b5cf42eD04c96563CC9687B13306f16eEC);
    }

    function setSubnodeRecord(
        bytes32 node,
        string calldata label,
        address owner,
        address resolver,
        uint64 ttl,
        uint64 expiry
    ) external override onlyOperator {
        bytes32 labelhash = keccak256(bytes(label));
        bytes32 subnode = _nameHash(node, labelhash);
        ens.setSubnodeRecord(node, labelhash, owner, resolver, ttl);
        _subnodeExpiries[subnode] = expiry;
    }

    function setSubnodeOwner(bytes32 node, string calldata label, address owner)
        external
        override
        onlyOperator
        returns (bytes32)
    {
        bytes32 labelhash = keccak256(bytes(label));
        bytes32 subnode = _nameHash(node, labelhash);
        ens.setSubnodeOwner(node, labelhash, owner);
        return subnode;
    }

    function setSubnodeExpiry(bytes32 node, string calldata label, uint64 expiry) external override onlyOperator {
        bytes32 labelhash = keccak256(bytes(label));
        bytes32 subnode = _nameHash(node, labelhash);
        _subnodeExpiries[subnode] = expiry;
    }

    function _nameHash(bytes32 parentNode, bytes32 labelhash) private pure returns (bytes32 b) {
        assembly {
            let memPtr := mload(0x40)
            mstore(memPtr, parentNode)
            mstore(add(memPtr, 0x20), labelhash)
            b := keccak256(memPtr, 0x40)
        }
    }

    function getNameData(bytes32 labelhash) external view returns (address owner, uint64 expiry) {
        uint256 id = uint256(labelhash);
        owner = ENSRegistrar.ownerOf(id);
        expiry = uint64(ENSRegistrar.nameExpires(id));
    }

    function getNodeData(bytes32 node) external view returns (address owner, uint64 expiry) {
        owner = ens.owner(node);
        expiry = _subnodeExpiries[node];
    }
}
