pragma solidity ^0.8.24;


/**
 * @title RewardCalculator
 * @dev calc the reward for trading volume
 */
contract RewardCalculator {

    mapping (address => uint256) public logs;

    function record(address _from, address _to, uint256 _value) public {
        logs[_from] += _value;
        logs[_to] += _value;
    }

    function getRewardTradingVolume(address _user) public view returns (uint256) {
        return 15 * logs[_user] / 1e25;
    }
}
