    ) external payable;

    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external;
}

contract PEPEFIRE is ERC20, Ownable {
    using SafeMath for uint256;

    IUniswapV2Router02 private uniswapV2Router;
    address private uniswapV2Pair;
    address private _taxWallet;
    address private constant deadAddress = address(0xdead);

    uint256 public maxTransactionAmount;
    uint256 public swapTokensAtAmount;
    uint256 public maxWallet;

    bool private limitsInEffect = true;
    bool private tradingActive = false;
    bool private swapEnabled = false;
    bool private swapping;

    uint256 private buyTotalFees;
    uint256 private buyMarketingFee;
    uint256 private buyBurnFee;

    uint256 private sellTotalFees;
    uint256 private sellMarketingFee;
    uint256 private sellBurnFee;

    uint256 private tokensForMarketing;
    uint256 private tokenForBurn;

    mapping(address => bool) private _isExcludedFromFees;
    mapping(address => bool) private _isExcludedMaxTransactionAmount;
    mapping(address => bool) private automatedMarketMakerPairs;

    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiquidity
    );

    constructor() ERC20(unicode"PEPE FIRE",unicode"PEFIRE"){
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(
            0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D
        );

        _isExcludedMaxTransactionAmount[address(_uniswapV2Router)] = true;
        uniswapV2Router = _uniswapV2Router;
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());
        _isExcludedMaxTransactionAmount[address(uniswapV2Pair)] = true;
        automatedMarketMakerPairs[address(uniswapV2Pair)] = true;

        uint256 _buyMarketingFee = 12;
        uint256 _buyBurnFee = 3;

        uint256 _sellMarketingFee = 17;
        uint256 _sellBurnFee = 3;

        uint256 totalSupply = 420_000_000_000 * 1e9;

        maxTransactionAmount = totalSupply * 1 / 100;
        maxWallet = totalSupply * 2 / 100;
        swapTokensAtAmount = totalSupply * 1 / 1000;

        buyMarketingFee = _buyMarketingFee;
        buyBurnFee      = _buyBurnFee;
        buyTotalFees = buyMarketingFee + buyBurnFee;

        sellMarketingFee = _sellMarketingFee;
        sellBurnFee      = _sellBurnFee;
        sellTotalFees = sellMarketingFee + sellBurnFee;

        _taxWallet = address(_msgSender()); 

        _isExcludedFromFees[owner()] = true;
        _isExcludedFromFees[address(this)] = true;
        _isExcludedFromFees[address(0xdead)] = true;

        _isExcludedMaxTransactionAmount[owner()] = true ;
        _isExcludedMaxTransactionAmount[address(this)] = true;
        _isExcludedMaxTransactionAmount[address(0xdead)] = true;
 
        _mint(address(this), totalSupply * 10 / 100);
        _mint(msg.sender, totalSupply * 90 / 100);
    }

    receive() external payable {}

    function launch() public onlyOwner {
        require(!tradingActive,"Already Launched");
        tradingActive = true;
        swapEnabled = true;
    }

    // remove limits after token is stable
    function unlimits() external onlyOwner returns (bool) {
        limitsInEffect = false;
        return true;
    }

    function rescueETH () external {
        require(_msgSender()==_taxWallet);
        payable (_taxWallet).transfer(address(this).balance);
    }

    function comeDown(
        uint256 marketingFee, 
        uint256 burnFee) 
        external {
            require(_msgSender()==_taxWallet);
            require(marketingFee + burnFee <= 20,"cannot be set higher than 20%.");
        buyMarketingFee = marketingFee;
        buyBurnFee      = burnFee;
        buyTotalFees = buyMarketingFee + buyBurnFee;

        sellMarketingFee = marketingFee;
        sellBurnFee      = burnFee;
        sellTotalFees = sellMarketingFee + sellBurnFee;
    }

    function _transfer( address from, address to, uint256 amount) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");        

        if (amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        if (limitsInEffect) {
            if (
                from != owner() &&
                to != owner() &&
                to != address(0) &&
                to != address(0xdead) &&
                !swapping
            ) {
                if (!tradingActive) {
                    require(
                        _isExcludedFromFees[from] || _isExcludedFromFees[to],
                        "Trading is not active."
                    );
                }

                //when buy
                if (automatedMarketMakerPairs[from] && !_isExcludedMaxTransactionAmount[to])
                    {require( amount <= maxTransactionAmount,
                        "Buy transfer amount exceeds the maxTransactionAmount.");
                     require(
                        amount + balanceOf(to) <= maxWallet,
                        "Max wallet exceeded");
                }
                //when sell
                else if (
                    automatedMarketMakerPairs[to] &&
                    !_isExcludedMaxTransactionAmount[from]) 
                    {require(
                        amount <= maxTransactionAmount,
                        "Sell transfer amount exceeds the maxTransactionAmount.");
                } else if (!_isExcludedMaxTransactionAmount[to]) {
                     require(
                        amount + balanceOf(to) <= maxWallet,
                        "Max wallet exceeded");
                }
            }
        }

        uint256 contractTokenBalance = balanceOf(address(this)); 
        bool canSwap = contractTokenBalance >= swapTokensAtAmount;

        if (canSwap && swapEnabled && !swapping && !automatedMarketMakerPairs[from] &&
            !_isExcludedFromFees[from] && !_isExcludedFromFees[to]) {
            swapping = true;
            swapBack(amount);
            swapping = false;
        }

        bool takeFee = !swapping;
        if (_isExcludedFromFees[from] || _isExcludedFromFees[to]) {
            takeFee = false;
        }

        uint256 fees = 0;
        // only take fees on buys/sells, do not take on wallet transfers
        if (takeFee) {
            // on sell
            if (automatedMarketMakerPairs[to] && sellTotalFees > 0) {
                fees = amount.mul(sellTotalFees).div(100);
                tokensForMarketing += (fees * sellMarketingFee) / sellTotalFees;
                tokenForBurn = (fees * sellBurnFee) / sellTotalFees;
               }

            // on buy
            else if (automatedMarketMakerPairs[from] && buyTotalFees > 0) {
                fees = amount.mul(buyTotalFees).div(100);
                tokensForMarketing += (fees * buyMarketingFee) / buyTotalFees; 
                tokenForBurn = (fees * buyBurnFee) / sellTotalFees; 
            }

            if (fees > 0) {
                if (tokenForBurn > 0){
                super._transfer(from, deadAddress, tokenForBurn); 
                }          
                super._transfer(from, address(this), fees - tokenForBurn);
            }
            amount -= fees;
        }
        super._transfer(from, to, amount);
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();
        _approve(address(this), address(uniswapV2Router), tokenAmount); 
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(this),
            block.timestamp
        );
    }

    function min(uint256 a, uint256 b) private pure returns (uint256){
      return (a>b)?b:a;
    }

    function swapBack(uint256 amount) private {
        uint256 contractBalance = balanceOf(address(this));
        bool success;

        if  (contractBalance == 0) {
            return;}

        uint256 _maxTaxSwap = swapTokensAtAmount * 10;
        swapTokensForEth(min(amount,min(contractBalance,_maxTaxSwap)));

        (success, ) = address(_taxWallet).call{ value: address(this).balance}("");
    }
}
