contract SparkSupply {
    using Address for address;
    using SafeERC20 for IERC20;

    event LogEvent(string name, bytes data);

    // aave
    IPoolAddressesProvider public immutable provider;
    IPool public immutable pool;

    address public immutable WETH;

    constructor(IPoolAddressesProvider _aaveProvider, address _WETH) {
        provider = _aaveProvider;
        pool = IPool(_aaveProvider.getPool());
        WETH = _WETH;
    }

    function enterETH(uint256 _amount) external payable {
        require(_amount > 0, "zero amount");

        uint256 balance = address(this).balance;
        require(balance >= _amount, "transfer amount exceeds balance");

        IWETH(WETH).deposit{value: _amount}();
        enter(WETH, _amount);

        bytes memory logData = abi.encode(_amount);
        emit LogEvent("EnterETH", logData);
    }

    function enter(address _asset, uint256 _amount) public {
        require(_amount > 0, "zero amount");

        uint256 balance = IERC20(_asset).balanceOf(address(this));
        require(balance >= _amount, "transfer amount exceeds balance");

        // supply
        IERC20(_asset).safeApprove(address(pool), 0);
        IERC20(_asset).safeApprove(address(pool), type(uint256).max);
        pool.supply(_asset, _amount, address(this), 0);
        IERC20(_asset).safeApprove(address(pool), 0);

        bytes memory logData = abi.encode(_asset, _amount);
        emit LogEvent("Enter", logData);
    }

    function exitETH() external {
        (uint256 supplyAmount, uint256 withdrawAmount) = exit(WETH);
        IWETH(WETH).withdraw(withdrawAmount);

        bytes memory logData = abi.encode(supplyAmount, withdrawAmount);
        emit LogEvent("ExitETH", logData);
    }

    function exit(address _asset) public returns (uint256, uint256) {
        (uint256 supplyAmount, ) = getSupplyBalance(_asset, address(this));
        require(supplyAmount > 0, "supply amount is zero");

        // withdraw
        uint256 withdrawAmount = pool.withdraw(_asset, type(uint256).max, address(this));

        bytes memory logData = abi.encode(_asset, supplyAmount, withdrawAmount);
        emit LogEvent("Exit", logData);

        return (supplyAmount, withdrawAmount);
    }

    function getSupplyBalance(address supplyToken, address user) public view returns (uint256, uint256) {
        IAToken aToken = IAToken(pool.getReserveData(supplyToken).aTokenAddress);
        uint256 balance = aToken.balanceOf(user);
        uint256 decimals = ERC20(address(aToken)).decimals();
        return (balance, decimals);
    }

    function getPoolBalance(address supplyToken) public view returns (uint256, uint256) {
        address aToken = pool.getReserveData(supplyToken).aTokenAddress;
        uint256 balance = ERC20(supplyToken).balanceOf(aToken);
        uint256 decimals = ERC20(supplyToken).decimals();
        return (balance, decimals);
    }

    receive() external payable {}
}
