      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./library/IBEP20.sol";
import "./library/SafeBEP20.sol";

contract Staking is Ownable {
    using SafeMath for uint256;
    using SafeBEP20 for IBEP20;
    // Info of each user.
    struct UserInfo {
        uint256 amount; // How many LP tokens the user has provided.
        uint256 rewardDebt; // Reward debt. See explanation below.
        //
        // We do some fancy math here. Basically, any point in time, the amount of AIVs
        // entitled to a user but is pending to be distributed is:
        //
        //   pending reward = (user.amount * pool.accAIVPerShare) - user.rewardDebt
        //
        // Whenever a user deposits or withdraws LP tokens to a pool. Here's what happens:
        //   1. The pool's `accAIVPerShare` (and `lastRewardBlock`) gets updated.
        //   2. User receives the pending reward sent to his/her address.
        //   3. User's `amount` gets updated.
        //   4. User's `rewardDebt` gets updated.
    }

    // Info of each pool.
    struct PoolInfo {
        IBEP20 lpToken; // Address of LP token contract.
        uint256 allocPoint; // How many allocation points assigned to this pool. AIVs to distribute per block.
        uint256 lastRewardBlock; // Last block number that AIVs distribution occurs.
        uint256 accAIVPerShare; // Accumulated AIVs per share, times 1e24. See below.
    }
    // AIV tokens created per block.
    uint256 public aivPerSecond;
    // Bonus muliplier for early aiv makers.
    uint256 public BONUS_MULTIPLIER = 1;

    // Info of each pool.
    PoolInfo[] public poolInfo;
    // Info of each user that stakes LP tokens.
    mapping(uint256 => mapping(address => UserInfo)) public userInfo;
    // Total allocation poitns. Must be the sum of all allocation points in all pools.
    uint256 public totalAllocPoint = 0;

    uint256 public totalClaimedReward = 0;
    uint256 public totalRewardAmount = 0;
    uint256 public totalStaked = 0;

    uint256 private cumulative_multiplier = 0;
    uint256 private max_multiplier = 180 days;

    // The block number when AIV mining starts.
    uint256 public startBlock;

    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event ClaimReward(address indexed user, uint256 amount);
    event EmergencyWithdraw(
        address indexed user,
        uint256 indexed pid,
        uint256 amount
    );

    constructor() {}

    function updateMultiplier(uint256 multiplierNumber) public onlyOwner {
        BONUS_MULTIPLIER = multiplierNumber;
    }

    function start(uint256 _startTime) public onlyOwner {
        aivPerSecond = totalRewardAmount / max_multiplier;
        startBlock = _startTime;
    }

    function addLpToken(IBEP20 _lpToken, uint256 _amount) public onlyOwner {
        IBEP20(_lpToken).safeTransferFrom(
            address(msg.sender),
            address(this),
            _amount
        );
        totalRewardAmount += _amount;
    }
    function recoverToken(IBEP20 _lpToken, uint256 _amount) public onlyOwner {
        IBEP20(_lpToken).safeTransfer(msg.sender, _amount);
    }

    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    // Add a new lp to the pool. Can only be called by the owner.
    // XXX DO NOT add the same LP token more than once. Rewards will be messed up if you do.
    function add(
        uint256 _allocPoint,
        IBEP20 _lpToken,
        bool _withUpdate
    ) public onlyOwner {
        if (_withUpdate) {
            massUpdatePools();
        }
        uint256 lastRewardBlock = block.timestamp > startBlock
            ? block.timestamp
            : startBlock;
        totalAllocPoint = totalAllocPoint.add(_allocPoint);
        poolInfo.push(
            PoolInfo({
                lpToken: _lpToken,
                allocPoint: _allocPoint,
                lastRewardBlock: lastRewardBlock,
                accAIVPerShare: 0
            })
        );
        updateStakingPool();
    }

    function updateStakingPool() internal {
        uint256 length = poolInfo.length;
        uint256 points = 0;
        for (uint256 pid = 1; pid < length; ++pid) {
            points = points.add(poolInfo[pid].allocPoint);
        }
        if (points != 0) {
            points = points.div(3);
            totalAllocPoint = totalAllocPoint.sub(poolInfo[0].allocPoint).add(
                points
            );
            poolInfo[0].allocPoint = points;
        }
    }

    // Return reward multiplier over the given _from to _to block.
    function getMultiplier(
        uint256 _from,
        uint256 _to
    ) public view returns (uint256) {
        return _to.sub(_from).mul(BONUS_MULTIPLIER);
    }

    // View function to see pending AIVs on frontend.
    function pendingAIV(
        uint256 _pid,
        address _user
    ) external view returns (uint256) {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_user];
        uint256 accAIVPerShare = pool.accAIVPerShare;
        uint256 lpSupply = totalStaked;
        if (block.timestamp > pool.lastRewardBlock && lpSupply != 0) {
            uint256 multiplier = getMultiplier(
                pool.lastRewardBlock,
                block.timestamp
            );
            if (cumulative_multiplier + multiplier > max_multiplier) {
                multiplier = max_multiplier - cumulative_multiplier;
            }
            uint256 aivReward = multiplier
                .mul(aivPerSecond)
                .mul(pool.allocPoint)
                .div(totalAllocPoint);

            accAIVPerShare = accAIVPerShare.add(
                aivReward.mul(1e24).div(lpSupply)
            );
        }
        return user.amount.mul(accAIVPerShare).div(1e24).sub(user.rewardDebt);
    }

    // Update reward variables for all pools. Be careful of gas spending!
    function massUpdatePools() public {
        uint256 length = poolInfo.length;
        for (uint256 pid = 0; pid < length; ++pid) {
            updatePool(pid);
        }
    }

    // Update reward variables of the given pool to be up-to-date.
    function updatePool(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        if (block.timestamp <= pool.lastRewardBlock) {
            return;
        }
        uint256 lpSupply = totalStaked;
        if (lpSupply == 0) {
            pool.lastRewardBlock = block.timestamp;
            return;
        }
        uint256 multiplier = getMultiplier(
            pool.lastRewardBlock,
            block.timestamp
        );
        if (cumulative_multiplier + multiplier > max_multiplier) {
            multiplier = max_multiplier - cumulative_multiplier;
        }
        uint256 aivReward = multiplier
            .mul(aivPerSecond)
            .mul(pool.allocPoint)
            .div(totalAllocPoint);

        pool.accAIVPerShare = pool.accAIVPerShare.add(
            aivReward.mul(1e24).div(lpSupply)
        );
        pool.lastRewardBlock = block.timestamp;
        cumulative_multiplier += multiplier;
    }

    // Deposit LP tokens to Staking for AIV allocation.
    function compound() public {
        PoolInfo storage pool = poolInfo[0];
        UserInfo storage user = userInfo[0][msg.sender];
        updatePool(0);
        uint256 pending = 0;
        if (user.amount > 0) {
            pending = user.amount.mul(pool.accAIVPerShare).div(1e24).sub(
                user.rewardDebt
            );
            if (pending > 0) {
                safeAIVTransfer(pool.lpToken, msg.sender, pending);
            }
        }
        if (pending > 0) {
            pool.lpToken.safeTransferFrom(
                address(msg.sender),
                address(this),
                pending
            );
            user.amount = user.amount.add(pending);
        }
        user.rewardDebt = user.amount.mul(pool.accAIVPerShare).div(1e24);
        totalStaked += pending;
        emit Deposit(msg.sender, 0, pending);
    }

    function deposit(uint256 _amount) public {
        PoolInfo storage pool = poolInfo[0];
        UserInfo storage user = userInfo[0][msg.sender];
        updatePool(0);
        if (user.amount > 0) {
            uint256 pending = user
                .amount
                .mul(pool.accAIVPerShare)
                .div(1e24)
                .sub(user.rewardDebt);
            if (pending > 0) {
                safeAIVTransfer(pool.lpToken, msg.sender, pending);
            }
        }
        if (_amount > 0) {
            pool.lpToken.safeTransferFrom(
                address(msg.sender),
                address(this),
                _amount
            );
            user.amount = user.amount.add(_amount);
        }
        user.rewardDebt = user.amount.mul(pool.accAIVPerShare).div(1e24);
        totalStaked += _amount;

        emit Deposit(msg.sender, 0, _amount);
    }

    // Withdraw LP tokens from Staking.
    function withdraw(uint256 _amount) public {
        PoolInfo storage pool = poolInfo[0];
        UserInfo storage user = userInfo[0][msg.sender];
        require(user.amount >= _amount, "withdraw: not good");
        updatePool(0);
        uint256 pending = user.amount.mul(pool.accAIVPerShare).div(1e24).sub(
            user.rewardDebt
        );
        if (pending > 0) {
            safeAIVTransfer(pool.lpToken, msg.sender, pending);
        }
        if (_amount > 0) {
            user.amount = user.amount.sub(_amount);
            pool.lpToken.safeTransfer(address(msg.sender), _amount);
        }
        user.rewardDebt = user.amount.mul(pool.accAIVPerShare).div(1e24);
        totalStaked -= _amount;
        emit Withdraw(msg.sender, 0, _amount);
    }

    // Withdraw without caring about rewards. EMERGENCY ONLY.
    function emergencyWithdraw() public {
        PoolInfo storage pool = poolInfo[0];
        UserInfo storage user = userInfo[0][msg.sender];
        pool.lpToken.safeTransfer(address(msg.sender), user.amount);
        emit EmergencyWithdraw(msg.sender, 0, user.amount);
        user.amount = 0;
        user.rewardDebt = 0;
    }
    // Safe aiv transfer function, just in case if rounding error causes pool to not have enough AIVs.
    function safeAIVTransfer(
        IBEP20 _lpToken,
        address _to,
        uint256 _amount
    ) internal {
        uint256 goodReward = _amount;
        if (totalRewardAmount - totalClaimedReward < _amount) {
            goodReward = totalRewardAmount - totalClaimedReward;
        }
        if (goodReward <= 0) {
            return;
        }

        IBEP20(_lpToken).safeTransfer(_to, goodReward);
        totalClaimedReward += _amount;
        emit ClaimReward(msg.sender, _amount);
    }
}
