pragma solidity 0.8.22;

interface IMerkleTreeWhitelist {
    /**
     * @notice Sets the root hash of the Merkle tree used for on-chain proof verification.
     * @dev if `root` is `bytes32(type(uint256).max)`, Merkle Tree verification is disabled.
     *      (see [`IPool.deposit()`](/interfaces/liquidityManagement/liquidityPool/IPool.sol/interface.IPool.html#deposit)).
     * @param root new hash root of the Merkle tree.
     */
    function setRoot(bytes32 root) external;

    /**
     * @notice Retrieves the current root hash of the Merkle tree used for on-chain proof verification.
     * @return root current hash root of the Merkle tree.
     */
    function getRoot() external view returns (bytes32 root);
}
