      "content": "// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.22;

import {MerkleProofLib} from "solady/src/utils/MerkleProofLib.sol";

library MerkleTreeRepository {
    bytes32 internal constant DISABLE_VERIFICATION = bytes32(type(uint256).max);
    bytes32 private constant ROOT_SLOT = keccak256("MerkleTreeRepository root slot V1");

    function setRoot(bytes32 _newRoot) internal {
        bytes32 slot = ROOT_SLOT;
        assembly {
            sstore(slot, _newRoot)
        }
    }

    function getRoot() internal view returns (bytes32 root) {
        bytes32 slot = ROOT_SLOT;
        assembly {
            root := sload(slot)
        }
    }

    function verify(
        bytes32[] calldata _proof,
        address _leaf,
        bytes32 _root
    ) internal pure returns (bool) {
        if (_root == DISABLE_VERIFICATION) return true;
        else return MerkleProofLib.verifyCalldata(_proof, _root, computeLeaf(_leaf));
    }

    function computeLeaf(address _leaf) private pure returns (bytes32) {
        return keccak256(abi.encode(_leaf));
    }
}
"
