pragma solidity 0.8.22;

import {
    IMerkleTreeWhitelist
} from "contracts/interfaces/liquidityManagement/liquidityPool/IMerkleTreeWhitelist.sol";
import {OwnableReadonlyDS} from "contracts/base/auth/OwnableReadonlyDS.sol";
import {MerkleTreeRepository} from "./libraries/MerkleTreeRepository.sol";

contract MerkleTreeWhitelist is IMerkleTreeWhitelist, OwnableReadonlyDS {
    using MerkleTreeRepository for bytes32[];

    function setRoot(bytes32 newRoot) external override onlyOwner {
        MerkleTreeRepository.setRoot(newRoot);
    }

    function getRoot() external view override returns (bytes32) {
        return MerkleTreeRepository.getRoot();
    }
}
