// File: contracts/NFT.sol



pragma solidity ^0.8.20;





contract NFT is ERC721, Ownable, ReentrancyGuard {
    using Strings for uint256;

    uint public MAX_TOKENS = 500;
    uint public price = 0.32 ether;
    uint256 public MAX_MINT_PER_WALLET = 25;

    bool public isSaleActive;
    uint256 public mintedSupply;
    mapping(address => uint256) private mintedPerWallet;
    mapping(address => uint256[]) private ownedTokens;
    // New mapping to track if a referrer has minted at least one NFT
    mapping(address => bool) public mintedByReferrer;

    mapping(address => bool) private whitelistedAddresses;
    address[] private whitelistedAddressesList;
    bool public whitelistEnabled = true;

    uint256 public referalPercentage = 5;

    string public baseUri;
    string public baseExtension = ".json";

    enum LaunchStage { WHITELIST_ONLY, PUBLIC_WITH_WHITELIST_REFERRAL, PUBLIC_WITH_ANY_REFERRAL }
    LaunchStage public launchStage;

    constructor(address _owner) ERC721("Peppermint FX", "PFX") Ownable(_owner) {
        baseUri = "ipfs://Qmc8AXpdohmh2vzxsab5ydjeyyJRjwunERRnBwj2xUXWvb/";
    }

    // Modifier to restrict access to token owner
    modifier onlyTokenOwner(address _addr) {
        require(msg.sender == _addr, "Caller is not the token owner");
        _;
    }

    modifier onlyWhitelisted() {
        require(!whitelistEnabled || whitelistedAddresses[msg.sender], "Not a whitelisted address");
        _;
    }

    function isWhitelisted(address _address) public view returns (bool) {
        return (whitelistedAddresses[_address]);
    }

    function getWhitelistedAddresses() public view returns (address[] memory) {
        return whitelistedAddressesList;
    }

    // Public function to get the number of NFTs a user can still mint
    function getAvailableToMint(address _addr) public view returns (uint256) {
        return MAX_MINT_PER_WALLET - mintedPerWallet[_addr];
    }

    
   function mint(uint256 _numTokens, address _referrer) external payable {
    require(isSaleActive, "The sale is paused.");
    
    uint256 availableToMint = getAvailableToMint(msg.sender);
    require(availableToMint > 0, "You have already minted the maximum allowed NFTs.");
    
    if (_numTokens > availableToMint) {
        _numTokens = availableToMint;
    }
    
    uint256 curTotalSupply = mintedSupply;
    require(curTotalSupply + _numTokens <= MAX_TOKENS, "Exceeds total supply.");
    require(_numTokens * price <= msg.value, "Insufficient funds.");

    bool isValidReferrer = _referrer != address(0) && mintedByReferrer[_referrer];

    if (launchStage == LaunchStage.WHITELIST_ONLY) {
        require(whitelistedAddresses[msg.sender], "Not a whitelisted address.");
        require(_referrer == address(0), "Referral not allowed during whitelist-only stage.");
    } else if (launchStage == LaunchStage.PUBLIC_WITH_WHITELIST_REFERRAL) {
        require(whitelistedAddresses[_referrer], "Referrer must be a whitelisted address.");
        require(isValidReferrer, "Referrer address is not valid and have minted at least one NFT.");
    } else if (launchStage == LaunchStage.PUBLIC_WITH_ANY_REFERRAL) {
        if (_referrer != address(0)) {
            require(isValidReferrer, "Referrer must have minted at least one NFT.");
        }
    }
    
    for (uint256 i = 1; i <= _numTokens; ++i) {
        uint256 tokenId = curTotalSupply + i;
        _safeMint(msg.sender, tokenId);
        ownedTokens[msg.sender].push(tokenId);
    }
    
    mintedPerWallet[msg.sender] += _numTokens;
    mintedSupply += _numTokens;
    
    mintedByReferrer[msg.sender] = true;
    
    uint256 totalCost = _numTokens * price;
    uint256 referrerShare = (totalCost * referalPercentage) / 100;
    uint256 ownerShare = totalCost;

    if (isValidReferrer) {
        ownerShare = totalCost - referrerShare;
        (bool referrerSuccess, ) = _referrer.call{value: referrerShare}("");
        require(referrerSuccess, "Referrer transfer failed.");
    }
    
    (bool ownerSuccess, ) = owner().call{value: ownerShare}("");
    require(ownerSuccess, "Owner transfer failed.");
 }

    function getEthBalance(address _addr) view public returns(uint256) {
        return _addr.balance;
    }

    // New function to get the token IDs minted by a specific address
    function getMyTokens(address _addr) external view onlyTokenOwner(_addr) returns (uint256[] memory) {
        return ownedTokens[_addr];
    }

    function getDetails() external view returns (uint256 Price, uint256 maxTokens, uint256 maxMintPerWallet, uint256 nftMintedSupply, bool isNftSaleActive, bool isWhitelistEnabled, LaunchStage _launchStage) {
        return(price, MAX_TOKENS, MAX_MINT_PER_WALLET, mintedSupply, isSaleActive, whitelistEnabled, launchStage);
    }

    // Override transfer functions to update ownedTokens mapping
    function transferFrom(address from, address to, uint256 tokenId) public virtual override {
        super.transferFrom(from, to, tokenId);
        _updateOwnedTokens(from, to, tokenId);
    }

    function _updateOwnedTokens(address from, address to, uint256 tokenId) internal {
        // Remove the token from the list of the sender
        uint256[] storage fromTokens = ownedTokens[from];
        for (uint256 i = 0; i < fromTokens.length; i++) {
            if (fromTokens[i] == tokenId) {
                fromTokens[i] = fromTokens[fromTokens.length - 1];
                fromTokens.pop();
                break;
            }
        }

        // Add the token to the list of the recipient
        ownedTokens[to].push(tokenId);
    }

    // -------------------------------------------Owner-only functions---------------------------------

    function flipSaleState() external onlyOwner {
        isSaleActive = !isSaleActive;
    }

    function setBaseUri(string memory _baseUri) external onlyOwner {
        baseUri = _baseUri;
    }

    function setPrice(uint256 _price) external onlyOwner {
        price = _price;
    }

    function setMaxMintTokensPerTx(uint256 _MaxMintPerWallet) external onlyOwner {
        MAX_MINT_PER_WALLET = _MaxMintPerWallet;
    }

    function setMaxTokens(uint _maxTokens) external onlyOwner {
        MAX_TOKENS = _maxTokens;
    }

    function setReferalPercentage(uint256 _referalPercentage) external onlyOwner {
        referalPercentage = _referalPercentage;
    }

    function setLaunchStage(LaunchStage _stage) external onlyOwner {
        launchStage = _stage;
    }

    function addAddressToWhitelist(address _address) external onlyOwner {
        if (!whitelistedAddresses[_address]) {
            whitelistedAddresses[_address] = true;
            whitelistedAddressesList.push(_address);
        }
    }
    
    function enableWhitelist() external onlyOwner {
        whitelistEnabled = true;
    }

    function disableWhitelist() external onlyOwner {
        whitelistEnabled = false;
    }

    function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        _requireOwned(tokenId);

        string memory currentBaseURI = _baseURI();
        return bytes(currentBaseURI).length > 0
            ? string(abi.encodePacked(currentBaseURI, tokenId.toString(), baseExtension))
            : "";
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return baseUri;
    }
}







// File: contracts/Token.sol



pragma solidity 0.8.20;







contract NFTStakingWithRewards is ERC20, ERC20Burnable, Ownable, IERC721Receiver {

    uint256 public totalStaked;
    uint256 public REWARD_PER_DAY = 24 * 1e18; // Reward per day
    uint256 private REWARD_PER_MINUTE = REWARD_PER_DAY / 1440; // 1440 minutes in a day
    uint256 public claimCooldown = 300; // Cooldown period for claiming rewards (5 minutes)

    IERC20 public usdtToken; // Added USDT token interface
    uint256 public pfxSwapPrice = 1e18; // Price of 1 PFX token in terms of USDT, scaled by 1e18
    uint256 public minPFXSwapAmount = 20 * 1e18; //50 * 1e18 tokens

    struct Stake {
        uint24 tokenId;
        uint48 timestamp;
        address owner;
        uint48 lastClaimed;
        uint256 accumulatedRewards; // Added to accumulate rewards
    }

    struct EarningInfo {
        uint256 tokenId;
        uint256 stakedTimestamp;
        uint256 lastClaimedTimestamp;
        uint256 earned;
    }

    event NFTStaked(address owner, uint256 tokenId, uint256 value);
    event NFTUnstaked(address owner, uint256 tokenId, uint256 value);
    event Claimed(address owner, uint256 amount);
    event Swapped(address indexed user, uint256 pfxAmount, uint256 usdtAmount); // Added event for swap

    NFT public nft;

    mapping(uint256 => Stake) public vault;

    constructor(NFT _nft, address _owner, address _usdtTokenAddress) ERC20("PFXRewards", "PFX") Ownable(_owner) {
        nft = _nft;
        usdtToken = IERC20(_usdtTokenAddress); // Initialize USDT token
    }

    function stake(uint256[] calldata tokenIds) external {
        require(tokenIds.length > 0, "Must stake at least one token");
        uint256 tokenId;
        totalStaked += tokenIds.length;
        for (uint i = 0; i < tokenIds.length; i++) {
            tokenId = tokenIds[i];
            require(nft.ownerOf(tokenId) == msg.sender, "not your token");
            require(vault[tokenId].tokenId == 0, 'already staked');

            nft.transferFrom(msg.sender, address(this), tokenId);
            emit NFTStaked(msg.sender, tokenId, block.timestamp);

            vault[tokenId] = Stake({
                owner: msg.sender,
                tokenId: uint24(tokenId),
                timestamp: uint48(block.timestamp),
                lastClaimed: uint48(block.timestamp),
                accumulatedRewards: 0 // Initialize to zero
            });
        }
    }

    function _unstake(address account, uint256[] calldata tokenIds) internal {
        require(tokenIds.length > 0, "Must unstake at least one token");
        uint256 tokenId;
        totalStaked -= tokenIds.length;
        for (uint i = 0; i < tokenIds.length; i++) {
            tokenId = tokenIds[i];
            Stake storage staked = vault[tokenId];
            require(staked.owner == account, "not an owner");

            if (block.timestamp >= staked.lastClaimed + claimCooldown) {
                _claim(account, tokenIds);
                nft.transferFrom(address(this), account, tokenId);
                delete vault[tokenId];
                emit NFTUnstaked(account, tokenId, block.timestamp);
            } else {
                nft.transferFrom(address(this), account, tokenId);
                delete vault[tokenId];
                emit NFTUnstaked(account, tokenId, block.timestamp);
            }
        }
    }

    function claim(uint256[] calldata tokenIds) external {
        _claim(msg.sender, tokenIds);
    }

    function unstake(uint256[] calldata tokenIds) external {
        _unstake(msg.sender, tokenIds);
    }

    function _claim(address account, uint256[] calldata tokenIds) internal {
        require(tokenIds.length > 0, "Must claim at least one token");
        uint256 tokenId;
        uint256 earned = 0;
        bool anyClaimed = false; // Flag to check if any claims were successful

        for (uint i = 0; i < tokenIds.length; i++) {
            tokenId = tokenIds[i];
            Stake storage staked = vault[tokenId];
            require(staked.owner == account, "Not an owner");

            // Check if the claim cooldown is met
            if (block.timestamp < staked.lastClaimed + claimCooldown) {
                continue; // Skip this token if the cooldown is not met
            }

            anyClaimed = true; // At least one claim is successful
            uint256 timeStaked = block.timestamp - staked.lastClaimed; // Calculate time since last claim

            // Calculate rewards based on minutes staked
            uint256 currentRewardPerMinute = REWARD_PER_MINUTE;
            uint256 oldAccumulatedRewards = staked.accumulatedRewards;
            
            // Calculate total earned rewards including old accumulated rewards
            earned += (currentRewardPerMinute * timeStaked) / 1 minutes + oldAccumulatedRewards;

            // Update the stake with the new last claimed timestamp and reset accumulated rewards
            vault[tokenId] = Stake({
                owner: account,
                tokenId: uint24(tokenId),
                timestamp: uint48(staked.timestamp),
                lastClaimed: uint48(block.timestamp),
                accumulatedRewards: 0 // Reset after claiming
            });
        }

        require(anyClaimed, "No NFTs met the claim cooldown"); // Revert if no claims were successful

        if (earned > 0) {
            _mint(account, earned);
            emit Claimed(account, earned);
        }
    }

    function setRewardsPerDay(uint256 _rewardsPerDay) external onlyOwner {
        require(_rewardsPerDay > 0, "Reward must be greater than zero");

        // Calculate and lock in old rewards for all staked NFTs before changing the reward rate
        for (uint i = 1; i <= nft.mintedSupply(); i++) {
            Stake storage staked = vault[i];
            if (staked.tokenId > 0) { // Ensure the NFT is staked
                uint256 timeStaked = block.timestamp - staked.lastClaimed;
                staked.accumulatedRewards += (REWARD_PER_MINUTE * timeStaked) / 1 minutes; // Add old rewards
                staked.lastClaimed = uint48(block.timestamp); // Update last claimed timestamp
            }
        }

        // Now change the rewards per day
        REWARD_PER_DAY = _rewardsPerDay;
        REWARD_PER_MINUTE = REWARD_PER_DAY / 1440;
    }

    function setMinPFXSwapAmount(uint256 _minPFXSwapAmount) external onlyOwner {
        require(_minPFXSwapAmount > 0, "Minimum PFX amount should be greater than zero");
        minPFXSwapAmount = _minPFXSwapAmount;
    }

    function setClaimCoolDown(uint256 _claimCoolDown) external onlyOwner {
        claimCooldown = _claimCoolDown;
    }

    function setPfxSwapPrice(uint256 _newPfxPrice) external onlyOwner {
        pfxSwapPrice = _newPfxPrice;
    }

    function earningInfo(address account, uint256[] calldata tokenIds) external view returns (EarningInfo[] memory) {
        uint256 tokenId;
        uint256 earned = 0;

        EarningInfo[] memory info = new EarningInfo[](tokenIds.length);

        for (uint i = 0; i < tokenIds.length; i++) {
            tokenId = tokenIds[i];
            Stake memory staked = vault[tokenId];
            require(staked.owner == account, "Not an owner");

            uint256 timeStaked = block.timestamp - staked.lastClaimed;
            earned = (REWARD_PER_MINUTE * timeStaked) / 1 minutes + staked.accumulatedRewards; // Include accumulated rewards

            info[i] = EarningInfo({
                tokenId: tokenId,
                stakedTimestamp: staked.timestamp,
                lastClaimedTimestamp: staked.lastClaimed,
                earned: earned
            });
        }

        return info;
    }

    function balanceOfNFT(address account) public view returns (uint256) {
        uint256 balance = 0;
        uint256 supply = nft.mintedSupply();
        for (uint i = 1; i <= supply; i++) {
            if (vault[i].owner == account) {
                balance += 1;
            }
        }
        return balance;
    }

    function tokensOfOwner(address account) public view returns (uint256[] memory ownerTokens) {
        uint256 supply = nft.mintedSupply();
        uint256[] memory tmp = new uint256[](supply);

        uint256 index = 0;
        for (uint tokenId = 1; tokenId <= supply; tokenId++) {
            if (vault[tokenId].owner == account) {
                tmp[index] = vault[tokenId].tokenId;
                index += 1;
            }
        }

        uint256[] memory tokens = new uint256[](index);
        for (uint i = 0; i < index; i++) {
            tokens[i] = tmp[i];
        }

        return tokens;
    }

    // Swap function to exchange PFX rewards for USDT
    function swapPFXForUSDT(uint256 pfxAmount) external {
        require(pfxAmount >= minPFXSwapAmount, "Amount must be greater than the minimum swap amount");
        //require(pfxAmount > 0, "Token amount must be greater than zero");

        // Calculate the USDT amount based on the scaled price
        uint256 usdtAmount = (pfxAmount * pfxSwapPrice) / 1e18;
        require(usdtToken.balanceOf(address(this)) >= usdtAmount, "Insufficient USDT in contract");

        // Transfer PFX tokens from the user to the contract (Burn PFX tokens)
        require(transfer(address(this), pfxAmount), "PFX transfer failed");

        // Transfer USDT from the contract to the user
        require(usdtToken.transfer(msg.sender, usdtAmount), "USDT transfer failed");

        emit Swapped(msg.sender, pfxAmount, usdtAmount);
    }

    // Function to burn tokens from the staking contract's balance
    function burnTokensFromContract(uint256 value) external onlyOwner {
        require(balanceOf(address(this)) >= value, "Insufficient PFX balance in the contract");

        // Call the _burn function directly on the token contract
        _burn(address(this), value);
    }

    function onERC721Received(
        address,
        address from,
        uint256,
        bytes calldata
    ) external pure override returns (bytes4) {
        require(from == address(0x0), "Cannot send nfts to Vault directly");
        return IERC721Receiver.onERC721Received.selector;
    }
}
