// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract Donation {
    address public owner;
    mapping(address => uint256) public donations;

    event DonationReceived(address indexed donor, address indexed recipient, uint256 amount);

    constructor() {
        owner = msg.sender;
    }

    function donate(address recipient, uint256 amount) public payable {
        require(amount > 0, "Donation amount must be greater than zero");
        require(msg.value == amount, "Donation amount must match the sent value");

        donations[recipient] += amount;
        emit DonationReceived(msg.sender, recipient, amount);
    }
}
