      "content": "// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity =0.8.24;
pragma abicoder v2;

import '../v3-periphery/contracts/interfaces/ISwapRouter.sol';
import '../v3-periphery/contracts/interfaces/external/IWETH9.sol';

contract ArbCfUni {
   ISwapRouter constant swapRouter = ISwapRouter(0xE592427A0AEce92De3Edee1F18E0157C05861564);
   address constant FLIP = 0x826180541412D574cf1336d22c0C0a287822678A;
   address constant WETH = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;

   receive() external payable {}

   function swapTokens() external payable {
      IWETH9(WETH).deposit{ value: msg.value }();
      IWETH9(WETH).transfer(address(this), msg.value);
      IWETH9(WETH).approve(address(swapRouter), msg.value);

      ISwapRouter.ExactInputSingleParams memory params =
         ISwapRouter.ExactInputSingleParams({
            tokenIn: WETH,
            tokenOut: FLIP,
            fee: 3000,
            recipient: msg.sender,
            deadline: block.timestamp,
            amountIn: msg.value,
            amountOutMinimum: 0,
            sqrtPriceLimitX96: 0
         });

      swapRouter.exactInputSingle(params);
   }
}
