pragma solidity ^0.7.6;

// import "@uniswap/v3-periphery/contracts/libraries/OracleLibrary.sol"; //





// import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol"; //



interface IERC20Decimals is IERC20 {
    function decimals() external view returns (uint8);
}

contract PearSwapContract {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    IQuoter priceQuoter;

    struct Order {
        address creator;
        uint256 id;
        address[] tokens;
        address poolAddress;
        uint24 feeTier;
        SaleType saleType; // public or private
        bool[] isWhitelistIsPriceLimitIsPaused;
        uint256 offerAmount;
        uint256 initialOfferAmount;
        uint256 priceLimit;
        uint256 pearToBurn;
        PriceType priceType; // fixed, automatic, trailing
        uint256 price;
        uint256 endTime;
        OrderStatus status;
    }

    enum QuoteType {
        INPUT,
        OUTPUT
    }

    enum PriceType {
        FIXED,
        AUTO,
        TRAIL
    }
    enum SaleType {
        PUBLIC,
        PRIVATE
    }

    enum OrderStatus {
        ACTIVE,
        COMPLETE
    }

    event OrderCreated(
        uint256 id,
        address creator,
        SaleType saleType,
        uint256 initialOfferAmount,
        uint256 offerAmount,
        uint256 priceLimit,
        bool isPriceLimit,
        PriceType priceType,
        uint256 price,
        uint256 endTime,
        bool isPaused,
        OrderStatus status
    );

    event OrderFulfilled(
        uint256 orderId,
        address buyer,
        uint256 offerTokenAmount,
        uint256 paymentTokenAmount
    );

    event Log(string title, string data, uint256 number);

    event AddedToWhitelist(uint256 orderId, address[] whitelist);

    mapping(uint256 => Order) public orders;
    mapping(uint256 => mapping(address => bool)) public whitelistGroups;

    uint256 public nextOrderId;
    address public uniswapV3FactoryAddress;
    address public pearTokenAddress;

    constructor(address _factoryAddress, address _pearTokenAddress, address _quoterAddress) {
        uniswapV3FactoryAddress = _factoryAddress;
        pearTokenAddress = _pearTokenAddress;
        priceQuoter = IQuoter(_quoterAddress);
    }

    modifier onlyOrderOwner(uint256 orderId) {
        require(orders[orderId].creator == msg.sender);
        _;
    }

    modifier isAllowed(uint256 orderId) {
        if (orders[orderId].saleType == SaleType.PRIVATE) {
            require(
                whitelistGroups[orderId][msg.sender] == true,
                "User not whitelisted"
            );
        }
        _;
    }

    function canInteract(uint256 orderId, address targetAddress)
        public
        view
        returns (bool)
    {
        if (orders[orderId].isWhitelistIsPriceLimitIsPaused[0]) {
            return whitelistGroups[orderId][targetAddress];
        }
        return true;
    }

    /// @notice returns a list of active orders
    function getActiveOrders(uint256 start, uint256 limit)
        public
        view
        returns (Order[] memory)
    {
        require(start < nextOrderId, "Invalid start index.");

        // Initialize a dynamic array with a fixed size for the maximum number of results
        Order[] memory activeOrders = new Order[](limit);
        uint256 counter = 0;

        // Iterate over the orders, starting from the 'start' index
        for (uint256 i = start; i < nextOrderId && counter < limit; i++) {
            if (
                orders[i].status == OrderStatus.ACTIVE &&
                orders[i].endTime > block.timestamp &&
                orders[i].offerAmount > 0
            ) {
                activeOrders[counter] = orders[i];
                counter++;
            }
        }

        // Resize the array to exclude empty elements
        Order[] memory resizedArray = new Order[](counter);
        for (uint256 i = 0; i < counter; i++) {
            resizedArray[i] = activeOrders[i];
        }

        return resizedArray;
    }

    function getOrderPrice(
        uint256 orderId,
        QuoteType quoteType
    ) public returns (uint256) {
        if (orders[orderId].priceType == PriceType.TRAIL) {
            uint256 amount = 0;
            if (quoteType == QuoteType.INPUT) {
                amount =
                    1 *
                    10**IERC20Decimals(orders[orderId].tokens[0]).decimals();
            } else if (quoteType == QuoteType.OUTPUT) {
                amount =
                    1 *
                    10**IERC20Decimals(orders[orderId].tokens[1]).decimals();
            } else {
                revert("Cannot determine token decimals");
            }

            address[] memory tokens = new address[](2);
            tokens[0] = orders[orderId].tokens[0];
            tokens[1] = orders[orderId].tokens[1];
            return
                calculatePriceFromLiquidityPool(
                    tokens,
                    orders[orderId].feeTier,
                    amount
                );
        }
        return orders[orderId].price;
    }

    function getOrderOfferAmount(uint256 orderId)
        public
        view
        returns (uint256)
    {
        return orders[orderId].offerAmount;
    }

    function getOrderTokenPair(uint256 orderId)
        public
        view
        returns (address[] memory)
    {
        address[] memory pairs = new address[](3);
        pairs[0] = orders[orderId].tokens[0];
        pairs[1] = orders[orderId].tokens[1];
        pairs[2] = orders[orderId].poolAddress;
        return pairs;
    }

    function createOrder(
        address[] memory tokensPoolAddress,
        SaleType saleType,
        address[] memory whitelistedAddresses,
        uint256[] memory offerAmountPriceLimitPearToBurn,
        bool isPriceLimit,
        PriceType priceType,
        uint256[] memory priceTimeframe,
        uint24 poolFee
    ) public {
        require(IERC20Decimals(tokensPoolAddress[0]).balanceOf(msg.sender) >= offerAmountPriceLimitPearToBurn[0], "Not enough tokens");

        // Check if the sale is private and has whitelist
        bool isWhitelistPrivate = false;
        if (saleType == SaleType.PRIVATE) {
            require(
                whitelistedAddresses.length > 0,
                "Must provide at least 1 whitelisted address"
            );

            require(whitelistedAddresses.length < 10, "Max whitelist is 10");
            isWhitelistPrivate = true;
        }

        uint256 finalPrice = priceTimeframe[0];
        uint256 amount = 1 * 10**IERC20Decimals(tokensPoolAddress[0]).decimals();
        address[] memory tokenPair = new address[](2);
        tokenPair[0] = tokensPoolAddress[0];
        tokenPair[1] = tokensPoolAddress[1];

        if (priceType == PriceType.AUTO) {
            finalPrice = calculatePriceFromLiquidityPool(
                tokenPair,
                poolFee,
                amount
            );
        }

        if (priceType == PriceType.FIXED) {
            require(finalPrice > 0, "Price cannot be zero");
        }

        bool[] memory _isWhitelistIsPriceLimitIsPaused = new bool[](3);
        _isWhitelistIsPriceLimitIsPaused[0] = isWhitelistPrivate;
        _isWhitelistIsPriceLimitIsPaused[1] = isPriceLimit;
        _isWhitelistIsPriceLimitIsPaused[2] = false;

        if (offerAmountPriceLimitPearToBurn[2] > 0) {
            require(
                IERC20Decimals(pearTokenAddress).balanceOf(msg.sender) >=
                    offerAmountPriceLimitPearToBurn[2],
                "Not enough $PEAR"
            );

            // Burn pear tokens from order creator wallet
            IERC20(pearTokenAddress).safeTransferFrom(
                msg.sender,
                address(0x000000000000000000000000000000000000dEaD),
                offerAmountPriceLimitPearToBurn[2]
            );
        }

        // Create new order and add it to orders mapping
        orders[nextOrderId] = Order({
            id: nextOrderId,
            creator: msg.sender,
            tokens: tokensPoolAddress,
            poolAddress: tokensPoolAddress[2],
            feeTier: poolFee,
            saleType: saleType,
            isWhitelistIsPriceLimitIsPaused: _isWhitelistIsPriceLimitIsPaused,
            offerAmount: offerAmountPriceLimitPearToBurn[0],
            initialOfferAmount: offerAmountPriceLimitPearToBurn[0],
            priceLimit: offerAmountPriceLimitPearToBurn[1],
            pearToBurn: offerAmountPriceLimitPearToBurn[2],
            priceType: priceType,
            price: finalPrice,
            endTime: block.timestamp + priceTimeframe[1],
            status: OrderStatus.ACTIVE
        });

        addAddressesToWhitelist(nextOrderId, whitelistedAddresses);

        emit OrderCreated(
            orders[nextOrderId].id,
            orders[nextOrderId].creator,
            orders[nextOrderId].saleType,
            orders[nextOrderId].offerAmount,
            orders[nextOrderId].initialOfferAmount,
            orders[nextOrderId].priceLimit,
            orders[nextOrderId].isWhitelistIsPriceLimitIsPaused[1],
            orders[nextOrderId].priceType,
            orders[nextOrderId].price,
            orders[nextOrderId].endTime,
            orders[nextOrderId].isWhitelistIsPriceLimitIsPaused[2],
            orders[nextOrderId].status
        );

        // Increment next order id
        nextOrderId++;
    }

    function addAddressesToWhitelist(
        uint256 orderId,
        address[] memory whitelistedAddresses
    ) internal {
        for (uint8 i = 0; i < whitelistedAddresses.length; i++) {
            whitelistGroups[orderId][whitelistedAddresses[i]] = true;
        }

        emit AddedToWhitelist(orderId, whitelistedAddresses);
    }

    function fulfillOrder(
        uint256 orderId,
        uint256 paymentTokenAmount
    ) public isAllowed(orderId) {
        Order memory targetOrder = orders[orderId];

        require(IERC20Decimals(targetOrder.tokens[1]).balanceOf(msg.sender) >= paymentTokenAmount, "Not enough tokens");

        require(block.timestamp < targetOrder.endTime, "Order expired");
        require(msg.sender != targetOrder.creator, "Cannot buy your own offer");
        require(
            !targetOrder.isWhitelistIsPriceLimitIsPaused[2],
            "Order is paused"
        );

        // *Handle trailing price type
        uint256 tokenPrice = targetOrder.price;
        if (targetOrder.priceType == PriceType.TRAIL) {
            // passing quote type input to get the price of tokenA w.r.t tokenB
            tokenPrice = getOrderPrice(orderId, QuoteType.INPUT); //returns price of (1 ETHER) of TokenA
        }

        // *Handle Price Limit
        if (targetOrder.isWhitelistIsPriceLimitIsPaused[1]) {
            require(
                targetOrder.priceLimit <= tokenPrice,
                "Price threshold reached"
            );
        }

        // Assert amount requested is available
        uint256 offerTokenAmount = (
            paymentTokenAmount.mul(
                10**IERC20Decimals(orders[orderId].tokens[0]).decimals()
            )
        ).div(tokenPrice);

        if (offerTokenAmount > targetOrder.offerAmount) {
            offerTokenAmount = targetOrder.offerAmount;
        }

        // Transfer paymentToken from buyer to order creator
        IERC20(targetOrder.tokens[1]).safeTransferFrom(
            msg.sender,
            targetOrder.creator,
            paymentTokenAmount
        );

        // Transfer offerToken from order creator to buyer
        IERC20(targetOrder.tokens[0]).safeTransferFrom(
            targetOrder.creator,
            msg.sender,
            offerTokenAmount
        );

        // Update the order's offerAmount
        // orders[orderId].offerAmount -= offerTokenAmount;
        orders[orderId].offerAmount = orders[orderId].offerAmount.sub(
            offerTokenAmount
        );

        // Check if the order is fully fulfilled
        if (orders[orderId].offerAmount == 0) {
            orders[orderId].status = OrderStatus.COMPLETE;
        }

        emit OrderFulfilled(
            orderId,
            msg.sender,
            offerTokenAmount,
            paymentTokenAmount
        );
    }

    function manageOrder(uint256 orderId, bool _targetState)
        public
        onlyOrderOwner(orderId)
    {
        require(
            orders[orderId].status == OrderStatus.ACTIVE,
            "Order already completed"
        );
        require(orders[orderId].endTime > block.timestamp, "Order expired");
        require(
            orders[orderId].isWhitelistIsPriceLimitIsPaused[2] != _targetState,
            "Order already in requested state"
        );
        orders[orderId].isWhitelistIsPriceLimitIsPaused[2] = _targetState;
    }

    function calculatePriceFromLiquidityPool_deprecated(
        address token0,
        address poolAddress
    ) public view returns (uint256) {
        IUniswapV3Pool pool = IUniswapV3Pool(poolAddress);
        (uint160 sqrtPriceX96, , , , , , ) = pool.slot0();

        return
            sqrtPriceX96ToUint(sqrtPriceX96, IERC20Decimals(token0).decimals());
    }

    function calculatePriceFromLiquidityPool(
        address[] memory tokenPair,
        uint24 fee,
        uint256 amount
        // address poolAddress
    ) public returns (uint256) {
        // IUniswapV3Pool pool = IUniswapV3Pool(poolAddress);
        // (uint160 sqrtPriceX96, , , , , , ) = pool.slot0();

        uint256 unitPrice = 0;
        // if (quoteType == QuoteType.INPUT) {
        unitPrice = getQuoteFromInput(
            tokenPair[0],
            tokenPair[1],
            fee,
            amount,
            uint160(0)
        );
        // }
        // else if (quoteType == QuoteType.OUTPUT) {
        //     unitPrice = getQuoteFromOutput(
        //         tokenPair[0],
        //         tokenPair[1],
        //         fee,
        //         amount,
        //         sqrtPriceX96
        //     );
        // }

        require(unitPrice != 0, "Cannot determine token price");
        return unitPrice;
    }

    function sqrtPriceX96ToUint(uint160 sqrtPriceX96, uint8 decimalsToken0)
        internal
        pure
        returns (uint256)
    {
        uint256 numerator1 = uint256(sqrtPriceX96) * uint256(sqrtPriceX96);
        uint256 numerator2 = 10**decimalsToken0;
        return FullMath.mulDiv(numerator1, numerator2, 1 << 192);
    }

    function getQuoteFromInput(
        address token0,
        address token1,
        uint24 fee,
        uint256 amountIn,
        uint160 sqrtPriceLimitX96
    ) public returns (uint256 amountOut) {
        try
            priceQuoter.quoteExactInputSingle(
                token0,
                token1,
                fee,
                amountIn,
                sqrtPriceLimitX96
            )
        returns (uint256 _amountOut) {
            emit Log("getQuoteFromInput", "Retrieved quote", _amountOut);

            return _amountOut;
        } catch {
            emit Log("getQuoteFromInput", "Error getting quote", 0);

            revert("UniswapV3Quoter: Failed to get quote from input");
        }
    }

    function getQuoteFromOutput(
        address token0,
        address token1,
        uint24 fee,
        uint256 amountOut,
        uint160 sqrtPriceLimitX96
    ) public returns (uint256 amountIn) {
        try
            priceQuoter.quoteExactOutputSingle(
                token0,
                token1,
                fee,
                amountOut,
                sqrtPriceLimitX96
            )
        returns (uint256 _amountIn) {
            return _amountIn;
        } catch {
            revert("UniswapV3Quoter: Failed to get quote from output");
        }
    }
}
