pragma solidity ^0.8.20;


import "./ERC1155Upgradeable.sol";
import "./OwnableUpgradeable.sol";
import "./ERC1155PausableUpgradeable.sol";
import "./ERC1155BurnableUpgradeable.sol";
import "./ERC1155SupplyUpgradeable.sol";
import "./IERC1155ReceiverUpgradeable.sol";
import "./Initializable.sol";
import "./ReentrancyGuardUpgradeable.sol";

import {Operatable} from "./Operator.sol";


contract PoglinLodestars is Initializable, ERC1155Upgradeable, OwnableUpgradeable, ERC1155PausableUpgradeable, ERC1155BurnableUpgradeable, ERC1155SupplyUpgradeable, Operatable {
    uint256 public constant NATURE = 1;
    uint256 public constant FIRE = 2;
    uint256 public constant WATER = 3;
    uint256 public constant EARTH = 4;

    uint256 public constant DARK = 5;
    uint256 public constant LIGHTNING = 6;
    uint256 public constant AETHER = 7;

    uint256 public constant SPACE = 8;
    uint256 public constant TIME = 9;
    uint256 public constant SOUL = 10;

    uint256 public constant CHAOS = 11;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize() initializer public {
        __ERC1155_init("https://token.poglin.com/pogl/{id}");
        /* __Ownable_init(); */
        __ERC1155Pausable_init();
        __ERC1155Burnable_init();
        __ERC1155Supply_init();

        OwnableUpgradeable.__Ownable_init_unchained();
        Operatable.__Operatable_init_unchained();

        transferOwnership(msg.sender);
    }

    function onERC1155Received(address, address, uint256, uint256, bytes calldata) external pure returns (bytes4) {
        return IERC1155ReceiverUpgradeable.onERC1155Received.selector;
    }

    function onERC1155BatchReceived(address, address, uint256[] calldata, uint256[] calldata, bytes calldata) external pure returns (bytes4) {
        return IERC1155ReceiverUpgradeable.onERC1155BatchReceived.selector;
    }

    function setURI(string memory newuri) public onlyOperator {
        _setURI(newuri);
    }

    function pause() public onlyOperator {
        _pause();
    }

    function unpause() public onlyOperator {
        _unpause();
    }

    function mint(address account, uint256 id, uint256 amount, bytes memory data)
        public
        onlyOperator
    {
        require(id >= NATURE && id <= CHAOS, "Invalid tokenID.");
        _mint(account, id, amount, data);
    }

    function mintBatch(address to, uint256[] memory ids, uint256[] memory amounts, bytes memory data)
        public
        onlyOperator
    {
        for (uint256 i = 0; i < ids.length; i++) {
            require(ids[i] >= NATURE && ids[i] <= CHAOS, "Invalid tokenID.");
        }
        _mintBatch(to, ids, amounts, data);
    }

    // The following functions are overrides required by Solidity.
    /* function _update(address from, address to, uint256[] memory ids, uint256[] memory values) */
    /*     internal */
    /*     override(ERC1155Upgradeable, ERC1155PausableUpgradeable, ERC1155SupplyUpgradeable) */
    /* { */
    /*     super._update(from, to, ids, values); */
    /* } */

    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    )
        internal
        override(ERC1155Upgradeable, ERC1155PausableUpgradeable, ERC1155SupplyUpgradeable)
    {
        ERC1155SupplyUpgradeable._beforeTokenTransfer(operator, from, to, ids, amounts, data);
        ERC1155PausableUpgradeable._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }

}
"},"PoglinStaking.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./ERC721Upgradeable.sol";

import "./IERC721Upgradeable.sol";
import "./IERC721ReceiverUpgradeable.sol";

import "./CountersUpgradeable.sol";
import "./StringsUpgradeable.sol";
import "./OwnableUpgradeable.sol";
import "./ReentrancyGuardUpgradeable.sol";
import "./Initializable.sol";

import {PoglinLodestars} from "./PoglinLodestars.sol";
import {Operatable} from "./Operator.sol";


contract PoglinStaking is Initializable, ReentrancyGuardUpgradeable, OwnableUpgradeable, Operatable, IERC721ReceiverUpgradeable {
    using CountersUpgradeable for CountersUpgradeable.Counter;
    CountersUpgradeable.Counter private _contractIds;

    address private _signer;
    bool private _allowSwaps;
    // registry of supported contracts
    mapping(address => uint) private _contracts;
    mapping(uint => string) private _contractSymbols;

    // Mapping from contractID to token ID to owner address
    mapping(uint256 => mapping(uint256 => address)) private _owners;

    // Mapping from contractID to owner address to token count
    mapping(uint256 => mapping(address => uint256)) private _balances;

    // Mapping from contractID to owner to list of owned token IDs
    mapping(uint256 => mapping(address => mapping(uint256 => uint256))) private _ownedTokens;

    // Mapping from contractID to token ID to index of the owner tokens list
    mapping(uint256 => mapping(uint256 => uint256)) private _ownedTokensIndex;

    // Mapping from contractID to Array with all token ids, used for enumeration
    mapping(uint256 => uint256[]) private _allTokens;

    // Mapping from contractID to token id to position in the allTokens array
    mapping(uint256 => mapping(uint256 => uint256)) private _allTokensIndex;

    address private _rewardContractAddress;
    mapping(address => uint) private _fulfilledClaims;

    /* mapping(address => uint256[]) private _openClaims; */

    function onERC721Received(address, address, uint256, bytes calldata) external pure returns (bytes4) {
        return IERC721ReceiverUpgradeable.onERC721Received.selector;
    }

    event StakeEvent(
        address indexed _owner,
        address contractAddress,
        string tokenSymbol,
        uint256[] tokenIds
    );

    event UnstakeEvent(
        address indexed _owner,
        address contractAddress,
        string tokenSymbol,
        uint256[] tokenIds
    );

    event SwapEvent(
        address indexed _owner,
        address contractAddress,
        string tokenSymbol,
        uint256[] inTokenIds,
        uint256[] outTokenIds
    );

    event ClaimCreatedEvent(
        address indexed _claimAddress,
        uint256[] tokenIds,
        uint256[] amounts,
        uint256[] allAmounts
    );

    event ClaimEvent(
        address indexed _claimAddress,
        uint256[] tokenIds,
        uint256[] amounts,
        string ident
    );

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() initializer {}

    function initialize() public initializer {
        OwnableUpgradeable.__Ownable_init_unchained();
        Operatable.__Operatable_init_unchained();

        transferOwnership(msg.sender);
        /* _setApprovalForAll(address(this), msg.sender, true); */
        _allowSwaps = true;
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Internal function without access restriction.
     */
    function transferOwnership(address newOwner) public override onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        _transferOwnership(newOwner);
        // REVOKE and approve new owner
        // _setApprovalForAll(address owner, address operator, bool approved)
        // TODO: whitelist operator
    }

    function addContract(address contractAddress) public onlyOperator {
        require(contractAddress != address(0), "Staking: Contract address cannot be the zero address");
        require(contractAddress != address(this), "Staking: Contract address cannot be the staking contract address itslef");
        require(_contracts[contractAddress] == 0, "Staking: Contract address is already registered");
        _contractIds.increment();
        _contracts[contractAddress] = _contractIds.current();
        _contractSymbols[_contractIds.current()] = ERC721Upgradeable(contractAddress).symbol();
    }

    function setRewardContractAddress(address contractAddress) public nonReentrant onlyOperator {
        require(contractAddress != address(0), "Reward contract address cannot be the zero address");
        require(contractAddress != address(this), "Reward contract cannot be the staking contract address itslef");
        require(_rewardContractAddress != contractAddress, "Reward contract address is already set to this address");
        _rewardContractAddress = contractAddress;
    }

    function getRewardContractAddress() external view returns (address) {
        return _rewardContractAddress;
    }

    /* function updateContract(address oldContractAddress, address newContractAddress) public onlyOperator { */
    /*     // TODO */
    /* } */

    /* function lockContract(address contractAddress) public onlyOperator { */
    /* } */

    function returnStakedTokens(address contractAddress) public onlyOperator {
        require(_contracts[contractAddress] != 0, "Staking: Contract is not registered");

        uint contractId =_contracts[contractAddress];
        uint256[] memory unstakedTokenIds = new uint256[](_allTokens[contractId].length);

        for (uint i = 0; i < unstakedTokenIds.length; i++) {
            unstakedTokenIds[i] = _allTokens[contractId][i];
            IERC721Upgradeable(contractAddress).safeTransferFrom(address(this), _owners[contractId][unstakedTokenIds[i]], unstakedTokenIds[i]);
        }

        for (uint i = 0; i < unstakedTokenIds.length; i++) {
            _removeToken(contractAddress, _owners[contractId][unstakedTokenIds[i]], unstakedTokenIds[i]);
        }

        emit UnstakeEvent(msg.sender, contractAddress, _contractSymbols[_contracts[contractAddress]], unstakedTokenIds);
    }

    function setSwapFlag(bool allow) public onlyOperator {
        _allowSwaps = allow;
    }

    function setSignerAddress(address signer) public onlyOperator {
        _signer = signer;
    }

    function getContractId(address contractAddress) public view returns (uint) {
        return _contracts[contractAddress];
    }

    function getContractSymbol(address contractAddress) external view returns (string memory) {
        return _contractSymbols[_contracts[contractAddress]];
    }

    // ownership
    function ownerOf(address contractAddress, uint256 tokenId) external view returns (address) {
        address owner = _owners[getContractId(contractAddress)][tokenId];
        /* require(owner != address(0), "ERC721: invalid token ID"); */
        return owner;
    }

    function tokenOfOwnerByIndex(address contractAddress, uint256 index) public view returns (uint256) {
        require(index < _balanceOf(contractAddress, msg.sender), "Enumerable: Owner index out of bounds");
        return _ownedTokens[getContractId(contractAddress)][msg.sender][index];
    }

    function getTokenOfOwnerByIndex(address contractAddress, address ownerAddress, uint256 index) public view returns (uint256) {
        require(index < _balanceOf(contractAddress, ownerAddress), "Enumerable: Owner index out of bounds");
        return _ownedTokens[getContractId(contractAddress)][ownerAddress][index];
    }

    function balanceOf(address contractAddress) external view returns (uint256) {
        return _balanceOf(contractAddress, msg.sender);
    }

    function getBalanceOf(address contractAddress, address tokenOwner) external view returns (uint256) {
        return _balanceOf(contractAddress, tokenOwner);
    }

    function _balanceOf(address contractAddress, address tokenOwner) private view returns (uint256) {
        require(tokenOwner != address(0), "ERC721: address zero is not a valid owner");
        return _balances[getContractId(contractAddress)][tokenOwner];
    }

    function _addToken(address contractAddress, address tokenOwner, uint256 tokenId) private {
        uint contractId =_contracts[contractAddress];

        // current
        uint256 length = _balanceOf(contractAddress, tokenOwner);

        // register owner by ID
        _owners[contractId][tokenId] = tokenOwner;
        _balances[contractId][tokenOwner] += 1;

        // add to all tokens enum
        _allTokensIndex[contractId][tokenId] = _allTokens[contractId].length;
        _allTokens[contractId].push(tokenId);

        // add to owner enm
        _ownedTokens[contractId][tokenOwner][length] = tokenId;
        _ownedTokensIndex[contractId][tokenId] = length;
    }

    function _removeTokenFromOwnerEnum(address contractAddress, address tokenOwner, uint256 tokenId) private {
        uint contractId =_contracts[contractAddress];

        // remove from owner enum
        uint256 lastTokenIndex = _balanceOf(contractAddress, tokenOwner) - 1;
        uint256 tokenIndex = _ownedTokensIndex[contractId][tokenId];

        // When the token to delete is the last token, the swap operation is unnecessary
        if (tokenIndex != lastTokenIndex) {
            uint256 lastTokenId = _ownedTokens[contractId][tokenOwner][lastTokenIndex];

            // Move the last token to the slot of the to-delete token
            _ownedTokens[contractId][tokenOwner][tokenIndex] = lastTokenId;
            // Update the moved token's index
            _ownedTokensIndex[contractId][lastTokenId] = tokenIndex;
        }

        // This also deletes the contents at the last position of the array
        delete _ownedTokensIndex[contractId][tokenId];
        delete _ownedTokens[contractId][tokenOwner][lastTokenIndex];
    }

    function _removeTokenFromAllTokenEnum(address contractAddress, uint256 tokenId) private {
        uint contractId =_contracts[contractAddress];

        // delete from all enum
        uint256 lastTokenIndex = _allTokens[contractId].length - 1;
        uint256 tokenIndex = _allTokensIndex[contractId][tokenId];

        // When the token to delete is the last token, the swap operation is unnecessary. However, since this occurs
        // rarely (when the last added token is removed) that we still do the swap here to avoid the gas cost of adding
        // an 'if' statement (like when removing form the owner enum)
        uint256 lastTokenId = _allTokens[contractId][lastTokenIndex];

        // Move the last token to the slot of the to-delete token
        _allTokens[contractId][tokenIndex] = lastTokenId;
        // Update the moved token's index
        _allTokensIndex[contractId][lastTokenId] = tokenIndex;

        // This also deletes the contents at the last position of the array
        delete _allTokensIndex[contractId][tokenId];
        _allTokens[contractId].pop();
    }

    function _removeToken(address contractAddress, address tokenOwner, uint256 tokenId) private {
        _removeTokenFromOwnerEnum(contractAddress, tokenOwner, tokenId);
        _removeTokenFromAllTokenEnum(contractAddress, tokenId);

        uint contractId =_contracts[contractAddress];

        // update ownership map and token balance
        delete _owners[contractId][tokenId];
        _balances[contractId][tokenOwner] -= 1;
    }

    /*
     * stakeTokens
     */
    function stakeTokens(address contractAddress, uint256[] calldata tokenIds) public nonReentrant {
        require(_contracts[contractAddress] != 0, "Staking: Contract address not registered");

        for (uint256 i = 0; i < tokenIds.length; i++) {
            require(_owners[_contracts[contractAddress]][tokenIds[i]] == address(0), "Staking: Token is already in custody");
        }
        for (uint256 i = 0; i < tokenIds.length; i++) {
            IERC721Upgradeable(contractAddress).safeTransferFrom(msg.sender, address(this), tokenIds[i]);
        }

        for (uint256 i = 0; i < tokenIds.length; i++) {
            _addToken(contractAddress, msg.sender, tokenIds[i]);
        }
        emit StakeEvent(msg.sender, contractAddress, _contractSymbols[_contracts[contractAddress]], tokenIds);
    }

    /*
     * unstakeToken
     */
    function unstakeTokens(address contractAddress, uint256[] calldata tokenIds) public nonReentrant {
        for (uint256 i = 0; i < tokenIds.length; i++) {
            require(_owners[_contracts[contractAddress]][tokenIds[i]] == msg.sender, "Transfer not allowed. Token not owned by sender.");
        }

        for (uint256 i = 0; i < tokenIds.length; i++) {
            IERC721Upgradeable(contractAddress).safeTransferFrom(address(this), msg.sender, tokenIds[i]);
        }

        for (uint256 i = 0; i < tokenIds.length; i++) {
            _removeToken(contractAddress, msg.sender, tokenIds[i]);
        }

        emit UnstakeEvent(msg.sender, contractAddress, _contractSymbols[_contracts[contractAddress]], tokenIds);
    }

    /*
     * swapTokens
     */
    function swapTokens(address contractAddress, uint256[] calldata inTokenIds, uint256[] calldata outTokenIds) public nonReentrant {
        require(_allowSwaps == true, "Swapping tokens is not yet supported");
        require(_contracts[contractAddress] != 0, "Staking: Contract address not registered");
        require(inTokenIds.length >= outTokenIds.length, "Cannot swap out more tokens than in");

        // unstake
        for (uint256 i = 0; i < outTokenIds.length; i++) {
            require(_owners[_contracts[contractAddress]][outTokenIds[i]] == msg.sender, "Swap: Transfer not allowed. Token not owned by address.");
        }
        for (uint256 i = 0; i < outTokenIds.length; i++) {
            IERC721Upgradeable(contractAddress).safeTransferFrom(address(this), msg.sender, outTokenIds[i]);
        }

        // stake
        for (uint256 i = 0; i < inTokenIds.length; i++) {
            require(_owners[_contracts[contractAddress]][inTokenIds[i]] == address(0), "Swap: Staking: Token is already in custody");
        }
        for (uint256 i = 0; i < inTokenIds.length; i++) {
            IERC721Upgradeable(contractAddress).safeTransferFrom(msg.sender, address(this), inTokenIds[i]);
        }

        for (uint256 i = 0; i < outTokenIds.length; i++) {
            _removeToken(contractAddress, msg.sender, outTokenIds[i]);
        }

        for (uint256 i = 0; i < inTokenIds.length; i++) {
            _addToken(contractAddress, msg.sender, inTokenIds[i]);
        }

        emit SwapEvent(msg.sender, contractAddress, _contractSymbols[_contracts[contractAddress]], inTokenIds, outTokenIds);
    }

    // claims
    function claimReward(uint256[] calldata tokenIds, uint256[] calldata amounts, uint period, bytes calldata signature, string calldata ident) public {
        require(verifyArgs(msg.sender, tokenIds, amounts, period, signature, ident) == true, "Parameter validation failed");
        require(_rewardContractAddress != address(0), "Reward contract address has not yet been set");
        require(period > _fulfilledClaims[msg.sender], "Invalid Claim Period");

        _fulfilledClaims[msg.sender] = period;
        PoglinLodestars(_rewardContractAddress).mintBatch(msg.sender, tokenIds, amounts, '');

        emit ClaimEvent(msg.sender, tokenIds, amounts, ident);
    }

    function verifyArgs(address claimerAddress, uint256[] calldata tokenIds, uint256[] calldata amounts, uint period, bytes calldata signature, string calldata ident) public view returns (bool) {
        /* emit ClaimLog(claimerAddress, tokenIds, amounts, period, signature); */
        require(_signer != address(0), "Signer address has not yet been set");
        bytes32 messageHash = getMessageHash(claimerAddress, tokenIds, amounts, period, ident);
        // non prefixed
        return recoverSigner(messageHash, signature) == _signer;

        // prefixed
        /* bytes32 ethSignedMessageHash = getEthSignedMessageHash(messageHash); */
        /* return recoverSigner(ethSignedMessageHash, signature) == _signer; */
    }

    function getMessageHash(address claimerAddress, uint256[] calldata tokenIds, uint256[] calldata amounts, uint period, string calldata ident) internal pure returns (bytes32) {
        /* return keccak256(abi.encodePacked(claimerAddress, tokenIds, amounts, period)); */
        return keccak256(abi.encode(claimerAddress, tokenIds, amounts, period, ident));
    }

    function getEthSignedMessageHash(bytes32 _messageHash) internal pure returns (bytes32) {
        /*
        Signature is produced by signing a keccak256 hash with the following format:
        "\x19Ethereum Signed Message\n" + len(msg) + msg
        */
        return
            keccak256(
                /* abi.encodePacked("\x19Ethereum Signed Message:\n32", _messageHash) */
                abi.encode("\x19Ethereum Signed Message:\n32", _messageHash)
                /* abi.encodePacked(_messageHash) */
            );
    }

    function recoverSigner(bytes32 _ethSignedMessageHash, bytes memory _signature) internal pure returns (address) {
        (bytes32 r, bytes32 s, uint8 v) = splitSignature(_signature);
        return ecrecover(_ethSignedMessageHash, v, r, s);
    }

    function splitSignature(bytes memory sig) internal pure returns (bytes32 r, bytes32 s, uint8 v) {
        require(sig.length == 65, "invalid signature length");

        assembly {
            /*
            First 32 bytes stores the length of the signature

            add(sig, 32) = pointer of sig + 32
            effectively, skips first 32 bytes of signature

            mload(p) loads next 32 bytes starting at the memory address p into memory
            */

            // first 32 bytes, after the length prefix
            r := mload(add(sig, 32))
            // second 32 bytes
            s := mload(add(sig, 64))
            // final byte (first byte of the next 32 bytes)
            v := byte(0, mload(add(sig, 96)))
        }

        // implicitly return (r, s, v)
    }
}
