pragma solidity ^0.8.3;

// import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "./Initializable.sol";
// import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "./ReentrancyGuardUpgradeable.sol";

import {Operatable} from "./Operator.sol";

/**
 * @title Whitelistable
 * @dev Implements a simple list of addresses for whitelisting for a presale
 *
 * TODO: make it ownable so that we can transfer ownership
 *
 */
contract Whitelistable is Initializable, ReentrancyGuardUpgradeable, Operatable {
    event WhitelistAddressAdded(address member);
    event WhitelistAddressRemoved(address member);

    address internal _wlOwner;

    mapping (address => uint256) members;
    bool internal _presale;
    bool internal _presaleDone;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() initializer {}

    /**
     * @dev Initializes the contract setting the deployer as the initial operator.
     */
    function __Whitelistable_init() internal virtual onlyInitializing {
        __Whitelistable_init_unchained();
    }

    function __Whitelistable_init_unchained() internal virtual onlyInitializing {
        _wlOwner = msg.sender;
        _presale = false;
        _presaleDone = false;
        Operatable.__Operatable_init_unchained();
    }

    /**
     * @dev A method to
     * @return whether or not the sale is open
     */
    function presaleIsOpen() public view returns(bool) {
        return _presaleDone == false && _presale == true;
    }

    /**
     * @dev A method to
     * @return whether or not the sale is over
     */
    function presaleIsOver() public view returns(bool) {
        return _presaleDone;
    }

    /**
     * @dev A method to set the presale variable
     * @return whether or not the sale is open
     */
    function setPresale(bool presale) public onlyOperator returns(bool) {
        _presale = presale;
        return _presale;
    }

    /**
     * @dev A method to set the flag that indicates that presale is over (ie. already happened)
     * @return whether or not the sale is over
     */
    function setPresaleOver(bool presaleDone) public onlyOperator returns(bool) {
        _presaleDone = presaleDone;
        if (_presaleDone) {
            _presale = false;
        }
        return _presaleDone;
    }

    /**
     * @dev A method to check for how many mints an address is whitelisted for
     * @param _address The address to check.
     * @return the number of tokens this address has been whitelisted for
     */
    function addressCanBuy(address _address) public view returns(uint256) {
        return members[_address];
    }

    /**
     * @dev A method to increase the amount of tokens an address is allowed to buy in presale
     * @param addresses The addresses to whitelist.
     * @param amounts The amounts to whitelist the corresponding addresses for.
     */
    function loadWhitelist(address[] calldata addresses, uint256[] calldata amounts) public nonReentrant onlyOperator {
        require(addresses.length == amounts.length, "both arrays must have the same length");

        for (uint256 i = 0; i < addresses.length; i++) {
            require(amounts[i] > 0, "Amount must be non-zero");
            if (members[addresses[i]] == 0) {
                delete members[addresses[i]];
            }
            members[addresses[i]] = amounts[i];
        }
    }

    /**
     * @dev A method to increase the amount of tokens an address is allowed to buy in presale
     * @param _address The address to whitelist.
     * @return the number of tokens this address has been whitelisted for
     */
    function increaseWhitelistCount(address _address, uint256 amount) public nonReentrant onlyOperator returns(uint256) {
        if (members[_address] == 0) {
            members[_address] = amount;
        } else {
            members[_address] = members[_address] + amount;
        }
        return members[_address];
    }

    /**
     * @dev A method to decrease the amount of tokens an address is allowed to buy in presale
     * @param _address The address to un-whitelist/decrease the amount for.
     * @param amount The amount to decrease the whitelist by. If set to 0 the address gets taken off the whitelist
     * @return the number of tokens this address has been whitelisted for
     */
    function decreaseWhitelistCount(address _address, uint256 amount) public returns(uint256) {
        require(members[_address] > 0, "address is not whitelisted");
        require(members[_address] >= amount, "address whitelist count is not sufficient");
        if (amount > 0) {
            members[_address] = members[_address] - amount;
        } else {
            members[_address] = 0;
        }
        if (members[_address] == 0) {
            delete members[_address];
        }

        return members[_address];
    }

    /**
     * @dev A method to decrease the amount of tokens the message sender is allowed to buy in presale. Used when buying tokens in presale mode
     * @param amount The amount to decrease the whitelist by. If set to 0 the address gets taken off the whitelist
     * @return the number of tokens this address has been whitelisted for
     */
    function decreaseSenderWhitelistCount(uint256 amount) public returns(uint256) {
        return decreaseWhitelistCount(msg.sender, amount);
    }
}
