    "src/SignPass.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
// solhint-disable max-line-length
pragma solidity ^0.8.24;

import { OwnableUpgradeable } from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import { UUPSUpgradeable } from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import { ERC721AQueryableUpgradeable } from "erc721a-upgradeable/contracts/extensions/ERC721AQueryableUpgradeable.sol";
import { ERC721AUpgradeable } from "erc721a-upgradeable/contracts/ERC721AUpgradeable.sol";
import { IERC721AUpgradeable } from "erc721a-upgradeable/contracts/IERC721AUpgradeable.sol";
import { ISP } from "@ethsign/sign-protocol-evm/src/interfaces/ISP.sol";
import { ISPHook } from "@ethsign/sign-protocol-evm/src/interfaces/ISPHook.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { IERC6066 } from "./IERC6066.sol";
import { IERC1271 } from "@openzeppelin/contracts/interfaces/IERC1271.sol";

contract SignPass is OwnableUpgradeable, UUPSUpgradeable, ERC721AQueryableUpgradeable, ISPHook, IERC6066, IERC1271 {
    uint256 public constant MAX_TOTAL_SUPPLY = 1000;

    // keccak256(abi.encode(uint256(keccak256("ethsign.misc.SignPass")) - 1)) & ~bytes32(uint256(0xff))
    bytes32 private constant SignPassStorageLocation =
        0xb9b3abae6ccc863f7f9206c4853133fd420450b77b02f95a306191ef8077bc00;

    /// @custom:storage-location erc7201:ethsign.misc.SignPass
    struct SignPassStorage {
        address spInstance;
        uint64 approvedSpSchemaId;
        address approvedSpAttester;
        string baseURI;
        mapping(uint256 tokenId => bool transferable) isUntransferable;
        mapping(uint256 tokenId => mapping(bytes32 hash => bool approved)) _erc6066;
    }

    error OverMint();
    error Unsupported();
    error InvalidData();
    error LowLevelCallError(bytes data);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        if (block.chainid != 31_337) _disableInitializers();
    }

    function initialize() external initializer initializerERC721A {
        __ERC721A_init_unchained("SignPass", "SIGNPASS");
        __Ownable_init_unchained(_msgSender());
    }

    function setSignProtocolConfig(
        address spInstance,
        uint64 approvedSpSchemaId,
        address approvedSpAttester
    )
        external
        onlyOwner
    {
        SignPassStorage storage $ = _getSignPassStorage();
        $.spInstance = spInstance;
        $.approvedSpSchemaId = approvedSpSchemaId;
        $.approvedSpAttester = approvedSpAttester;
    }

    function setBaseURI(string calldata baseURI) external onlyOwner {
        _getSignPassStorage().baseURI = baseURI;
    }

    function safeMint(address to, uint256 quantity) external onlyOwner {
        _safeMint(to, quantity);
    }

    function safeTransferFrom(address from, address to, uint256[] calldata tokenIds) external {
        for (uint256 i = 0; i < tokenIds.length; i++) {
            safeTransferFrom(from, to, tokenIds[i], "");
        }
    }

    function sign(uint256 tokenId, bytes32 hash, bool approved) external {
        if (ownerOf(tokenId) != _msgSenderERC721A()) revert Unsupported();
        _getSignPassStorage()._erc6066[tokenId][hash] = approved;
        emit ERC6066Signed(ownerOf(tokenId), tokenId, hash, approved);
    }

    function withdrawETH() external onlyOwner {
        (bool success, bytes memory data) = owner().call{ value: address(this).balance }("");
        if (!success) revert LowLevelCallError(data);
    }

    function didReceiveAttestation(
        address attester,
        uint64 schemaId,
        uint64 attestationId,
        bytes calldata extraData
    )
        external
        payable
    {
        _getSignPassStorage().isUntransferable[_checkAndGetTokenIdFromHook(
            _msgSender(), attester, schemaId, attestationId, extraData
        )] = true;
    }

    function didReceiveRevocation(
        address attester,
        uint64 schemaId,
        uint64 attestationId,
        bytes calldata extraData
    )
        external
        payable
    {
        _getSignPassStorage().isUntransferable[_checkAndGetTokenIdFromHook(
            _msgSender(), attester, schemaId, attestationId, extraData
        )] = false;
    }

    function isValidSignature(bytes32 hash, bytes memory signature) external view returns (bytes4 magicValue) {
        uint256 tokenId = abi.decode(signature, (uint256));
        return isValidSignature(tokenId, hash, "");
    }

    function didReceiveRevocation(address, uint64, uint64, IERC20, uint256, bytes calldata) external pure override {
        revert Unsupported();
    }

    function didReceiveAttestation(address, uint64, uint64, IERC20, uint256, bytes calldata) external pure override {
        revert Unsupported();
    }

    function transferFrom(
        address from,
        address to,
        uint256 tokenId
    )
        public
        payable
        virtual
        override(ERC721AUpgradeable, IERC721AUpgradeable)
    {
        if (_getSignPassStorage().isUntransferable[tokenId]) revert Unsupported();
        ERC721AUpgradeable.transferFrom(from, to, tokenId);
    }

    function isValidSignature(uint256 tokenId, bytes32 hash, bytes memory) public view returns (bytes4 magicValue) {
        return _getSignPassStorage()._erc6066[tokenId][hash] ? IERC6066.isValidSignature.selector : bytes4(0);
    }

    function _mint(address to, uint256 quantity) internal override {
        if (totalSupply() + quantity > MAX_TOTAL_SUPPLY) revert OverMint();
        super._mint(to, quantity);
    }

    function _checkAndGetTokenIdFromHook(
        address sender,
        address attester,
        uint64 schemaId,
        uint64 attestationId,
        bytes calldata extraData
    )
        internal
        view
        returns (uint256 tokenId)
    {
        SignPassStorage storage $ = _getSignPassStorage();
        if (sender != $.spInstance || schemaId != $.approvedSpSchemaId || attester != $.approvedSpAttester) {
            revert Unsupported();
        }
        address recipient = abi.decode(ISP($.spInstance).getAttestation(attestationId).recipients[0], (address));
        tokenId = abi.decode(extraData, (uint256));
        if (ownerOf(tokenId) != recipient) revert InvalidData();
    }

    // solhint-disable-next-line no-empty-blocks
    function _authorizeUpgrade(address newImplementation) internal virtual override onlyOwner { }

    function _baseURI() internal view virtual override returns (string memory) {
        return _getSignPassStorage().baseURI;
    }

    function _getSignPassStorage() internal pure returns (SignPassStorage storage $) {
        assembly {
            $.slot := SignPassStorageLocation
        }
    }
}
"
    }
