      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";

contract PngvnHandler is Ownable, ReentrancyGuard {

    IERC20 public pngvnToken;

    uint256 public totalDeposits;
    uint256 public activeRoundId;
    bytes32 public root;

    mapping(bytes32 => bool) public leafClaimed;
    mapping(address => uint256) public balances;

    enum ClaimStatus {
        PAUSED,
        LIVE
    }

    ClaimStatus public claimStatus;

    event Deposited(address holder, uint256 amount, uint256 timestamp);
    event Withdrew(address holder, uint256 amount, uint256 timestamp);
    event HolderShareClaimed(address holder, uint256 amount,uint256 roundId, uint256 timestamp);
    event DividendRoundCreated(uint256 roundId, uint256 poolSize, uint256 timestamp);
    event DividendRoundFinalized(uint256 roundId, uint256 unclaimedDividends, uint256 timestamp);
    event DividendRootSet(bytes32 root, uint256 roundId, uint256 timestamp);
    event ClaimStatusUpdated(ClaimStatus newStatus, uint256 timestamp);


    constructor(address initialOwner, address _pngvnToken) Ownable(initialOwner) {
        pngvnToken = IERC20(_pngvnToken);
    }


    function setRoot(bytes32 _root) external onlyOwner {

        root = _root;

        emit DividendRootSet(_root, activeRoundId, block.timestamp);
    }

    function setClaimStatus(ClaimStatus _newClaimStatus) external onlyOwner {

        claimStatus = _newClaimStatus;

        emit ClaimStatusUpdated(_newClaimStatus, block.timestamp);
    }

    function setNewClaimRound(bytes32 _root)payable external onlyOwner {

        claimStatus = ClaimStatus.LIVE;
        root = _root;
        activeRoundId ++;

        emit DividendRoundCreated(activeRoundId, msg.value, block.timestamp);
    }

    function finalizeClaimRound() external onlyOwner {

        claimStatus = ClaimStatus.PAUSED;

        bool unclaimedRewards = address(this).balance > 0;

        if(unclaimedRewards){
            uint256 unclaimedDividends = address(this).balance;

            (bool success, ) = owner().call{value: unclaimedDividends}("");
            require(success, "Failed to send unclaimed rewards");

            emit DividendRoundFinalized(activeRoundId, unclaimedDividends, block.timestamp);
        }

        emit DividendRoundFinalized(activeRoundId, 0, block.timestamp);
    }


    function deposit(uint256 amount) public nonReentrant{

        balances[msg.sender] += amount;
        totalDeposits += amount;

        require(
           amount > 0 && pngvnToken.transferFrom(msg.sender, address(this), amount),
            "Deposit failed"
        );

        emit Deposited(msg.sender, amount, block.timestamp);
    }

    function withdraw(uint256 amount) public nonReentrant{
        require(
            amount > 0, "Withdraw amount must be greater than zero"
        );

        require(
            balances[msg.sender] >= amount,
            "Insufficient balance"
        );

        balances[msg.sender] -= amount;
        totalDeposits -= amount;

        require(
            pngvnToken.transfer(msg.sender, amount),
            "Withdrawal failed"
        );

        emit Withdrew(msg.sender, amount, block.timestamp);
    }

    function claimShare(uint256 holderShare, bytes32[] calldata proof) external nonReentrant{

        bytes32 leaf = keccak256(abi.encodePacked(msg.sender, holderShare));

        require(
            claimStatus == ClaimStatus.LIVE,
            "Claim is not live"
        );

        require(
            !leafClaimed[leaf],
            "Share already claimed"
        );

        require(
            MerkleProof.verify(proof, root, leaf),
            "Invalid proof"
        );

        leafClaimed[leaf] = true;

        (bool success, ) = address(msg.sender).call{value: holderShare}("");

        require(
            success,
            "Failed to send share"
        );

        emit HolderShareClaimed(msg.sender, holderShare, activeRoundId, block.timestamp);
    }

    function adminWithdraw() external onlyOwner {
        (bool success, ) = owner().call{value: address(this).balance}("");
        require(success, "Withdraw failed !");
    }

    receive() external payable {}
}"
