    "contracts/SwapMeet.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.23;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./EIP712.sol";
import "./errors.sol";

event SwapCreated(bytes32 swapId);
event PublicSaleActive(uint16 dropId);
event PublicSaleDeactivated(uint16 dropId);
event PreSaleActive(uint16 dropId);
event PreSaleDeactivated(uint16 dropId);
event DropCreated(uint16 dropId);

contract SwapMeet is AccessControl, ReentrancyGuard, Pausable, EIP712 {

    struct Drop {
        address tokenContract;
        address transferDestination;
        uint16 minimumTokenId;
        uint16 maximumTokenId;
        uint8 requiredTokenCount;
        uint8 maximumSwapsPerAddress;
        uint8 maximumSwaps;
        bool publicSaleActive;
        bool preSaleActive;
    }

    struct Swap {
        uint16 dropId;
        uint[] tokenIds;
        address holderAddress;
    }

    mapping(uint16 => Drop) public drops;
    mapping(bytes32 => Swap) public swaps;
    mapping(uint256 => mapping(address => uint256)) public swapsPerAddress;
    mapping(uint256 => uint256) public swapsPerDrop;
    mapping(address => bytes32[]) private userSwaps;

    mapping(uint16 => mapping(address => uint256)) public preSaleSwaps;

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(OPERATOR_ROLE, msg.sender);

        setSigningAddress(msg.sender);
    }

    function preSaleSwap(uint16 dropId, bytes32 swapId, uint[] calldata tokenIds, bytes calldata signature)
        external nonReentrant whenNotPaused requiresWhitelist(signature) {
        Drop storage drop = drops[dropId];

        // Ensure presale is active
        if (!drop.preSaleActive) revert PreSaleIsNotActive(dropId);

        // Ensure User has not already swapped
        if (preSaleSwaps[dropId][msg.sender] >= 1)
            revert MaxPresaleSwapsReached();

        // Enforce swap maximums
        if (swapsPerDrop[dropId] >= drop.maximumSwaps)
            revert SwapLimitReached();

        // Enforce swap limits
        if (swapsPerAddress[dropId][msg.sender] >= drop.maximumSwapsPerAddress)
            revert TooManySwaps(msg.sender);

        // Do not allow swaps to be reused
        Swap memory existingSwap = swaps[swapId];
        if (existingSwap.holderAddress != address(0))
            revert SwapAlreadyExists(swapId);

        // Cache variables to save gas
        address tokenContract = drop.tokenContract;
        address transferDestination = drop.transferDestination;

        if (tokenIds.length != drop.requiredTokenCount)
            revert InsufficientTokens();
        if (!IERC721(tokenContract).isApprovedForAll(msg.sender, address(this)))
            revert NotApprovedForAll();

        for (uint i = 0; i < tokenIds.length; i++) {
            uint tokenId = tokenIds[i];

            if (tokenId < drop.minimumTokenId || tokenId > drop.maximumTokenId)
                revert InvalidTokenIdForDrop(tokenId);
            if (IERC721(tokenContract).ownerOf(tokenId) != msg.sender)
                revert SenderDoesNotOwnToken(tokenId);

            IERC721(tokenContract).transferFrom(
                msg.sender,
                transferDestination,
                tokenId
            );
        }

        swaps[swapId] = Swap(dropId, tokenIds, msg.sender);

        swapsPerDrop[dropId]++;
        swapsPerAddress[dropId][msg.sender]++;
        preSaleSwaps[dropId][msg.sender]++;

        userSwaps[msg.sender].push(swapId);

        emit SwapCreated(swapId);
    }

    function isPreSaleEligible(uint16 dropId, bytes calldata signature) requiresWhitelist(signature) view external returns(bool) {
        if (!drops[dropId].preSaleActive) revert DropIsNotActive(dropId);

        uint256 preSaleCount = preSaleSwaps[dropId][msg.sender];

        return preSaleCount >= 1 ? false : true;
    }

    function swap(
        uint16 dropId,
        bytes32 swapId,
        uint[] calldata tokenIds
    ) external nonReentrant whenNotPaused {
        Drop storage drop = drops[dropId];

        // Enforce swap maximums
        if (swapsPerDrop[dropId] >= drop.maximumSwaps)
            revert SwapLimitReached();

        // Enforce swap limits
        if (swapsPerAddress[dropId][msg.sender] >= drop.maximumSwapsPerAddress)
            revert TooManySwaps(msg.sender);

        // Do not allow swaps to be reused
        Swap memory existingSwap = swaps[swapId];
        if (existingSwap.holderAddress != address(0))
            revert SwapAlreadyExists(swapId);

        // Cache variables to save gas
        address tokenContract = drop.tokenContract;
        address transferDestination = drop.transferDestination;
        uint minimumTokenId = drop.minimumTokenId;
        uint maximumTokenId = drop.maximumTokenId;

        if (!drop.publicSaleActive) revert DropIsNotActive(dropId);
        if (tokenIds.length != drop.requiredTokenCount)
            revert InsufficientTokens();
        if (!IERC721(tokenContract).isApprovedForAll(msg.sender, address(this)))
            revert NotApprovedForAll();

        for (uint i = 0; i < tokenIds.length; i++) {
            uint tokenId = tokenIds[i];

            if (tokenId < minimumTokenId || tokenId > maximumTokenId)
                revert InvalidTokenIdForDrop(tokenId);
            if (IERC721(tokenContract).ownerOf(tokenId) != msg.sender)
                revert SenderDoesNotOwnToken(tokenId);

            IERC721(tokenContract).transferFrom(
                msg.sender,
                transferDestination,
                tokenId
            );
        }

        swaps[swapId] = Swap(dropId, tokenIds, msg.sender);

        swapsPerDrop[dropId]++;
        swapsPerAddress[dropId][msg.sender]++;

        userSwaps[msg.sender].push(swapId);

        emit SwapCreated(swapId);
    }

    function getSwapsByAddress(address user) external view returns (bytes32[] memory) {
        return userSwaps[user];
    }

    function getDrop(uint16 dropId) external view returns (Drop memory) {
        return drops[dropId];
    }

    function getSwap(bytes32 swapId) external view returns (Swap memory) {
        return swaps[swapId];
    }


    function createDrop(
        uint16 dropId,
        address tokenContract,
        address transferDestination,
        uint16 minimumTokenId,
        uint16 maximumTokenId,
        uint8 requiredTokenCount,
        uint8 maximumSwapsPerAddress,
        uint8 maximumSwaps
    ) external onlyOperators {
        if(tokenContract == address(0)) revert InvalidTokenContract();

        drops[dropId] = Drop(
            tokenContract,
            transferDestination,
            minimumTokenId,
            maximumTokenId,
            requiredTokenCount,
            maximumSwapsPerAddress,
            maximumSwaps,
            false,
            true
        );

        emit DropCreated(dropId);
    }

    function activatePublicSale(uint16 dropId) external onlyOperators {
        drops[dropId].publicSaleActive = true;

        emit PublicSaleActive(dropId);
    }

    function deactivateDrop(uint16 dropId) external onlyOperators {
        drops[dropId].publicSaleActive = false;

        emit PublicSaleDeactivated(dropId);
    }

    function activatePreSale(uint16 dropId) external onlyOperators {
        drops[dropId].preSaleActive = true;

        emit PreSaleActive(dropId);
    }

    function deactivatePreSale(uint16 dropId) external onlyOperators {
        drops[dropId].preSaleActive = false;

        emit PreSaleDeactivated(dropId);
    }
}
