     * BoxType.SEASON1_MAJESTIC_BOX
     * - Majestic box type for season1
     *
     * BoxType.SEASON1_BOOSTER_OMEGA_BOX
     * - Booster Omega box type for season1
     *
     * BoxType.SEASON1_BOOSTER_PREMIUM_BOX
     * - Booster Premium box type for season1
     *
     * BoxType.SEASON1_BOOSTER_BASIC_BOX
     * - Booster Basic box type for season1
     *
     * BoxType.SEASON1_FOUNDERS_BOX
     * - Founders box type for season1
     */
    uint public constant SEASON1_MAJESTIC_BOX = 1;
    uint public constant SEASON1_BOOSTER_OMEGA_BOX = 20;
    uint public constant SEASON1_BOOSTER_PREMIUM_BOX = 21;
    uint public constant SEASON1_BOOSTER_BASIC_BOX = 22;
    uint public constant SEASON1_FOUNDERS_BOX = 30;
}

interface IWLBox is IERC721AUpgradeable {
    function mint(address to, uint256 quantity, bool reserved) external returns (uint256, uint256);
    function burn(uint256[] calldata tokenIds) external;
    function claimCount() external view returns (uint);
}

interface IWLBoxMint {
    function mint(address to, uint256 quantity, bool reserved) external returns (uint256, uint256);
}"
    },
    "contracts/WLBoxShop.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.18;

import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/cryptography/MerkleProofUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/cryptography/ECDSAUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "./IWLBox.sol";
import "./WLVerify.sol";

/**
 * @dev WLBoxShop is a shop contract selling various boxes
 * Those box NFTs will be used to claim part NFTs
 */
contract WLBoxShop is
    WLVerify,
    AccessControlUpgradeable, 
    UUPSUpgradeable, 
    ReentrancyGuardUpgradeable {
    /**
     * @dev Roles
     * DEFAULT_ADMIN_ROLE
     * - can update role of each account
     *
     * OPERATOR_ROLE
     * - can enable/disable shopEnabled
     * - can update allow list
     * - can update box settings
     *
     * RESERVE_MINTER_ROLE
     * - can call mintReservedBox function
     *
     * DEPLOYER_ROLE
     * - can update the logic contract     
     */
    bytes32 public constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");
    bytes32 public constant RESERVE_MINTER_ROLE = keccak256("RESERVE_MINTER_ROLE");
    bytes32 public constant DEPLOYER_ROLE = keccak256("DEPLOYER_ROLE");

    IERC721 public promoteToken;
    address public paymentReceiver;
    address public encryptor;
    IERC20Upgradeable public paymentToken;
    bytes32 public allowListMerkleRoot;

    mapping(uint => Box) public boxes;
    mapping(uint256 => address) public promoteTokenUsedInClaim;
    mapping(address => bool) public accountUsedInSeason1;

    bool public shopEnabled;
    bool public claimEnabled;
    bool public checkAllowList;
    bool public purchaseLimited;

    struct Box {
        IWLBoxMint boxContract;
        uint256 price;
        uint maxCount;
        uint purchased;
        uint accountLimit;
    }

    struct PurchaseCount {
        uint boxType;
        uint maxCount;
        uint purchased;
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() initializer {}

    function _authorizeUpgrade(address) internal override only(DEPLOYER_ROLE) {}

    /**
     * @dev
     * Params
     * `adminAddress`: `DEFAULT_ADMIN_ROLE` will be granted
     * `operatorAddress`: `OPERATOR_ROLE` will be granted
     * `promoteTokenAddress`:  a NFT contract address that can claim founders box
     * `_encryptor`: admin address that encrypts openBox transaction data
     * `_paymentReceiver`: box payment fee will be transferred to this account
     * `_paymentToken`: box payment will receive this token
     */
    function initialize(
        address adminAddress,
        address operatorAddress,
        address promoteTokenAddress,
        address reservedBoxMinterAddress,
        address _encryptor,
        address _paymentReceiver,
        address _paymentToken,
        address _warmWalletContract,
        address _delegateCashContract) initializer public {
        paymentReceiver = _paymentReceiver;
        paymentToken = IERC20Upgradeable(_paymentToken);
        promoteToken = IERC721(promoteTokenAddress);
        encryptor = _encryptor;
        shopEnabled = true;
        claimEnabled = false;
        checkAllowList = true;
        purchaseLimited = true;

        __ReentrancyGuard_init();
        __WLVerify_init(_warmWalletContract, _delegateCashContract);
        _setupRole(DEFAULT_ADMIN_ROLE, adminAddress);
        _setupRole(OPERATOR_ROLE, operatorAddress);
        _setupRole(RESERVE_MINTER_ROLE, reservedBoxMinterAddress);
        _setupRole(DEPLOYER_ROLE, _msgSender());
    }

    // modifier
    modifier only(bytes32 role) {
        require(hasRole(role, _msgSender()), "Caller does not have permission");
       _;
    }

    /**
     * @dev Claims founders box from promote token NFTs
     * To support delegated wallet, it fetches token owner from delegated service and aggregates count to mint NFTs
     * 
     * Requirements
     * - message sender should have the right of promote token NFTs that are not used before
     */
    function claimBox(uint256[] memory tokenIds) external nonReentrant {
        require(claimEnabled, "ClaimBox has not been enabled");
        require(tokenIds.length > 0, "Invalid tokenId list");
        uint256[] memory tokenIds2 = new uint256[](tokenIds.length);

        uint idCount = tokenIds.length;
        address currentTokenOwner = address(0);
        uint nextIndex = 0;

        while (idCount > 0) {
            uint count = 0;
            for (uint i=0; i<idCount; i++) {
                (bool isVerified, address tokenOwnerAddress) = verifyTokenOwner(
                    address(promoteToken),
                    tokenIds[i]
                );
                require(isVerified, "Account doesn't have all promote token NFTs");
                if (currentTokenOwner == address(0)) {
                    currentTokenOwner = tokenOwnerAddress;
                }

                if (currentTokenOwner == tokenOwnerAddress) {
                    require(tokenOwnerAddress != address(0), "Token owner should not be zero address");
                    require(promoteTokenUsedInClaim[tokenIds[i]] == address(0), "One of promote token tokenIds has already been used");
                    promoteTokenUsedInClaim[tokenIds[i]] = tokenOwnerAddress;
                    count++;
                } else {
                    tokenIds2[nextIndex] = tokenIds[i];
                    nextIndex++;
                }
            }

            _mintFoundersBox(
                currentTokenOwner, 
                count, 
                true,
                false
            );

            idCount = nextIndex;
            tokenIds = tokenIds2;
            currentTokenOwner = address(0);
            nextIndex = 0;
        }
    }

    /**
     * @dev Accounts can buy multiple box NFTs at once
     * Each box NFT contract can have different address
     * All box NFT contracts consist of ERC721A
     *
     * Params
     * - `merkleProof`: proof to validate the msg sender is in the allow list
     * - `signature`: signature to validate all parameters
     * - `blockNumberLimit`: a transaction should end before blockNumberLimit
     * - `boxCountList`: box count list that an account wants to buy
     * - `promoted`: boolean value indicating it is promoted or not

     *
     * Requirements
     * - shopEnabled must be true
     * - account can buy boxes only once in a season: checked by accountUsedInSeason1
     * - all boxes have limited total count
     * - all boxes have limited count per account
     * - msg sender should be in merkle tree to proof allow list
     * - if a account use promote NFT tokenId, It can be used only once
     * - account should have enough erc20 tokens for payment 
     */
    function buyBox(
        bytes32[] calldata merkleProof,
        bytes calldata signature,
        uint256 blockNumberLimit,
        uint[] calldata boxCountList,
        bool promoted) external nonReentrant {
        require(block.number <= blockNumberLimit, "Transaction has expired");
        require(boxCountList.length == 4, "Invalid box count list");
        require(boxCountList[0] + boxCountList[1] + boxCountList[2] + boxCountList[3] >= 1, "Total box count should be greater than 1");
        require(shopEnabled, "BuyBox has not been enabled");
        require(
            validateBuyBox(signature, blockNumberLimit, boxCountList, promoted), 
            "Invalid signature"
        );

        if(purchaseLimited) {
            require(!accountUsedInSeason1[_msgSender()], "Account has already participated in this sale");
        }

        if(checkAllowList) {
            require(isAllowed(_msgSender(), merkleProof), "Wallet address has not been allowed");
        }

        uint majesticCount = checkAvailableBoxCount(
            "Majestic Box", BoxType.SEASON1_MAJESTIC_BOX, boxCountList[0]);
        uint boosterBasicCount = checkAvailableBoxCount(
            "Booster BASIC Box", BoxType.SEASON1_BOOSTER_BASIC_BOX, boxCountList[1]);
        uint boosterPremiumCount = checkAvailableBoxCount(
            "Booster PREMIUM Box", BoxType.SEASON1_BOOSTER_PREMIUM_BOX, boxCountList[2]);
        uint boosterOmegaCount = checkAvailableBoxCount(
            "Booster OMEGA Box", BoxType.SEASON1_BOOSTER_OMEGA_BOX, boxCountList[3]);

        require(majesticCount +
            boosterBasicCount +
            boosterPremiumCount +
            boosterOmegaCount > 0, "Insufficient amount of boxes");

        // payment
        SafeERC20Upgradeable.safeTransferFrom(
            paymentToken, 
            _msgSender(), 
            paymentReceiver, 
            calculateBoxPrice(BoxType.SEASON1_MAJESTIC_BOX, majesticCount) +
            calculateBoxPrice(BoxType.SEASON1_BOOSTER_BASIC_BOX, boosterBasicCount) +
            calculateBoxPrice(BoxType.SEASON1_BOOSTER_PREMIUM_BOX, boosterPremiumCount) +
            calculateBoxPrice(BoxType.SEASON1_BOOSTER_OMEGA_BOX, boosterOmegaCount)
        );

        accountUsedInSeason1[_msgSender()] = true;
        _mintMajesticBox(_msgSender(), majesticCount, promoted, false);
        _mintBoosterBox(_msgSender(), boosterBasicCount, boosterPremiumCount, boosterOmegaCount, promoted, false);
    }

    /**
     * @dev Creates reserved boxes
     * Each box contract has a total reserved minted count to track the total number of reserved NFTs
     * 
     * Requirements
     * - the caller must have the `RESERVE_MINTER_ROLE`
     */
    function mintReservedBox(
        address to,
        uint majesticBoxCount,
        uint boosterBasicBoxCount,
        uint boosterPremiumBoxCount,
        uint boosterOmegaBoxCount,
        uint foundersBoxCount) external only(RESERVE_MINTER_ROLE) nonReentrant {
        _mintMajesticBox(to, majesticBoxCount, false, true);
        _mintBoosterBox(to, boosterBasicBoxCount, boosterPremiumBoxCount, boosterOmegaBoxCount, false, true);
        _mintFoundersBox(to, foundersBoxCount, false, true);
    }

    /**
     * @dev Returns purchased count list of majestic, booster boxes
     */
    function getPurchasedBoxCount(uint[] calldata boxTypes) external view returns (PurchaseCount[] memory) {
        PurchaseCount[] memory list = new PurchaseCount[](boxTypes.length);
        for (uint i=0; i<boxTypes.length; i++) {
            uint boxType = boxTypes[i];
            list[i].purchased = boxes[boxType].purchased;
            list[i].maxCount = boxes[boxType].maxCount;
            list[i].boxType = boxType;
        }
        return list;
    }

    /**
     * @dev Returns boolean list of whether each tokenIds has been used or not
     */
    function getPromoteTokenUsedInClaim(uint256[] calldata tokenIds) external view returns(bool[] memory) {
        bool[] memory used = new bool[](tokenIds.length);
        for (uint i=0; i<tokenIds.length; i++) {
           if (promoteTokenUsedInClaim[tokenIds[i]] != address(0)) {
            used[i] = true;
           } else {
            used[i] = false;
           }
        }

        return used;
    }

    /**
     * @dev Creates Majestic box NFTs
     */
    function _mintMajesticBox(
        address owner, uint amount, bool promoted, bool reserved) private {
        if (amount > 0) {
            (uint256 start, uint256 end) = 
                boxes[BoxType.SEASON1_MAJESTIC_BOX].boxContract.mint(owner, amount, reserved);

            if (!reserved) {
                boxes[BoxType.SEASON1_MAJESTIC_BOX].purchased += amount;
            }


            emit BoxMinted(
                _msgSender(), 
                address(boxes[BoxType.SEASON1_MAJESTIC_BOX].boxContract), 
                start,
                end,
                BoxType.SEASON1_MAJESTIC_BOX,
                promoted,
                reserved
            );
        }
    }

    /**
     * @dev Creates Booster box NFTs
     * All Booster box types(Basic, Premium, Omega) have same nft contract, it mints NFTs at once to reduce gas fee 
     */
    function _mintBoosterBox(
        address owner, 
        uint basicBoxAmount, 
        uint premiumBoxAmount, 
        uint omegaBoxAmount, 
        bool promoted, 
        bool reserved) private {
        uint total = basicBoxAmount + premiumBoxAmount + omegaBoxAmount;
        if (total > 0) {
            (uint256 start, uint256 end) = 
                boxes[BoxType.SEASON1_BOOSTER_BASIC_BOX].boxContract.mint(owner, total, reserved);
            require(end - start + 1 == total, "Invalid amount of Booster box is minted");

            if (basicBoxAmount > 0) {
                if (!reserved) {
                    boxes[BoxType.SEASON1_BOOSTER_BASIC_BOX].purchased += basicBoxAmount;
                }        
                emit BoxMinted(
                    _msgSender(), 
                    address(boxes[BoxType.SEASON1_BOOSTER_BASIC_BOX].boxContract), 
                    start,
                    start + basicBoxAmount -1,
                    BoxType.SEASON1_BOOSTER_BASIC_BOX, 
                    promoted,
                    reserved
                );
            }

            if (premiumBoxAmount > 0) {
                if (!reserved) {
                    boxes[BoxType.SEASON1_BOOSTER_PREMIUM_BOX].purchased += premiumBoxAmount;
                }

                emit BoxMinted(
                    _msgSender(), 
                    address(boxes[BoxType.SEASON1_BOOSTER_PREMIUM_BOX].boxContract), 
                    start + basicBoxAmount,
                    start + basicBoxAmount + premiumBoxAmount -1,
                    BoxType.SEASON1_BOOSTER_PREMIUM_BOX, 
                    promoted,
                    reserved
                );
            }

            if (omegaBoxAmount > 0) {
                if (!reserved) {
                    boxes[BoxType.SEASON1_BOOSTER_OMEGA_BOX].purchased += omegaBoxAmount;
                }

                emit BoxMinted(
                    _msgSender(), 
                    address(boxes[BoxType.SEASON1_BOOSTER_OMEGA_BOX].boxContract), 
                    start + basicBoxAmount + premiumBoxAmount,
                    start + basicBoxAmount + premiumBoxAmount + omegaBoxAmount -1,
                    BoxType.SEASON1_BOOSTER_OMEGA_BOX, 
                    promoted,
                    reserved
                );
            }
        }
    }

    /**
     * @dev Creates Founders box NFTs
     */
    function _mintFoundersBox(address owner, uint amount, bool promoted, bool reserved) private {
        if (amount > 0) {
            (uint256 start, uint256 end) = 
                boxes[BoxType.SEASON1_FOUNDERS_BOX].boxContract.mint(owner, amount, reserved);

            emit BoxMinted(
                owner, 
                address(boxes[BoxType.SEASON1_FOUNDERS_BOX].boxContract), 
                start,
                end,
                BoxType.SEASON1_FOUNDERS_BOX, 
                promoted,
                reserved
            );
        }
    }

    function calculateBoxPrice(uint boxType, uint amount) internal view returns (uint) {
        if (amount <= 0) {
            return 0;
        }
        return boxes[boxType].price * amount;
    }

    function checkAvailableBoxCount(string memory boxName, uint boxType, uint amount) internal view returns (uint) {
        if(amount <= 0) {
            return 0;
        }

        require(
            amount <= boxes[boxType].accountLimit,
            string(abi.encodePacked(boxName, " cannot purchase more than the allotted limit"))
        );

        if (boxes[boxType].purchased + amount >  boxes[boxType].maxCount) {
            // return available count
            return boxes[boxType].maxCount - boxes[boxType].purchased;
        }

        return amount;
    }

    /**
     * @dev Validates buyBox function parameters
     */
    function validateBuyBox(
        bytes calldata signature,        
        uint256 blockNumberLimit,
        uint[] calldata boxCountList,
        bool promoted) internal view returns (bool) {
        bytes32 hashed = keccak256(abi.encode(_msgSender(), blockNumberLimit, boxCountList, promoted));
        (address recovered, ECDSAUpgradeable.RecoverError error) = ECDSAUpgradeable.tryRecover(hashed, signature);

        if (error == ECDSAUpgradeable.RecoverError.NoError && recovered == encryptor ) {
            return true;
        }

        return false;
    }

    /**
     * @dev Checks allow list
     */
    function isAllowed(address account, bytes32[] calldata merkleProof) public view returns (bool) {
        return MerkleProofUpgradeable.verifyCalldata(
                    merkleProof, 
                    allowListMerkleRoot, 
                    keccak256(abi.encodePacked(account))
                );
    }

    // admin
    /**
     * @dev Enables shop
     *
     * Requirements
     * - the caller must have the `OPERATOR_ROLE`
     */
    function setShopEnabled(bool shop, bool claim) external only(OPERATOR_ROLE) {
        shopEnabled = shop;
        claimEnabled = claim;
    }

    /**
     * @dev Sets allow list
     *
     * Requirements
     * - the caller must have the `OPERATOR_ROLE`
     */
    function setAllowList(bool use, bytes32 merkleRoot) external only(OPERATOR_ROLE) {
        checkAllowList = use;
        allowListMerkleRoot = merkleRoot;
    }

    /**
     * @dev Sets purchaseLimited
     *
     * Requirements
     * - the caller must have the `OPERATOR_ROLE`
     */
    function setPurchaseLimit(bool value) external only(OPERATOR_ROLE) {
        purchaseLimited = value;
    }

    /**
     * @dev Sets Encryptor
     *
     * Requirements
     * - the caller must have the `OPERATOR_ROLE`
     */
    function updateEncryptor(address _encryptor) external only(OPERATOR_ROLE) {
        require(_encryptor != address(0), "Zero address cannot be used");
        encryptor = _encryptor;
    }

    /**
     * @dev Sets box settings
     *
     * Params
     * - `boxTypeList`: each box type can have different contract address (ex, BoxType.SEASON1_MAJESTIC_BOX)
     * - `maxCountList`: max count limits total selling amount of each box
     * - `accountLimitList`: each account can buy each box up to accountLimit
     * - `priceList`: selling price of each box
     * - `contractList`: box NFT contract list
     * 
     * Requirements
     * - the caller must have the `OPERATOR_ROLE`
     */
    function setBox(
        uint[] calldata boxTypeList, 
        uint[] calldata maxCountList,
        uint[] calldata accountLimitList,
        uint256[] calldata priceList,
        address majesticBoxContract,
        address boosterBoxContract,
        address foundersBoxContract) external only(OPERATOR_ROLE) {
        require(boxTypeList.length == maxCountList.length, "Invalid array length at maxCount");
        require(boxTypeList.length == accountLimitList.length, "Invalid array length at accountLimit");
        require(boxTypeList.length == priceList.length, "Invalid array length at price");

        for(uint i=0; i<boxTypeList.length; i++) {
            if (boxTypeList[i] == BoxType.SEASON1_MAJESTIC_BOX) {
                boxes[boxTypeList[i]].boxContract = IWLBoxMint(majesticBoxContract);
            } else if (boxTypeList[i] == BoxType.SEASON1_FOUNDERS_BOX) {
                boxes[boxTypeList[i]].boxContract = IWLBoxMint(foundersBoxContract);
            } else {
                boxes[boxTypeList[i]].boxContract = IWLBoxMint(boosterBoxContract);        
            }

            boxes[boxTypeList[i]].price = priceList[i];
            boxes[boxTypeList[i]].maxCount = maxCountList[i];
            boxes[boxTypeList[i]].accountLimit = accountLimitList[i];
        }
    }

    function updatePaymentToken(address _paymentReceiver, address _paymentToken) external only(OPERATOR_ROLE) {
        require(_paymentReceiver != address(0), "Invalid payment receiver");
        require(_paymentToken != address(0), "Invalid payment token");
        paymentReceiver = _paymentReceiver;
        paymentToken = IERC20Upgradeable(_paymentToken);
    }

    // events
    event BoxMinted(
        address indexed owner, 
        address boxContract, 
        uint256 start, 
        uint256 end, 
        uint boxType, 
        bool promoted,
        bool reserved
    );

    /**
     * @dev This empty reserved space is put in place to allow future versions to add new
     * variables without shifting down storage in the inheritance chain.
     * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
     */
    uint256[50] private __gap;
}"
    },
    "contracts/WLVerify.sol": {
      "content": "// SPDX-License-Identifier: MIT
// This contract is forked from https://etherscan.io/address/0xe2be48D3bE2663A83b433E88205b39fF48F0FF4c
pragma solidity 0.8.18;
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

interface WarmInterface {
    function ownerOf(
        address contractAddress,
        uint256 tokenId
    ) external view returns (address);
}

interface DelegateCashInterface {
    function checkDelegateForToken(
        address delegate,
        address vault,
        address contract_,
        uint256 tokenId
    ) external view returns (bool);
}

error ZeroAddressCheck();

/**
 * @title WLVerify - check for token ownership via contract, warm wallet and delegate cash
 * Warm Wallet https://github.com/wenewlabs/public/tree/main/HotWalletProxy
 * Delegate.cash https://github.com/delegatecash/delegation-registry
 */
abstract contract WLVerify is Initializable {
    address public WARM_WALLET_CONTRACT;
    address public DELEGATE_CASH_CONTRACT;

    function __WLVerify_init(address _warmWalletContract, address _delegateCashContract) internal onlyInitializing {
        if (
            _warmWalletContract == address(0) ||
            _delegateCashContract == address(0)
        ) revert ZeroAddressCheck();
        WARM_WALLET_CONTRACT = _warmWalletContract;
        DELEGATE_CASH_CONTRACT = _delegateCashContract;
    }

    /**
     * @notice verify contract token based claim using warm wallet and delegate cash
     * @param tokenContract the smart contract address of the token
     * @param tokenId the tokenId
     * @return bool token ownership check
     * @return address token owner's wallet address
     */
    function verifyTokenOwner(
        address tokenContract,
        uint256 tokenId
    ) internal view returns (bool, address) {
        address tokenOwner = IERC721(tokenContract).ownerOf(tokenId);
        if (tokenOwner == address(0)) revert ZeroAddressCheck();
        // 1. check contract token owner
        // 2. check warm wallet delegation - ownerOf()
        //      all delegation
        //      is a mapping of token owner's wallet to hot wallet
        //      coldWalletToHotWallet[owner].walletAddress
        // 3. check delegate.cash delegation - checkDelegateForToken()
        //      checks three forms of delegation all, contract, and contract/token id
        return (
            (msg.sender == tokenOwner ||
                msg.sender ==
                WarmInterface(WARM_WALLET_CONTRACT).ownerOf(
                    tokenContract,
                    tokenId
                ) ||
                DelegateCashInterface(DELEGATE_CASH_CONTRACT)
                    .checkDelegateForToken(
                        msg.sender,
                        tokenOwner,
                        tokenContract,
                        tokenId
                    )),
            tokenOwner
        );
    }
}"
    },
    "erc721a-upgradeable/contracts/IERC721AUpgradeable.sol": {
      "content": "// SPDX-License-Identifier: MIT
// ERC721A Contracts v4.2.3
// Creator: Chiru Labs

pragma solidity ^0.8.4;

/**
 * @dev Interface of ERC721A.
 */
interface IERC721AUpgradeable {
    /**
     * The caller must own the token or be an approved operator.
     */
    error ApprovalCallerNotOwnerNorApproved();

    /**
     * The token does not exist.
     */
    error ApprovalQueryForNonexistentToken();

    /**
     * Cannot query the balance for the zero address.
     */
    error BalanceQueryForZeroAddress();

    /**
     * Cannot mint to the zero address.
     */
    error MintToZeroAddress();

    /**
     * The quantity of tokens minted must be more than zero.
     */
    error MintZeroQuantity();

    /**
     * The token does not exist.
     */
    error OwnerQueryForNonexistentToken();

    /**
     * The caller must own the token or be an approved operator.
     */
    error TransferCallerNotOwnerNorApproved();

    /**
     * The token must be owned by `from`.
     */
    error TransferFromIncorrectOwner();

    /**
     * Cannot safely transfer to a contract that does not implement the
     * ERC721Receiver interface.
     */
    error TransferToNonERC721ReceiverImplementer();

    /**
     * Cannot transfer to the zero address.
     */
    error TransferToZeroAddress();

    /**
     * The token does not exist.
     */
    error URIQueryForNonexistentToken();

    /**
     * The `quantity` minted with ERC2309 exceeds the safety limit.
     */
    error MintERC2309QuantityExceedsLimit();

    /**
     * The `extraData` cannot be set on an unintialized ownership slot.
     */
    error OwnershipNotInitializedForExtraData();

    // =============================================================
    //                            STRUCTS
    // =============================================================

    struct TokenOwnership {
        // The address of the owner.
        address addr;
        // Stores the start time of ownership with minimal overhead for tokenomics.
        uint64 startTimestamp;
        // Whether the token has been burned.
        bool burned;
        // Arbitrary data similar to `startTimestamp` that can be set via {_extraData}.
        uint24 extraData;
    }

    // =============================================================
    //                         TOKEN COUNTERS
    // =============================================================

    /**
