interface INONE {
    function unblacklist(address) external;
    function blacklistLiquidityPool(address) external;
    function withdrawStuckEth(address) external;
    function withdrawStuckToken(address, address) external;
}

contract NoneHelper is Ownable {

    address public teamTreasury;

    address public holderRewardsReserve;

    uint256 public teamAllocation = 80;

    INONE public noneTokenContract;

    constructor(address _teamTreasury, address _holderRewardsReserve) Ownable(msg.sender) {
        teamTreasury = _teamTreasury;
        holderRewardsReserve = _holderRewardsReserve;
    }

    function setNoneTokenContract (address _token) external onlyOwner {
        noneTokenContract = INONE(_token);
    }

    function changeTeamTreasury(address _teamTreasury) external onlyOwner {
        teamTreasury = _teamTreasury;
    }

    function changeHolderReserve(address _holderRewardsReserve) external onlyOwner {
        holderRewardsReserve = _holderRewardsReserve;
    }

    function changeTeamAllocation(uint256 _teamAllocation) external onlyOwner {
        require(_teamAllocation <= 100, "Cannot be more than 100%");
        teamAllocation = _teamAllocation;
    }

    function withdrawExcessEth() external onlyOwner {
        // Send remaining balance to teamTreasury
        (bool success, ) = teamTreasury.call{value: address(this).balance}("");
        require(success, "Transfer to Team Treasury failed.");
    }

    // Calls main token contract to withdraw any stuck ETH.
    function withdrawStuckEth() external onlyOwner {
        noneTokenContract.withdrawStuckEth(owner());
    }

    // Calls main token contract to withdraw any stuck tokens.
    function withdrawStuckToken(address _token) external onlyOwner {
        noneTokenContract.withdrawStuckToken(_token, owner());
    }

    // Unblacklist any Uniswap V3 LP Pools if we chose to use them in the future.
    function unblacklist(address _accountOrContract) external onlyOwner {
        noneTokenContract.unblacklist(_accountOrContract);
    }

    // Ability to blacklist any Uniswap V3 LP pools.
    function blacklistLiquidityPool(address _contract) external onlyOwner {
        noneTokenContract.blacklistLiquidityPool(_contract);
    }

    receive() external payable {
        unchecked {
        uint256 amountReceived = msg.value;
        //Calculates reward allocation by subtracting the amount allocated to the team
        uint256 rewardAllocation = 100 - teamAllocation;

        (bool success, ) = teamTreasury.call{value: (amountReceived / 100 * teamAllocation)}("");
        require(success, "Transfer to Team Treasury failed");
        (success, ) = holderRewardsReserve.call{value: (amountReceived / 100 * rewardAllocation)}("");
        require(success, "Transfer to Reward Reserve failed");
        }
    }

}
