pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

import "@balancer-labs/v2-interfaces/contracts/vault/IAuthorizer.sol";
import "@balancer-labs/v2-solidity-utils/contracts/openzeppelin/EnumerableSet.sol";

// AdminAuthorizer is an implementation of the Authorizer interface
// where only the registered admins are authorized to perform any action.
contract AdminAuthorizer is IAuthorizer {
    using EnumerableSet for EnumerableSet.AddressSet;

    EnumerableSet.AddressSet admins;

    // solhint-disable-next-line const-name-snakecase
    address private constant _EVERYWHERE = address(-1);

    // External permissions
    // actionId -> account -> where -> isGranted
    mapping(bytes32 => mapping(address => mapping(address => bool))) private _isPermissionGranted;

    constructor(address admin) {
        admins.add(admin);
    }

    /** Admins **/

    function isAdmin(address account) public view returns (bool) {
        return admins.contains(account);
    }

    function grantAdmin(address account) public {
        require(isAdmin(msg.sender), "AdminAuthorizer: only admin can add admins");
        admins.add(account);
    }

    function revokeAdmin(address account) public {
        require(isAdmin(msg.sender), "AdminAuthorizer: only admin remove admins");
        admins.remove(account);
    }

    /** Specific permissions  [TimeLockAuthorizer replicant] **/

    function grantPermission(bytes32 actionId, address account, address where) external {
        require(isAdmin(msg.sender), "AdminAuthorizer: only admins can grant permissions");
        _isPermissionGranted[actionId][account][where] = true;
    }

    function revokePermission(bytes32 actionId, address account, address where) external {
        require(isAdmin(msg.sender), "AdminAuthorizer: only admins can revoke permissions");
        _isPermissionGranted[actionId][account][where] = false;
    }

    function hasPermission(bytes32 actionId, address account, address where) public view returns (bool) {
         return _isPermissionGranted[actionId][account][where] || _isPermissionGranted[actionId][account][_EVERYWHERE];
    }

    /** IAuthorizer **/

    function canPerform(bytes32 actionId, address account, address where) external view override returns (bool) {
        return isAdmin(account) || hasPermission(actionId, account, where);
    }
}
