      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.17;
import "openzeppelin/access/Ownable.sol";
import {SafeTransferLib} from "solmate/utils/SafeTransferLib.sol";
import {ReentrancyGuard} from "solmate/utils/ReentrancyGuard.sol";

//import {IERC721} from "openzeppelin/token/ERC721/IERC721.sol";
// import {basePool} from "../basePool/basePoolSTETHNFT.sol";

interface IPool {
    function addNfts(uint256 newMints) external;
}

interface IERC721Mint {
    function mint(uint256 amount, address to) external;
}

contract smolMinting is Ownable, ReentrancyGuard {
    address public smolDollar;
    address public smolFed;
    address public smolBasePool;
    uint256 public mintingBasePrice;
    bool public mintOn;
    /// @notice constant to scale uints into percentages (1e6 == 100%)
    uint256 public constant PERCENTAGE_SCALE = 1e6;
    address artistSafe;
    IERC721Mint smolDollarInterface;
    IPool smolBasePoolInterface;

    constructor() {}

    function initalize(
        address _smolDollar,
        address _smolFed,
        address _smolBasePool,
        uint256 _mintingBasePriceInit,
        address _artistSafe
    ) public onlyOwner {
        smolDollar = _smolDollar;
        smolFed = _smolFed;
        mintingBasePrice = _mintingBasePriceInit;
        artistSafe = _artistSafe;
        smolBasePool = _smolBasePool;
        smolDollarInterface = IERC721Mint(_smolDollar);
        smolBasePoolInterface = IPool(_smolBasePool);
    }

    function turnMint(bool onOf) public onlyOwner {
        mintOn = onOf;
    }

    function setFed(address fed) public onlyOwner {
        smolFed = fed;
    }

    function setSmolDollar(address dollar) public onlyOwner {
        smolDollar = dollar;
        smolDollarInterface = IERC721Mint(dollar);
    }

    function setBasePool(address pool) public onlyOwner {
        smolBasePool = pool;
        smolBasePoolInterface = IPool(pool);
    }

    function mint(address to, uint256 amount) public payable nonReentrant {
        require(mintOn == true, "MINTING_NOT_STARTED");

        (
            uint256 priceAll,
            uint256 artistProfit,
            uint256 reserve
        ) = calculatePrice(amount);

        require(msg.value >= priceAll, "WRONG_VALUE");

        smolDollarInterface.mint(amount, to);
        // (bool suc1, ) = address(smolDollar).call(
        //     abi.encodeWithSignature("mint(uint256,address)", amount, to)
        // );
        // require(suc1 == true, "MINTING_CALL_FAILED");

        smolBasePoolInterface.addNfts(amount);
        // (bool suc2, ) = address(smolBasePool).call(
        //     abi.encodeWithSignature("addNfts(uint256)", amount)
        // );
        // require(suc2 == true, "BASEPOOL_CALL_FAILED");

        //send fee to artistsafe
        SafeTransferLib.safeTransferETH(artistSafe, artistProfit);

        //send backing to smol fed
        SafeTransferLib.safeTransferETH(smolFed, reserve);
    }

    function calculatePrice(
        uint256 amount
    )
        public
        view
        returns (uint256 price, uint256 artistProfit, uint256 reserve)
    {
        artistProfit = calculatedPriceDiscount(amount) * amount;
        reserve = mintingBasePrice * amount;
        price = (artistProfit + reserve);
    }

    function calculatedPriceDiscount(
        uint256 amount
    ) internal view returns (uint256 price) {
        if (amount >= 50) {
            ///50 Mints 75% off of Artist Fee
            price = _scaleAmountByPercentage(mintingBasePrice, 250000);
        } else if (amount >= 25) {
            ///25 Mints 50% off of Artist Fee
            price = _scaleAmountByPercentage(mintingBasePrice, 500000);
        } else if (amount >= 10) {
            ///10 Mints 25% off of Artist Fee
            price = _scaleAmountByPercentage(mintingBasePrice, 750000);
        } else if (amount >= 5) {
            ///5 Mints 10% off of Artist Fee
            price = _scaleAmountByPercentage(mintingBasePrice, 900000);
        } else {
            price = mintingBasePrice;
        }
    }

    ///https://github.com/0xSplits/splits-contracts/blob/main/contracts/SplitMain.sol
    function _scaleAmountByPercentage(
        uint256 amount,
        uint256 scaledPercent
    ) public pure returns (uint256 scaledAmount) {
        // use assembly to bypass checking for overflow & division by 0
        // scaledPercent has been validated to be < PERCENTAGE_SCALE)
        // & PERCENTAGE_SCALE will never be 0
        // pernicious ERC20s may cause overflow, but results do not affect ETH & other ERC20 balances
        assembly {
            /* eg (100 * 2*1e4) / (1e6) */
            scaledAmount := div(mul(amount, scaledPercent), PERCENTAGE_SCALE)
        }
    }
}
"
