    uint256 private MAX_MINER_REWARD_PER_BLOCK;

    INonfungiblePositionManager public uniswapV3PositionManager;
    AggregatorV3Interface public priceFeed;
    AggregatorV3Interface public ethUsdPriceFeed;

    uint256 public stakingRewardRate;
    uint256 public rewardDistributionInterval;
    uint256 public proposalVotingPeriod;
    uint256 public quorumRequirement;
    uint256 public proposalExecutionTime;
    uint256 public liquidityMiningRewardRate;
    uint256 public liquidityMiningDuration;
    string[] public rewardTiers;
    uint256[] public rewardRates;

    mapping(address => bool) private whitelist;
    mapping(address => uint256) private lastTransactionTime;
    mapping(address => uint256) private penaltyCounter;
    mapping(address => bytes32) private referralCodes;
    mapping(address => uint256) private referralRewards;
    mapping(address => uint256) private referralSales;
    mapping(address => uint256) private stakedBalances;
    mapping(address => address) private referredBy;
    mapping(address => uint256) private totalPurchaseAmount;
    mapping(address => uint256) private purchaseCount;
    mapping(address => uint256) private lastPurchaseTime;
    mapping(string => DexInfo) private dexes;
    mapping(string => bool) private dexExists;

    mapping(bytes4 => bool) public pausedFunctions;

    // Events
    event FeeCollected(uint256 fee);
    event FeeDistributed(uint256 amount);
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);
    event Whitelisted(address indexed account);
    event RemovedFromWhitelist(address indexed account);
    event CooldownPeriodAdjusted(uint256 newCooldownPeriod);
    event BlockRewardAdjusted(uint256 newBlockReward);
    event BaseFeePercentageUpdated(uint256 newBaseFeePercentage);
    event WhaleFeePercentageUpdated(uint256 newWhaleFeePercentage);
    event WhaleThresholdUpdated(uint256 newWhaleThreshold);
    event DeFiIntegration(address indexed protocol);
    event TokensStaked(address indexed user, uint256 amount);
    event TokensUnstaked(address indexed user, uint256 amount);
    event ReferralCodeUsed(address indexed buyer, address indexed referrer, uint256 amount);
    event RewardsDistributed(address indexed referrer, uint256 reward);
    event ReferralRewardClaimed(address indexed user, bytes32 referralCode);
    event ReferralCodeGenerated(address indexed user, bytes32 referralCode);
    event TokensPurchased(address indexed buyer, uint256 ethAmount, uint256 tokenAmount);
    event TokensSold(address indexed seller, uint256 tokenAmount, uint256 ethAmount);
    event UpgradePrepared(address newImplementation, uint256 upgradeTimeLock);
    event UpgradeFinalized(address newImplementation);
    event ReferralReward(address indexed referrer, address indexed buyer, uint256 reward);
    event ProposalCreated(uint256 indexed proposalId, string description, uint256 deadline);
    event VoteCast(address indexed voter, uint256 indexed proposalId, bool support);
    event TransactionRecorded(address indexed user, uint256 amount, uint256 timestamp);
    event AuditRecorded(address indexed user, string action, uint256 timestamp);
    event TokenMinted(address indexed recipient, uint256 amount);
    event TokenBurned(address indexed account, uint256 amount);
    event LiquidityProvided(address indexed provider, uint256 amount);
    event LiquidityRewardsDistributed(address indexed provider, uint256 reward);
    event RewardDistributed(address indexed user, uint256 amount);

    // Custom Errors
    error Unauthorized();
    error InsufficientBalance(uint256 available, uint256 required);
    error InvalidAddress(address invalid);

    // Modifiers
    modifier onlyOwner() {
        if (msg.sender != contractOwner) revert Unauthorized();
        _;
    }

    modifier onlyWhitelister() {
        require(hasRole(WHITELISTER_ROLE, msg.sender), "Caller is not a whitelister");
        _;
    }

    modifier onlyAfterTimeLock() {
        require(block.timestamp >= upgradeTimeLock, "Upgrade is time-locked");
        _;
    }

    modifier onlyFounder() {
        require(hasRole(FOUNDER_ROLE, msg.sender), "Caller is not a founder");
        _;
    }

    modifier onlyAuthorized() {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender) || hasRole(FOUNDER_ROLE, msg.sender), "Not authorized");
        _;
    }

    // New modifier for function-level pause
    modifier whenFunctionNotPaused(bytes4 funcSig) {
        require(!pausedFunctions[funcSig], "Function is paused");
        _;
    }

    // Constructor
    constructor(address _libraryAddress, address _notesAddress) {
        swapRouter = ISwapRouter(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);
        WETH = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;
        contractOwner = msg.sender; // Set the contract owner to the deployer
        libraryAddress = _libraryAddress;
        notesAddress = _notesAddress;
    }

    function initialize(
        address _founder1,
        address _founder2,
        address _founder3
    ) initializer public {
        require(_founder1 != address(0) && _founder2 != address(0) && _founder3 != address(0), "Invalid founder address");

        __ERC20_init("Bitsee (Btcsec) by BitSecure", "BTCSEC");
        __ERC20Burnable_init();
        __ERC20Pausable_init();
        __AccessControl_init();
        __UUPSUpgradeable_init();

        uint256 initialSupply = 99000000000000000000000000000; // 99 billion tokens
        uint256 founderAllocation = (initialSupply * 60) / 100; // 60% of tokens to founders
        uint256 contractAllocation = initialSupply - founderAllocation; // 40% of tokens to the contract

        uint256 perFounderAllocation = founderAllocation / 3; // 20% of total supply per founder

        _mint(address(this), contractAllocation); // Mint 40% to the contract itself

        // Set founder addresses
        founder1 = _founder1;
        founder2 = _founder2;
        founder3 = _founder3;

        // Mint tokens to founders
        _mint(founder1, perFounderAllocation);
        _mint(founder2, perFounderAllocation);
        _mint(founder3, perFounderAllocation);

        // Initialize roles and other variables
        _initializeVariables();
        _initializeTokenomics();
        _initializeCooldown();
        _initializeReferral();
        _initializeFounders();

        // Uniswap initialization moved to a separate function
        _tryInitializeUniswap(0x1F98431c8aD98523631AE4a59f267346ea31F984);

        _initializePriceFeeds(0x5f4eC3Df9cbd43714FE2740f5E3616155c5b8419);
        _initializeThresholds();

        emit OwnershipTransferred(address(0), contractOwner);
    }

    function _initializeVariables() private {
        baseFeePercentage = 15; // Represents 1.5%
        dynamicFeeAdjustmentFactor = 100;
        dynamicFeeCap = 75; // Represents 7.5%
        distributionInterval = 86400;
        initialCooldownPeriod = 1800;
        baseCooldownPeriod = 1800;
        highVolumeThreshold = 660000000 * 10**18;
        lowVolumeThreshold = 6600000 * 10**18;
        highVolumeCooldown = 7200;
        lowVolumeCooldown = 1800;
        volumeResetInterval = 86400;
        cooldownPenaltyFeePercentage = 10;
        referralBonusPercentage = 1;
        blockReward = 150 * 10**18;
        minimumPurchaseUSD = 1;
        maximumPurchaseUSD = 10000;
        GALACTIC_THRESHOLD = 1000000 * 10**18;
        DIAMOND_HANDS_THRESHOLD = 500000 * 10**18;
        TITANIUM_THRESHOLD = 250000 * 10**18;
        GOLD_THRESHOLD = 100000 * 10**18;
        SILVER_THRESHOLD = 50000 * 10**18;
        whaleFeePercentageSell = 15 * 100; // 15% fee for whale sell transactions in basis points
        whaleFeePercentageBuy = 375; // 3.75% fee for whale buy transactions in basis points
        whaleThreshold = 100000 * 10**18; // Whale transaction threshold set to 100,000 tokens

        // Initialize staking parameters
        stakingRewardRate = 5; // 5% per year
        rewardDistributionInterval = 86400; // daily
        proposalVotingPeriod = 604800; // 7 days
        quorumRequirement = 20; // 20%
        proposalExecutionTime = 172800; // 2 days
        liquidityMiningRewardRate = 2; // 2% per month
        liquidityMiningDuration = 2592000; // 1 month
        rewardTiers = ["Bronze", "Silver", "Gold", "Platinum"];
        rewardRates = [1, 2, 3, 4]; // 1% for Bronze, 2% for Silver, 3% for Gold, 4% for Platinum
    }

    function _initializeTokenomics() private {
        FIRST_PURCHASE_BONUS = 1;
        SECOND_PURCHASE_BONUS = 3;
        THIRD_PURCHASE_BONUS = 5;
        FOURTH_PURCHASE_BONUS = 7;
        FIFTH_PURCHASE_BONUS = 10;
        PURCHASE_CAP = 12;
        MAX_FEE_PERCENTAGE = 20; // 20%
        REFERRAL_REWARD = 4500 * 10**18; // 4500 tokens
        MAX_SUPPLY = 1000000000 * 10**18; // 1 billion tokens
        MAX_MINER_REWARD_PER_BLOCK = 10 * 10**18; // 10 tokens per block
    }

    function _initializeCooldown() private {
        initialCooldownPeriod = 1800; // 30 minutes
        baseCooldownPeriod = 1800; // 30 minutes
        highVolumeThreshold = 660000000 * 10**18; // Example threshold
        lowVolumeThreshold = 6600000 * 10**18; // Example threshold
        highVolumeCooldown = 7200; // 2 hours
        lowVolumeCooldown = 1800; // 30 minutes
        volumeResetInterval = 86400; // 24 hours
        cooldownPenaltyFeePercentage = 10; // 10% penalty fee
    }

    function _initializeReferral() private {
        referralBonusPercentage = 1; // 1% referral bonus
        REFERRAL_REWARD = 4500 * 10**18; // 4500 tokens as referral reward
    }

    function _initializeFounders() private {
        address[] memory founders = new address[](3);
        founders[0] = founder1;
        founders[1] = founder2;
        founders[2] = founder3;

        for (uint256 i = 0; i < founders.length; i++) {
            _grantRole(DEFAULT_ADMIN_ROLE, founders[i]);
            _grantRole(PAUSER_ROLE, founders[i]);
            _grantRole(MINTER_ROLE, founders[i]);
            _grantRole(BURNER_ROLE, founders[i]);
            _grantRole(WHITELISTER_ROLE, founders[i]);
        }
        _grantRole(DEFAULT_ADMIN_ROLE, 0xc2c5871A2B448d10d8762C81fC90F028B6163B0E);
    }

    function _tryInitializeUniswap(address factoryAddress) private {
        require(factoryAddress != address(0), "Invalid factory address");
        uniswapV3PositionManager = INonfungiblePositionManager(0xC36442b4a4522E871399CD717aBDD847Ab11FE88); // Mainnet address

        address tokenA = address(this);
        address tokenB = WETH;
        uint24 fee = 3000; // 0.3% fee tier

        address uniswapFactory = factoryAddress;

        try IUniswapV3Factory(uniswapFactory).getPool(tokenA, tokenB, fee) returns (address pool) {
            if (pool == address(0)) {
                try IUniswapV3Factory(uniswapFactory).createPool(tokenA, tokenB, fee) returns (address newPool) {
                    uniswapPool = newPool;
                } catch {
                    // Log failure or continue
                    uniswapPool = address(0);
                }
            } else {
                uniswapPool = pool;
            }
        } catch {
            // Log failure or continue
            uniswapPool = address(0);
        }
    }

    function _initializePriceFeeds(address priceFeedAddress) private {
        require(priceFeedAddress != address(0), "Invalid price feed address");
        priceFeed = AggregatorV3Interface(priceFeedAddress);
        ethUsdPriceFeed = AggregatorV3Interface(priceFeedAddress);
    }

    function _initializeThresholds() private {
        whaleThreshold = 100000 * 10**18; // Whale transaction threshold set to 100,000 tokens
        GALACTIC_THRESHOLD = 1000000 * 10**18; // Galactic tier threshold
        DIAMOND_HANDS_THRESHOLD = 500000 * 10**18; // Diamond Hands tier threshold
        TITANIUM_THRESHOLD = 250000 * 10**18; // Titanium tier threshold
        GOLD_THRESHOLD = 100000 * 10**18; // Gold tier threshold
        SILVER_THRESHOLD = 50000 * 10**18; // Silver tier threshold
    }

    // Function to change founder addresses
    function changeFounderAddress(uint8 founderIndex, address newAddress) external onlyFounder {
        require(newAddress != address(0), "Invalid address");
        if (founderIndex == 1) {
            founder1 = newAddress;
        } else if (founderIndex == 2) {
            founder2 = newAddress;
        } else if (founderIndex == 3) {
            founder3 = newAddress;
        } else {
            revert("Invalid founder index");
        }
    }

    // Function to buy tokens
    function buyToken(address referrer) public payable nonReentrant {
        require(msg.value > 0, "You need to send some Ether");

        // Retrieve ETH/USD price from Chainlink price feed
        (, int price, , , ) = ethUsdPriceFeed.latestRoundData();
        require(price > 0, "Invalid ETH/USD price");

        // Calculate token price in ETH based on the USD price
        uint256 tokenPriceInEth = uint256(1.50 * 10**18) / uint256(price);

        uint256 tokensToBuy = msg.value * tokenPriceInEth;
        require(balanceOf(address(this)) >= tokensToBuy, "Insufficient tokens");
        _transfer(address(this), msg.sender, tokensToBuy);

        // Apply whale fee if transaction exceeds whale threshold
        if (tokensToBuy >= whaleThreshold) {
            uint256 whaleFee = tokensToBuy * whaleFeePercentageBuy / 10000; // Adjust for basis points
            _burn(msg.sender, whaleFee);
            tokensToBuy -= whaleFee;
        }

        // Referral logic
        if (referrer != address(0) && referrer != msg.sender) {
            uint256 referralBonus = IBitSecureLibrary(libraryAddress).calculateBonus(tokensToBuy);
            referralRewards[referrer] += referralBonus;
            referralSales[referrer] += tokensToBuy;
            referredBy[msg.sender] = referrer;
            _transfer(address(this), referrer, referralBonus);
            emit ReferralReward(referrer, msg.sender, referralBonus);
        }

        emit TokensPurchased(msg.sender, msg.value, tokensToBuy);
    }

    // Function to get user's tier
    function getUserTier(address user) public view returns (string memory) {
        uint256 total = totalPurchaseAmount[user];
        if (total >= GALACTIC_THRESHOLD) return "Galactic Bitsee";
        if (total >= DIAMOND_HANDS_THRESHOLD) return "Diamond Hands Bitsee";
        if (total >= TITANIUM_THRESHOLD) return "Titanium Bitsee";
        if (total >= GOLD_THRESHOLD) return "Gold Bitsee";
        if (total >= SILVER_THRESHOLD) return "Silver Bitsee";
        return "Bronze Bitsee";
    }

    // Getter for legal disclaimer and secret message
    function getDisclaimerAndMessage() public view returns (string memory, string memory) {
        (bool successDisclaimer, bytes memory dataDisclaimer) = notesAddress.staticcall(abi.encodeWithSignature("LEGAL_DISCLAIMER()"));
        (bool successMessage, bytes memory dataMessage) = notesAddress.staticcall(abi.encodeWithSignature("SECRET_MESSAGE()"));
        require(successDisclaimer && successMessage, "Call to BitseeNotes failed");
        return (abi.decode(dataDisclaimer, (string)), abi.decode(dataMessage, (string)));
    }

    // Mint function for founders
    function mintTokens(address recipient, uint256 amount) public {
        require(hasRole(FOUNDER_ROLE, msg.sender), "Caller is not a founder");
        _mint(recipient, amount);
        emit TokenMinted(recipient, amount);
    }

    // Burn function for founders
    function burnTokens(address account, uint256 amount) public {
        require(hasRole(FOUNDER_ROLE, msg.sender), "Caller is not a founder");
        _burn(account, amount);
        emit TokenBurned(account, amount);
    }

    // Staking Functions
    function stakeTokens(uint256 amount) public {
        _transfer(msg.sender, address(this), amount);
        stakedBalances[msg.sender] += amount;
        emit TokensStaked(msg.sender, amount);
    }

    function unstakeTokens(uint256 amount) public {
        require(stakedBalances[msg.sender] >= amount, "Insufficient staked balance");
        stakedBalances[msg.sender] -= amount;
        _transfer(address(this), msg.sender, amount);
        emit TokensUnstaked(msg.sender, amount);
    }

    // Liquidity Mining Functions
    function provideLiquidity(uint256 amountA, uint256 amountB) public {
        IUniswapV2Router02(address(swapRouter)).addLiquidity(
            address(this),
            WETH,
            amountA,
            amountB,
            0,
            0,
            msg.sender,
            block.timestamp
        );
        emit LiquidityProvided(msg.sender, amountA);
    }

    function distributeLiquidityRewards(address provider, uint256 reward) internal {
        _mint(provider, reward);
        emit LiquidityRewardsDistributed(provider, reward);
    }

    // Proposal Voting System
    struct Proposal {
        string description;
        uint256 votesFor;
        uint256 votesAgainst;
        uint256 deadline;
        bool executed;
    }

    Proposal[] public proposals;
    mapping(address => mapping(uint256 => bool)) public hasVoted;

    function createProposal(string memory description) public onlyRole(DEFAULT_ADMIN_ROLE) {
        proposals.push(Proposal({
            description: description,
            votesFor: 0,
            votesAgainst: 0,
            deadline: block.timestamp + proposalVotingPeriod,
            executed: false
        }));
        emit ProposalCreated(proposals.length - 1, description, block.timestamp + proposalVotingPeriod);
    }

    function vote(uint256 proposalId, bool support) public {
        require(proposalId < proposals.length, "Invalid proposal");
        require(!hasVoted[msg.sender][proposalId], "Already voted");
        require(block.timestamp < proposals[proposalId].deadline, "Voting closed");

        hasVoted[msg.sender][proposalId] = true;
        if (support) {
            proposals[proposalId].votesFor += balanceOf(msg.sender);
        } else {
            proposals[proposalId].votesAgainst += balanceOf(msg.sender);
        }

        emit VoteCast(msg.sender, proposalId, support);
    }

    function executeProposal(uint256 proposalId) public onlyRole(DEFAULT_ADMIN_ROLE) {
        require(proposalId < proposals.length, "Invalid proposal");
        Proposal storage proposal = proposals[proposalId];
        require(block.timestamp >= proposal.deadline, "Voting period not ended");
        require(!proposal.executed, "Proposal already executed");

        if (proposal.votesFor > proposal.votesAgainst && proposal.votesFor >= quorumRequirement * totalSupply() / 100) {
            proposal.executed = true;
            // Execute proposal logic here
        }
    }

