    }

    // Contract upgrade and finalization
    function prepareUpgrade(address newImplementation) external onlyOwner {
        upgradeTimeLock = block.timestamp + 2 days;
        emit UpgradePrepared(newImplementation, upgradeTimeLock);
    }

    function finalizeUpgrade(address newImplementation) external onlyOwner onlyAfterTimeLock {
        _authorizeUpgrade(newImplementation);
        upgradeToAndCall(newImplementation, "");
        emit UpgradeFinalized(newImplementation);
    }

    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {
        // Add any additional upgrade logic here
    }

    function _update(address from, address to, uint256 value) internal override(ERC20Upgradeable, ERC20PausableUpgradeable) {
        super._update(from, to, value);
    }

    function renounceOwnership() public onlyOwner {
        emit OwnershipTransferred(contractOwner, address(0));
        contractOwner = address(0);
    }

    // Function to set bonus percentages
    function setBonusPercentages(uint256 _first, uint256 _second, uint256 _third, uint256 _fourth, uint256 _fifth) public onlyRole(FOUNDER_ROLE) {
        FIRST_PURCHASE_BONUS = _first;
        SECOND_PURCHASE_BONUS = _second;
        THIRD_PURCHASE_BONUS = _third;
        FOURTH_PURCHASE_BONUS = _fourth;
        FIFTH_PURCHASE_BONUS = _fifth;
    }

    // Staff tracking
    mapping(address => uint256) staffSales;
    mapping(address => uint256) staffReferralRewards;

    event StaffSaleRecorded(address indexed staff, uint256 amount);
    event StaffReferralRewardRecorded(address indexed staff, uint256 reward);

    function recordStaffSale(address staff, uint256 amount) external onlyAuthorized {
        staffSales[staff] += amount;
        emit StaffSaleRecorded(staff, amount);
    }

    function recordStaffReferralReward(address staff, uint256 reward) external onlyAuthorized {
        staffReferralRewards[staff] += reward;
        emit StaffReferralRewardRecorded(staff, reward);
    }

    function getStaffSales(address staff) public view returns (uint256) {
        return staffSales[staff];
    }

    function getStaffReferralRewards(address staff) public view returns (uint256) {
        return staffReferralRewards[staff];
    }

    // Withdraw function
    function withdraw(uint256 amount) public {
        if (msg.sender != contractOwner) revert Unauthorized();
        if (balanceOf(msg.sender) < amount) revert InsufficientBalance(balanceOf(msg.sender), amount);
        _transfer(msg.sender, address(this), amount);
    }

    // Function to pay staff
    function payStaff(address staffPayAddress) external onlyFounder {
