pragma solidity ^0.8.17;

import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Permit.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";

/**
 * Reward token contract to be used by the blaze protocol.
 * The entire amount is minted by the main blaze contract 
 * (Blaze.sol - which is the owner of this contract)
 * directly to an account when it claims rewards.
 */
contract BlazeERC20 is ERC20Permit, ERC20Burnable {

    /**
     * The address of the Blaze.sol contract instance.
     */
    address public immutable owner;

    /**
     * Sets the owner address. 
     * Called from within the Blaze.sol constructor.
     */
    constructor() ERC20("Titan Blaze", "BLAZE")
    ERC20Permit("Titan Blaze") {
        owner = _msgSender();
    }

    /**
     * The total supply is naturally capped by the distribution algorithm 
     * implemented by the main blaze contract, however an additional check 
     * that will never be triggered is added to reassure the reader.
     * 
     * @param account the address of the reward token reciever
     * @param amount wei to be minted
     */
    function mintReward(address account, uint256 amount) external {
        require(_msgSender() == owner, "Blaze: caller is not Blaze contract.");
        _mint(account, amount);
    }
}
"
