pragma solidity 0.8.12;

contract Multicall3 {
    constructor() {
        admin = msg.sender;
    }

    struct Call {
        address target;
        bytes callData;
    }

    modifier onlyAdmin() {
        require(msg.sender == admin);
        _;
    }

    address admin;

    function aggregate(Call[] calldata calls)
        public
        payable
        onlyAdmin
    {
        uint256 length = calls.length;
        Call calldata call;
        for (uint256 i = 0; i < length; ) {
            bool success;
            call = calls[i];
            (success, ) = call.target.call{value: msg.value}(call.callData);
            require(success, "Multicall3: call failed");
            unchecked {
                ++i;
            }
        }
    }
}
