pragma solidity ^0.8.20;

import "@chainlink/contracts/src/v0.8/shared/interfaces/AggregatorV3Interface.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract PresaleV2 is
    Initializable,
    ReentrancyGuardUpgradeable,
    OwnableUpgradeable
{
    bool public claimEnabled;
    bool public stakeEnabled;
    bool public refEnabled;

    address public constant eth = 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;
    bool public presaleOpen;

    mapping(address => uint256) public stakedBalances;
    mapping(address => uint256) public lastStakedTime;
    mapping(address => uint256) public balances;
    mapping(address => uint256) public stakingPoints;

    mapping(address => uint256) public referrals;

    event Staked(
        address indexed user,
        uint256 currentAmount,
        uint256 totalUserStakedAmount
    );
    event UnstakedAndClaimed(
        address indexed user,
        uint256 amount,
        uint256 points
    );
    event PresaleOpened();
    event PresaleClosed();
    event PresaleTokenAddressUpdated(address newAddress);
    event TokensClaimed(address claimant, uint256 amount);
    event PaymentTokenAdded(address token, address priceFeed, uint256 decimals);
    event PaymentTokenRemoved(address token);
    event TokenPriceUpdated(uint256 newPrice);
    event ReferralPercentageUpdated(uint256 newPercentage);
    event TreasuryUpdated(address newTreasury);
    event PurchaseMade(
        address token,
        uint256 amount,
        address buyer,
        uint256 tokensIssued,
        address refWallet,
        bool stake
    );
    event ReferralClaimed(address claimant, uint256 amount);
    event WithdrawalMade(address token, uint256 amount, address to);

    event PaymentTokenReferralPercentageSet(uint256 percentage);
    event PaymentTokenReferralRewardClaimed(
        address indexed wallet,
        address indexed token,
        uint256 amount
    );
    event PaymentTokenReferralRewardAdded(
        address indexed _ref,
        address indexed token,
        uint256 amount,
        uint256 inUSD,
        address indexed buyer
    );

    struct PaymentToken {
        address token;
        AggregatorV3Interface priceFeed;
        uint256 decimals;
    }

    address public presaleTokenAddress;
    address public treasury;
    uint256 public tokenPriceUSD;
    uint256 public refPercentage;
    PaymentToken[] public paymentTokens;

    uint256 public totalBoughtTokens;
    uint256 public totalStaked;
    uint256 public totalStakingPoints;
    uint256 public totalReferralTokens;

    // ----  v2 vars -------------------
    uint256 paymentTokenReferralPercentage;

    mapping(address => mapping(address => uint256))
        public paymentTokenReferralRewards; // paymentTokenReferralRewards[wallet][token]; token can any of the payment tokens user used to buy

    mapping(address => uint256) public totalRewardsByPaymentToken;
    mapping(address => address) public originalReferrer;

    mapping(address => address[]) referrerToBuyers;
    mapping(address => uint256) public earnedInUSD;

    // ----------------------------------

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize() public initializer {
        __Ownable_init_unchained();
        __ReentrancyGuard_init();
    }

    function setup(
        uint256 _tokenPriceUSD,
        address _treasury,
        address _presaleTokenAddress,
        bool _claimEnabled,
        bool _stakeEnabled,
        bool _refEnabled
    ) external onlyOwner {
        presaleOpen = true;
        refPercentage = 8;
        totalBoughtTokens = 0;
        totalStaked = 0;
        totalStakingPoints = 0;
        totalReferralTokens = 0;

        paymentTokenReferralPercentage = 22;

        tokenPriceUSD = _tokenPriceUSD;
        treasury = _treasury;
        presaleTokenAddress = _presaleTokenAddress;
        claimEnabled = _claimEnabled;
        stakeEnabled = _stakeEnabled;
        refEnabled = _refEnabled;
    }

    function openPresale() external onlyOwner {
        require(!presaleOpen, "Presale is open");
        presaleOpen = true;
        emit PresaleOpened();
    }

    function closePresale() external onlyOwner {
        require(presaleOpen, "Presale is closed");
        presaleOpen = false;
        emit PresaleClosed();
    }

    function toggleClaimEnabled() public onlyOwner {
        claimEnabled = !claimEnabled;
    }

    function toggleStakeEnabled() public onlyOwner {
        stakeEnabled = !stakeEnabled;
    }

    function toggleRefEnabled() public onlyOwner {
        refEnabled = !refEnabled;
    }

    function setPresaleTokenAddress(address _newAddress) external onlyOwner {
        require(_newAddress != address(0), "Invalid address");
        presaleTokenAddress = _newAddress;
        emit PresaleTokenAddressUpdated(_newAddress);
    }

    function removePaymentToken(address _token) external onlyOwner {
        int256 index = -1;
        for (uint i = 0; i < paymentTokens.length; i++) {
            if (paymentTokens[i].token == _token) {
                index = int256(i);
                break;
            }
        }

        require(index >= 0, "Token not found");

        paymentTokens[uint256(index)] = paymentTokens[paymentTokens.length - 1];
        paymentTokens.pop();

        emit PaymentTokenRemoved(_token);
    }

    function setTokenPriceUSD(uint256 _newPrice) external onlyOwner {
        tokenPriceUSD = _newPrice;
        emit TokenPriceUpdated(_newPrice);
    }

    function setRefPercentage(uint256 _newPercentage) external onlyOwner {
        require(_newPercentage <= 100, "Percentage too high");
        refPercentage = _newPercentage;
        emit ReferralPercentageUpdated(_newPercentage);
    }

    function setTreasury(address _newTreasury) external onlyOwner {
        require(_newTreasury != address(0), "Invalid address");
        treasury = _newTreasury;
        emit TreasuryUpdated(_newTreasury);
    }

    function addPaymentToken(
        address _token,
        address _priceFeed,
        uint256 _decimals
    ) external onlyOwner {
        require(_token != address(0), "Invalid token address");
        require(_priceFeed != address(0), "Invalid price feed address");

        for (uint i = 0; i < paymentTokens.length; i++) {
            require(paymentTokens[i].token != _token, "Token already added");
        }
        paymentTokens.push(
            PaymentToken({
                token: _token,
                priceFeed: AggregatorV3Interface(_priceFeed),
                decimals: _decimals
            })
        );

        emit PaymentTokenAdded(_token, _priceFeed, _decimals);
    }

    function calculateTokens(
        address _token,
        uint256 _amount
    ) external view returns (uint256 tokens) {
        require(_token != address(0), "Invalid _token address");
        require(_amount > 0, "Amount should be > 0");

        AggregatorV3Interface priceFeed;
        uint256 decimals;
        bool found = false;
        for (uint i = 0; i < paymentTokens.length; i++) {
            if (paymentTokens[i].token == _token) {
                priceFeed = paymentTokens[i].priceFeed;
                decimals = paymentTokens[i].decimals;
                found = true;
                break;
            }
        }
        require(found, "Payment token not supported");

        uint256 amountInUSD = convertPaymentTokenToUsd(
            _amount,
            priceFeed,
            decimals
        );
        tokens = amountInUSD / tokenPriceUSD;

        return tokens;
    }

    function claimReferralBalance(address wallet) external nonReentrant {
        require(claimEnabled, "claim is not enabled");
        require(refEnabled, "referral is not enabled");

        uint256 amount = referrals[wallet];
        require(amount > 0, "No tokens to claim");
        referrals[wallet] = 0;
        balances[wallet] = amount;

        emit ReferralClaimed(wallet, amount);
    }

    function getReferralTokensToBeClaimed(
        address wallet
    ) external view returns (uint256) {
        return referrals[wallet];
    }

    function convertPaymentTokenToUsd(
        uint256 _paymentTokenAmount,
        AggregatorV3Interface priceFeed,
        uint256 _decimals
    ) public view returns (uint256) {
        (, int price, , , ) = priceFeed.latestRoundData();
        require(price > 0, "Invalid price data");
        return (_paymentTokenAmount * uint256(price)) / 1e2 / (10 ** _decimals);
    }

    function getUserBalance(address _user) public view returns (uint256) {
        return balances[_user];
    }

    function claimTokens() external nonReentrant {
        require(claimEnabled, "claim is not enabled on this chain");
        require(!presaleOpen, "Presale must be closed to claim tokens");
        uint256 tokensToClaim = balances[msg.sender];
        require(tokensToClaim > 0, "No tokens to claim");
        balances[msg.sender] = 0;

        require(
            IERC20(presaleTokenAddress).transfer(msg.sender, tokensToClaim),
            "Token transfer failed"
        );

        emit TokensClaimed(msg.sender, tokensToClaim);
    }

    function withdrawETH(uint256 _amount) external onlyOwner {
        require(_amount <= address(this).balance, "Insufficient balance");
        payable(treasury).transfer(_amount);

        emit WithdrawalMade(eth, _amount, treasury);
    }

    function withdrawToken(address _token, uint256 _amount) external onlyOwner {
        require(
            IERC20(_token).balanceOf(address(this)) >= _amount,
            "Insufficient token balance"
        );
        IERC20(_token).transfer(treasury, _amount);

        emit WithdrawalMade(_token, _amount, treasury);
    }

    function stake(uint256 _amount) external {
        _stake(msg.sender, _amount);
    }

    function _stake(address user, uint256 amount) internal {
        require(stakeEnabled, "Staking not enabled");
        require(amount > 0, "Cannot stake 0");
        require(balances[user] >= amount, "Insufficient balance to stake");

        uint256 claimedAmount = _unstakeAndClaimPoints(user);
        uint256 totalAmountToStake = claimedAmount + amount;

        balances[user] -= totalAmountToStake;
        stakedBalances[user] += totalAmountToStake;
        totalStaked += totalAmountToStake;
        lastStakedTime[user] = block.timestamp;

        emit Staked(user, amount, stakedBalances[user]);
    }

    function unstakeAndClaimPoints() external nonReentrant returns (uint256) {
        return _unstakeAndClaimPoints(msg.sender);
    }

    function _unstakeAndClaimPoints(address user) internal returns (uint256) {
        require(stakeEnabled, "Staking not enabled");
        require(stakedBalances[user] >= 0, "zero staked balance");

        uint256 amount = stakedBalances[user];
        uint256 period = block.timestamp - lastStakedTime[user];
        uint256 currentStakingPoints = period * amount;

        stakedBalances[user] = 0;
        totalStaked -= amount;
        balances[user] += amount;

        stakingPoints[user] += currentStakingPoints;
        totalStakingPoints += currentStakingPoints;

        emit UnstakedAndClaimed(user, amount, currentStakingPoints);

        return amount;
    }

    function getUserTotalStakingPoints(
        address _user
    ) public view returns (uint256) {
        uint256 amount = stakedBalances[_user];
        uint256 period = block.timestamp - lastStakedTime[_user];
        uint256 currentStakingPoints = period * amount;

        return currentStakingPoints + stakingPoints[_user];
    }

    function buy(
        address _token,
        uint256 _amount,
        address _onBehalfOf,
        address _refWallet,
        bool _stakeIt
    ) public payable nonReentrant returns (uint256) {
        require(presaleOpen, "presale is closed");
        require(_token != address(0), "Invalid _token address");
        require(_onBehalfOf != address(0), "Invalid onBehalfOf address");
        require(_amount > 0, "Amount should be more than 0");
        require(_refWallet != address(0), "Invalid refWallet address");
        require(_refWallet != _onBehalfOf, "buyer can not refer themself");

        if (originalReferrer[_onBehalfOf] == address(0)) {
            originalReferrer[_onBehalfOf] = _refWallet;
            referrerToBuyers[_refWallet].push(_onBehalfOf);
        } else {
            _refWallet = originalReferrer[_onBehalfOf];
        }

        AggregatorV3Interface priceFeed;
        bool found = false;
        uint256 decimals;
        for (uint i = 0; i < paymentTokens.length; i++) {
            if (paymentTokens[i].token == _token) {
                priceFeed = paymentTokens[i].priceFeed;
                decimals = paymentTokens[i].decimals;
                found = true;
                break;
            }
        }
        require(found, "Payment token not supported");

        uint256 amountInUSD = convertPaymentTokenToUsd(
            _amount,
            priceFeed,
            decimals
        );

        uint256 refEarnedinUSD = (paymentTokenReferralPercentage *
            amountInUSD) / 100;

        uint256 paymentTokenReferralAmount = (paymentTokenReferralPercentage *
            _amount) / 100;

        uint256 forTreasury = _amount - paymentTokenReferralAmount;

        if (_token == eth) {
            (bool sent, ) = treasury.call{value: forTreasury}("");
            require(sent, "Failed to send Ether");
        } else {
            SafeERC20.safeTransferFrom(
                IERC20(_token),
                msg.sender,
                treasury,
                forTreasury
            );
            SafeERC20.safeTransferFrom(
                IERC20(_token),
                msg.sender,
                address(this),
                paymentTokenReferralAmount
            );
        }

        uint256 tokens = (amountInUSD * (10 ** 18)) / tokenPriceUSD;
        balances[_onBehalfOf] += tokens;
        totalBoughtTokens += tokens;

        uint256 refAmount = (tokens * refPercentage) / 100;
        referrals[_refWallet] += refAmount;
        totalReferralTokens += refAmount;

        earnedInUSD[_refWallet] += refEarnedinUSD;

        if (_stakeIt == true) {
            _stake(_onBehalfOf, tokens);
        }

        emit PurchaseMade(
            _token,
            _amount,
            _onBehalfOf,
            tokens,
            _refWallet,
            _stakeIt
        );

        paymentTokenReferralRewards[_refWallet][
            _token
        ] += paymentTokenReferralAmount;

        totalRewardsByPaymentToken[_token] += paymentTokenReferralAmount;

        emit PaymentTokenReferralRewardAdded(
            _refWallet,
            _token,
            paymentTokenReferralAmount,
            refEarnedinUSD,
            _onBehalfOf
        );

        return tokens;
    }

    receive() external payable {}

    // ------ v2 methods ----- //

    function setPaymentTokenReferralPercentage(
        uint256 _percentage
    ) public onlyOwner {
        paymentTokenReferralPercentage = _percentage;

        emit PaymentTokenReferralPercentageSet(_percentage);
    }

    // Function to get the paymentTokenReferralReward for a specific wallet and token
    function getPaymentTokenReferralReward(
        address wallet,
        address token
    ) public view returns (uint256) {
        return paymentTokenReferralRewards[wallet][token];
    }

    function _claimPaymentTokenReward(
        address _wallet,
        address _token
    ) internal returns (bool) {
        uint256 rewardAmount = paymentTokenReferralRewards[_wallet][_token];
        if (rewardAmount == 0) {
            return false;
        }

        paymentTokenReferralRewards[_wallet][_token] = 0;

        if (_token == eth) {
            (bool sent, ) = _wallet.call{value: rewardAmount}("");
            require(sent, "Failed to send Ether");
        } else {
            SafeERC20.safeTransfer(IERC20(_token), _wallet, rewardAmount);
        }
        emit PaymentTokenReferralRewardClaimed(_wallet, _token, rewardAmount);
        return true;
    }

    // Function to claim the referral reward
    function claimPaymentTokenReferralReward(
        address _wallet,
        address _token
    ) public nonReentrant {
        _claimPaymentTokenReward(_wallet, _token);
    }

    // New function to claim all referral rewards for all tokens
    function claimAllPaymentTokenReferralRewards(
        address _wallet
    ) public nonReentrant {
        for (uint256 i = 0; i < paymentTokens.length; i++) {
            address tokenAddress = paymentTokens[i].token;
            if (paymentTokenReferralRewards[_wallet][tokenAddress] > 0) {
                _claimPaymentTokenReward(_wallet, tokenAddress);
            }
        }
    }

    function getPaymentTokenReferralPercentage() public view returns (uint256) {
        return paymentTokenReferralPercentage;
    }

    function getReferrerToBuyers(
        address _refWallet
    ) external view returns (address[] memory) {
        return referrerToBuyers[_refWallet];
    }

    function getEarnedInUSD(address referrer) public view returns (uint256) {
        return earnedInUSD[referrer];
    }

    function getOriginalReferrer(
        address _onBehalfOf
    ) public view returns (address) {
        return originalReferrer[_onBehalfOf];
    }

    function getTotalRewardsByPaymentToken(
        address _token
    ) public view returns (uint256) {
        return totalRewardsByPaymentToken[_token];
    }

    function getPaymentTokens() public view returns (PaymentToken[] memory) {
        return paymentTokens;
    }
