      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.25;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/proxy/transparent/TransparentUpgradeableProxy.sol";

contract MAGAAClaim is Initializable, OwnableUpgradeable {
    struct Claim {
        address beneficiary;
        uint256 amount;
    }

    IERC20 public token;
    Claim[] public claims;
    uint256 public amountInClaims;

    event TokenSet(IERC20 token);
    event ClaimAdded(uint256 indexed claimId, address indexed beneficiary);
    event ClaimCanceled(uint256 indexed claimId, address indexed beneficiary);
    event ClaimWithdraw(uint256 indexed claimId, address indexed beneficiary, uint256 indexed amount);

    function initialize() public initializer {
        __Ownable_init(msg.sender);
    }

    function setToken(IERC20 _token) external onlyOwner {
        require(address(token) == address(0), "Token is already set");
        token = _token;
        emit TokenSet(token);
    }

    function createClaimBulk(
        address[] calldata _beneficiary,
        uint256[] calldata _amount
    ) external onlyOwner {
        require(
            _beneficiary.length == _amount.length,
            "Parameters length mismatch"
        );

        for (uint256 i = 0; i < _beneficiary.length; i++) {
            _createClaim(_beneficiary[i], _amount[i]);
        }
    }

    function createClaim(address _beneficiary, uint256 _amount)
        external
        onlyOwner
        returns (uint256 claimId)
    {
        return _createClaim(_beneficiary, _amount);
    }

    function _createClaim(address _beneficiary, uint256 _amount)
        internal
        returns (uint256 claimId)
    {
        require(
            _beneficiary != address(0),
            "Cannot create claim for zero address"
        );

        amountInClaims = amountInClaims + _amount;

        claimId = claims.length;
        claims.push(
            Claim({beneficiary: _beneficiary, amount: _amount})
        );

        emit ClaimAdded(claimId, _beneficiary);
    }

    function cancelClaim(uint256 _claimId) external onlyOwner {
        Claim memory claim = getClaim(_claimId);

        require(claim.amount != 0, "Claim is already 0");

        claims[_claimId].amount = 0; 

        amountInClaims = amountInClaims - claim.amount;

        emit ClaimCanceled(_claimId, claim.beneficiary);
    }

    function withdrawFromClaim(uint256 _claimId) external {
        Claim memory claim = getClaim(_claimId);

        require(claim.beneficiary == msg.sender, "Only beneficiary can claim");
        require(claim.amount != 0, "Already claimed");

        require(getTokensAvailable() >= claim.amount, "Not enough tokens");

        amountInClaims = amountInClaims - claim.amount;

        claims[_claimId].amount = 0;

        token.transfer(claim.beneficiary, claim.amount);

        emit ClaimWithdraw(_claimId, claim.beneficiary, claim.amount);
    }

    function getClaim(uint256 _claimId) internal view returns (Claim memory) {
        require(_claimId < claims.length, "No claim with such id");
        return claims[_claimId];
    }

    function withdrawExcessiveTokens() external onlyOwner {
        token.transfer(owner(), getTokensAvailable());
    }

    function withdrawAllTokens() external onlyOwner {
        token.transfer(owner(), token.balanceOf(address(this)));
    }

    function getTokensAvailable() public view returns (uint256) {
        return token.balanceOf(address(this)) - amountInClaims;
    }

    function getClaimById(uint256 _claimId)
        public
        view
        returns (
            address beneficiary,
            uint256 amount
        )
    {
        Claim memory claim = getClaim(_claimId);
        beneficiary = claim.beneficiary;
        amount = claim.amount;
    }

    function getClaimsCount() public view returns (uint256 _claimsCount) {
        return claims.length;
    }
}
