interface IDividendDistributor {
    function setDistributionCriteria(uint256 _minPeriod, uint256 _minDistribution) external;
    function setShare(address shareholder, uint256 amount) external;
    function addShare(address shareholder, uint256 amount) external;
    function subShare(address shareholder, uint256 amount) external;
    function shares(address shareholder) external returns (uint256 amount, uint256 totalExcluded, uint256 totalRealised);
    function deposit() external payable;
}

contract HOLDPAD is IERC20, Ownable {
    using SafeMath for uint256;

    address DEAD = 0x000000000000000000000000000000000000dEaD;
    address ZERO = 0x0000000000000000000000000000000000000000;
    address DEAD_NON_CHECKSUM = 0x000000000000000000000000000000000000dEaD;

    string constant _name = "HOLDPAD";
    string constant _symbol = "HPAD";
    uint8 constant _decimals = 18;

    uint256 _totalSupply = 100_000_000 * (10 ** _decimals);

    mapping (address => uint256) _balances;
    mapping (address => mapping (address => uint256)) _allowances;

    mapping (address => bool) isDividendExempt;

    IDEXRouter public router;
    address public pair;

    IDividendDistributor distributor;
    address public distributorAddress;
    address public treasuryAddress;

    uint256 distributorGas = 500000;

    constructor (address _treasuryAddress, address _distributor) {
		address _router  = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D;
        router = IDEXRouter(_router);
        pair = IDEXFactory(router.factory()).createPair(address(this), router.WETH());
        
        distributor = IDividendDistributor(_distributor);
        distributorAddress = address(distributor);
        treasuryAddress = _treasuryAddress;

		isDividendExempt[pair] = true;
        isDividendExempt[address(this)] = true;
        isDividendExempt[DEAD] = true;
        
        _balances[msg.sender] = _totalSupply;
        emit Transfer(address(0), msg.sender, _totalSupply);
    }

    receive() external payable { }

    function totalSupply() external view override returns (uint256) { return _totalSupply; }
    function decimals() external pure override returns (uint8) { return _decimals; }
    function symbol() external pure override returns (string memory) { return _symbol; }
    function name() external pure override returns (string memory) { return _name; }
    function getOwner() external view override returns (address) { return owner; }
    function balanceOf(address account) public view override returns (uint256) { return _balances[account]; }
    function allowance(address holder, address spender) external view override returns (uint256) { return _allowances[holder][spender]; }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _allowances[msg.sender][spender] = amount;
        emit Approval(msg.sender, spender, amount);
        return true;
    }

    function transfer(address recipient, uint256 amount) external override returns (bool) {
        return _transferFrom(msg.sender, recipient, amount);
    }

    function transferFrom(address sender, address recipient, uint256 amount) external override returns (bool) {
        if(_allowances[sender][msg.sender] != _totalSupply){
            _allowances[sender][msg.sender] = _allowances[sender][msg.sender].sub(amount, "Insufficient Allowance");
        }

        return _transferFrom(sender, recipient, amount);
    }

    function _transferFrom(address sender, address recipient, uint256 amount) internal returns (bool) {
        _balances[sender] = _balances[sender].sub(amount, "Insufficient Balance");
        _balances[recipient] = _balances[recipient].add(amount);

        if(!isDividendExempt[sender]){ 
			try distributor.subShare(sender, amount) {} catch {} 
		} else {
			try distributor.subShare(treasuryAddress, amount) {} catch {} 
		}
		
        if(!isDividendExempt[recipient]){ 
			try distributor.addShare(recipient, amount) {} catch {} 
		} else {
			try distributor.addShare(treasuryAddress, amount) {} catch {} 
		}
		
        emit Transfer(sender, recipient, amount);
        return true;
    }

    function setIsDividendExempt(address holder, bool exempt) external onlyOwner {
        require(holder != address(this) && holder != pair);
        isDividendExempt[holder] = exempt;
        if(exempt){
            distributor.setShare(holder, 0);
        }else{
            distributor.setShare(holder, _balances[holder]);
        }
    }

    function setTreasuryAddress(address _treasuryAddress) external onlyOwner {
        (uint256 amount, ,) = distributor.shares(treasuryAddress);
		try distributor.subShare(treasuryAddress, amount) {} catch {}
		
		treasuryAddress = _treasuryAddress;
		
		try distributor.addShare(treasuryAddress, amount) {} catch {} 
    }

    function setDistributorAddress(address _distributor) external onlyOwner {
        distributor = IDividendDistributor(_distributor);
    }

    function getCirculatingSupply() public view returns (uint256) {
        return _totalSupply.sub(balanceOf(DEAD)).sub(balanceOf(ZERO));
    }
	
	function clearStuckBalance() external onlyOwner {
        uint256 amount = address(this).balance;
        payable(msg.sender).transfer(amount);
    }
	
	function clearStuckToken(address TokenAddress, uint256 amount) external onlyOwner {
        IERC20(TokenAddress).transfer(msg.sender, _getTokenAmount(TokenAddress, amount));
    }
	
	function _getTokenAmount(address _tokenAddress, uint256 _amount) internal view returns (uint256 quotient) {
		uint _weiDecimal = 18;
		
		IERC20 tokenAddress = IERC20(_tokenAddress);
		uint256 tokenDecimal = tokenAddress.decimals();
		uint256 decimalDiff = 0;
		uint256 decimalDiffConverter = 0;
		uint256 amount = 0;
			
		if(_weiDecimal != tokenDecimal){
			if(_weiDecimal > tokenDecimal){
				decimalDiff = _weiDecimal - tokenDecimal;
				decimalDiffConverter = 10**decimalDiff;
				amount = _amount.div(decimalDiffConverter);
			} else {
				decimalDiff = tokenDecimal - _weiDecimal;
				decimalDiffConverter = 10**decimalDiff;
				amount = _amount.mul(decimalDiffConverter);
			}		
		} else {
			amount = _amount;
		}
		
		uint256 _quotient = amount;
		
		return (_quotient);
    }
}
