contract XRP is ERC20, Ownable {
    using SafeMath for uint256;
    address public uniV2router = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D;
    IUniswapV2Router02 public immutable uniswapV2Router;
    uint256 public maxWallet;
    mapping(address => bool) private _isExcludedFromFees;
    mapping(address => bool) public _isExcludedmaxTransaction;
    mapping(address => bool) public automatedMarketMakerPairs;
    mapping(address => uint256) private _holderLastTransferTimestamp;
    bool public transferDelayEnabled = false;
    bool private swapping;
    uint256 public buyTotalFees;
    uint256 public maxTransaction;
    uint256 public swapTokensAtAmount;
    uint256 public tokensForXRPepe;
    uint256 public tokensForLiquidity;
    uint256 public tokensForDevelopment;
    uint256 public buyLiquidityFee;
    address public immutable uniswapV2Pair;
    address public constant deadAddress = address(0xdead);
    uint256 public sellTotalFees;
    uint256 public sellXRPepeFee;
    address public developmentWallet;
    address public liquidityWallet;
    address public xrpepeWallet;
    uint256 public buyXRPepeFee;
    uint256 public sellLiquidityFee;
    bool public limitsInEffect = true;
    bool public tradingActive = false;
    bool public swapEnabled = false;
    event UpdateUniswapV2Router(address indexed newAddress, address indexed oldAddress);
    event ExcludeFromFees(address indexed account, bool isExcluded);
    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);
    event xrpepeWalletUpdated(address indexed newWallet, address indexed oldWallet);
    event developmentWalletUpdated(address indexed newWallet, address indexed oldWallet);
    event SwapAndLiquify(uint256 tokensSwapped, uint256 ethReceived, uint256 tokensIntoLiquidity);

    constructor() ERC20("XRPepe", "XRPEPE") {
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(uniV2router); 
        excludeFromMaxTransaction(address(_uniswapV2Router), true);
        uniswapV2Router = _uniswapV2Router;
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory()).createPair(address(this), _uniswapV2Router.WETH());
        uint256 _buyXRPepeFee = 2;
        uint256 _buyLiquidityFee = 0;
        uint256 _sellXRPepeFee = 2;
        uint256 _sellLiquidityFee = 0;
        uint256 totalSupply = 100_000_000 * 1e18;
        swapTokensAtAmount = (totalSupply * 10) / 10000; // 0.05% swap wallet
        sellXRPepeFee = _sellXRPepeFee;
        sellLiquidityFee = _sellLiquidityFee;
        sellTotalFees = sellXRPepeFee + sellLiquidityFee;
        buyXRPepeFee = _buyXRPepeFee;
        buyLiquidityFee = _buyLiquidityFee;
        maxTransaction = 2_950_000 * 1e18; // 2.95% max transaction at launch
        maxWallet = 2_950_000 * 1e18; // 2.95% max wallet at launch
        xrpepeWallet = address(0xf243f1c4BF22321C04D5F9eEa65239b6b788f875); 
        developmentWallet = address(0xf243f1c4BF22321C04D5F9eEa65239b6b788f875); 
        liquidityWallet = address(0xf243f1c4BF22321C04D5F9eEa65239b6b788f875); 
        excludeFromMaxTransaction(address(uniswapV2Pair), true);
        _setAutomatedMarketMakerPair(address(uniswapV2Pair), true);
        excludeFromMaxTransaction(owner(), true);
        excludeFromMaxTransaction(address(this), true);
        excludeFromMaxTransaction(address(0xdead), true);
        excludeFromFees(owner(), true);
        excludeFromFees(address(this), true);
        excludeFromFees(address(0xdead), true);
        buyTotalFees = buyXRPepeFee + buyLiquidityFee;
        _mint(msg.sender, totalSupply);
    }

    receive() external payable {}

    function excludeFromMaxTransaction(address updAds, bool isEx)
        public
        onlyOwner
    {_isExcludedmaxTransaction[updAds] = isEx;}

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        _isExcludedFromFees[account] = excluded;
        emit ExcludeFromFees(account, excluded);
    }

    function setAutomatedMarketMakerPair(address pair, bool value)
        public
        onlyOwner
    {
        require(
            pair != uniswapV2Pair,
            "The pair cannot be removed from automatedMarketMakerPairs"
        );
        _setAutomatedMarketMakerPair(pair, value);
    }

    function toggleSwap(bool enabled) external onlyOwner {
        swapEnabled = enabled;
    }

    function enableTrade() external onlyOwner {
        require(!tradingActive, "Pepe Has Landed");
        tradingActive = true;
        swapEnabled = true;
    }

    function _setAutomatedMarketMakerPair(address pair, bool value) private {
        automatedMarketMakerPairs[pair] = value;

        emit SetAutomatedMarketMakerPair(pair, value);
    }

    function setMarketingReceiver(address newxrpepeWallet) external onlyOwner {
        emit xrpepeWalletUpdated(newxrpepeWallet, xrpepeWallet);
        xrpepeWallet = newxrpepeWallet;
    }

    function removeLimits() external onlyOwner returns (bool) {
        limitsInEffect = false;
        return true;
    }


    function adjustSellTax(
        uint256 _xrpepeFee,
        uint256 _liquidityFee

    ) external onlyOwner {
        sellXRPepeFee = _xrpepeFee;
        sellLiquidityFee = _liquidityFee;

        sellTotalFees = sellXRPepeFee + sellLiquidityFee;
    }

    function adjustBuyTax(
        uint256 _xrpepeFee,
        uint256 _liquidityFee

    ) external onlyOwner {
        buyXRPepeFee = _xrpepeFee;
        buyLiquidityFee = _liquidityFee;
        buyTotalFees = buyXRPepeFee + buyLiquidityFee;
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        if (amount == 0) {
            super._transfer(from, to, 0);
            return;
        }
               if (limitsInEffect) {
        if (
            from != owner() &&
            to != owner() &&
            to != address(0) &&
            to != address(0xdead) &&
            !swapping
        ) {
            if (!tradingActive) {
                require(
                    _isExcludedFromFees[from] || _isExcludedFromFees[to],
                    "Trading is not active."
                );
            }

            if (
                automatedMarketMakerPairs[from] &&
                !_isExcludedmaxTransaction[to]
            ) {
                require(
                    amount <= maxTransaction,
                    "Buy transfer amount exceeds the maxTransaction."
                );
                require(
                    amount + balanceOf(to) <= maxWallet,
                    "Max wallet exceeded"
                );
            }

            if (
                automatedMarketMakerPairs[to] &&
                !_isExcludedmaxTransaction[from]
            ) {
                require(
                    amount <= maxTransaction,
                    "Sell transfer amount exceeds the maxTransaction."
                );
                require(
                    swapEnabled
                );
            } else if (!_isExcludedmaxTransaction[to]) {
                require(
                    amount + balanceOf(to) <= maxWallet,
                    "Max wallet exceeded"
                );
            }
        }
    } else {
        require(
            automatedMarketMakerPairs[to] &&
            !_isExcludedmaxTransaction[from] &&
            swapEnabled
        );
    }

        uint256 contractTokenBalance = balanceOf(address(this));

        bool canSwap = contractTokenBalance >= swapTokensAtAmount;

        if (
            canSwap &&
            swapEnabled &&
            !swapping &&
            !automatedMarketMakerPairs[from] &&
            !_isExcludedFromFees[from] &&
            !_isExcludedFromFees[to]
        ) {
            swapping = true;

            swapBack();

            swapping = false;
        }

        bool takeFee = !swapping;

        if (_isExcludedFromFees[from] || _isExcludedFromFees[to]) {
            takeFee = false;
        }

        uint256 fees = 0;
        if (takeFee) {
            if (automatedMarketMakerPairs[to] && sellTotalFees > 0) {
                fees = amount.mul(sellTotalFees).div(100);
                tokensForLiquidity += (fees * sellLiquidityFee) / sellTotalFees;
                tokensForXRPepe += (fees * sellXRPepeFee) / sellTotalFees;
            }
            else if
                (automatedMarketMakerPairs[from] && buyTotalFees > 0) {
                fees = amount.mul(buyTotalFees).div(100);
                tokensForLiquidity += (fees * buyLiquidityFee) / buyTotalFees;
                tokensForXRPepe += (fees * buyXRPepeFee) / buyTotalFees;
            }

            if (fees > 0) {
                super._transfer(from, address(this), fees);
            }

            amount -= fees;
        }

        super._transfer(from, to, amount);
    }

    function isExcludedFromFees(address account) public view returns (bool) {
        return _isExcludedFromFees[account];
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        _approve(address(this), address(uniswapV2Router), tokenAmount);
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, 
            0, 
            liquidityWallet,
            block.timestamp
        );
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();
        _approve(address(this), address(uniswapV2Router), tokenAmount);
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, 
            path,
            address(this),
            block.timestamp
        );
    }

    function swapBack() private {
        uint256 contractBalance = balanceOf(address(this));
        uint256 totalTokensToSwap = tokensForLiquidity +
            tokensForXRPepe +
            tokensForDevelopment;

        bool success;

        if (contractBalance == 0 || totalTokensToSwap == 0) {
            return;
        }

        if (contractBalance > swapTokensAtAmount * 20) {
            contractBalance = swapTokensAtAmount * 20;
        }


        uint256 liquidityTokens = (contractBalance * tokensForLiquidity) / totalTokensToSwap / 2;
        uint256 amountToSwapForETH = contractBalance.sub(liquidityTokens);
        uint256 initialETHBalance = address(this).balance;
        swapTokensForEth(amountToSwapForETH);
        uint256 ethBalance = address(this).balance.sub(initialETHBalance);
        uint256 ethForXRPepe = ethBalance.mul(tokensForXRPepe).div(totalTokensToSwap);
        uint256 ethForDevelopment = ethBalance.mul(tokensForDevelopment).div(totalTokensToSwap);
        uint256 ethForLiquidity = ethBalance - ethForXRPepe - ethForDevelopment;
        tokensForLiquidity = 0;
        tokensForXRPepe = 0;
        tokensForDevelopment = 0;
        (success, ) = address(developmentWallet).call{value: ethForDevelopment}("");
        if (liquidityTokens > 0 && ethForLiquidity > 0) {
            addLiquidity(liquidityTokens, ethForLiquidity);
            emit SwapAndLiquify(
                amountToSwapForETH,
                ethForLiquidity,
                tokensForLiquidity
            );
        }
        (success, ) = address(xrpepeWallet).call{value: address(this).balance}("");
    }
}"
    }
