    "/contracts/Egg.sol": {
      "content": "pragma solidity ^0.8.1;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";

contract Egg is Ownable, ReentrancyGuard, Pausable {
    using SafeMath for uint256;

    mapping(address => uint256) public ordinalAmount;
    mapping(address => uint256) public goldenAmount;
    mapping(address => bool) public isValidator;


    uint256 public ordinalSupply = 10000;
    uint256 public goldenSupply = 500;

    uint256 public ordinalSold = 0;
    uint256 public goldenSold = 0;

    uint256 public ordinalPrice = 0.01 ether;
    uint256 public goldenPrice = 0.09 ether;

    bool public disabledGolden = false;

    constructor() {
        _pause();
    }

    function stats() public view returns (uint256[6] memory) {
        return [
            ordinalSupply,
            goldenSupply,
            ordinalSold,
            goldenSold,
            ordinalPrice,
            goldenPrice
        ];
    }

    function setOrdinalPrice(uint256 newOrdinalPrice) external onlyOwner {
        ordinalPrice = newOrdinalPrice;
    }

    function setGoldenPrice(uint256 newGoldenPrice) external onlyOwner {
        goldenPrice = newGoldenPrice;
    }

    function setValidator(address validator, bool status) external onlyOwner {
        require(isValidator[validator] != status, "Validator status is already set to this value");
        isValidator[validator] = status;
    }

    function addGoldenSupply(uint256 amount) external onlyOwner {
        goldenSupply = goldenSupply.add(amount);
    }

    function addOrdinalSupply(uint256 amount) external onlyOwner {
        ordinalSupply = ordinalSupply.add(amount);
    }

    function setGoldenDisabled(bool status) external onlyOwner {
        disabledGolden = status;
    }

    function pauseSale() external onlyOwner {
        _pause();
    }

    function resumeSale() external onlyOwner {
        _unpause();
    }


    function PURCHASE_GOLDEN_TYPEHASH() public pure returns (bytes32) {
        return keccak256("PurchaseGolden(uint256 amount,address validator,address to,uint256 max)");
    }

    function purchaseOrdinal(uint256 amount) public payable nonReentrant whenNotPaused {
        require(amount + ordinalSold <= ordinalSupply, "Not enough supply for sale.");
        require(ordinalPrice.mul(amount) == msg.value, "Incorrect Ether value sent.");

        ordinalAmount[msg.sender] += amount;
        ordinalSold = ordinalSold.add(amount);
    }


    function purchaseGolden(
        uint256 amount,
        address validator,
        address to,
        uint256 max,
        uint8 v,
        bytes32 r,
        bytes32 s) public payable nonReentrant whenNotPaused {

        require(!disabledGolden, "Golden egg sale is finish");
        require(isValidator[validator], "Validator is not valid");
        bytes32 hash = keccak256(
            abi.encodePacked(PURCHASE_GOLDEN_TYPEHASH(), amount, validator, to, max)
        );
        require(validator == ecrecover(hash, v, r, s), "Invalid signature");

        require(amount + goldenSold <= goldenSupply, "Not enough golden supply for sale.");
        require(goldenPrice.mul(amount) == msg.value, "Incorrect Ether value sent.");

        require(max >= goldenAmount[to] + amount, "Purchase exceeds maximum limit.");

        goldenAmount[msg.sender] += amount;
        goldenSold = goldenSold.add(amount);
    }


    function withdrawEther() external onlyOwner {
        (bool success,) = msg.sender.call{value: address(this).balance}("");
        require(success, "Withdrawal failed.");
    }

    function withdrawERC20(address tokenAddress) external onlyOwner {
        IERC20 token = IERC20(tokenAddress);
        require(
            token.transfer(msg.sender, token.balanceOf(address(this))),
            "ERC20 withdrawal failed."
        );
    }

    function withdrawERC721(
        address tokenAddress,
        uint256 tokenId
    ) external onlyOwner {
        IERC721 token = IERC721(tokenAddress);
        token.transferFrom(address(this), msg.sender, tokenId);
    }

}
