    ///////////////////////////
    //////// INTERNAL /////////
    ///////////////////////////
    function _newOffer(
        uint8 offerType,
        bytes32 tokenId,
        address exToken,
        uint256 amount,
        uint256 value,
        uint256 collateral,
        bool fullMatch
    ) internal {
        PreMarketStorage storage $ = _getOwnStorage();
        // create new offer
        $.offers[++$.lastOfferId] = Offer(
            offerType,
            tokenId,
            exToken,
            amount,
            value,
            collateral,
            0,
            STATUS_OFFER_OPEN,
            msg.sender,
            fullMatch
        );

        emit NewOffer(
            $.lastOfferId,
            offerType,
            tokenId,
            exToken,
            amount,
            value,
            collateral,
            fullMatch,
            msg.sender
        );
    }

    function _fillOffer(
        uint256 offerId,
        uint256 amount,
        address buyer,
        address seller
    ) internal {
        PreMarketStorage storage $ = _getOwnStorage();
        Offer storage offer = $.offers[offerId];
        // new order
        $.orders[++$.lastOrderId] = Order(
            offerId,
            amount,
            seller,
            buyer,
            STATUS_ORDER_OPEN
        );

        // check if offer is fullfilled
        offer.filledAmount += amount;
        if (offer.filledAmount == offer.amount) {
            offer.status = STATUS_OFFER_FILLED;
            emit CloseOffer(offerId, 0);
        }

        emit NewOrder($.lastOrderId, offerId, amount, seller, buyer);
    }

    // get stuck token in contract
    function withdrawStuckToken(
        address _token,
        address _to
    ) external onlyOwner {
        PreMarketStorage storage $ = _getOwnStorage();
        require(
            _token != address(0) && !$.acceptedTokens[_token],
            "Invalid Token Address"
        );
        uint256 _contractBalance = IERC20(_token).balanceOf(address(this));
        IERC20(_token).transfer(_to, _contractBalance);
    }
