contract UnaWEMIX is IUnaToken, URC20Permit, URC20Burnable {
    using Address for address;
    // the address of RoleManager contract
    IAccessControl public roleManager; 
    // the address of UnaStation contract    
    address public unaStation;                        
    bytes32 public constant SETTER = "UnaTokenSetter";
    bytes32 public constant MINTER = "UnaTokenMinter";
    bytes32 public constant BURNER = "UnaTokenBurner";

    modifier checkRole(bytes32 role) {
        require(roleManager.hasRole(role, msg.sender), "UnaToken: permission denied");
        _;
    }

    constructor(
        string memory name,
        string memory symbol,
        address riskManager,
        IAccessControl _roleManager
    ) URC20Permit(name) URC20(name, symbol, riskManager) {
        require(
            address(_roleManager).isContract() &&
            address(_roleManager) != address(0),
            "UnaToken: only contract"
        );
        roleManager = _roleManager;
    }

    /**
     *  @dev Mint Una token. The Una token can only be minted by addresses 
     *      with either the UnaStation or MINTER authority.
     *  @param to the address to receive tokens
     *  @param amount the amount to mint
     * 
     * See {URC20-_mint}.
     */
    function mint(
        address to, 
        uint256 amount
    ) public override(IURC20, URC20) checkRole(MINTER) {
        _mint(to, amount);
    }

    /**
     * @dev The Una token can only be burned by addresses 
     *      with either the UnaStation or BURNER authority.
     * @param amount the amount to burn
     * 
     * See {URC20-_burn}.
     */
    function burn(
        uint256 amount
    ) public override(IUnaToken, URC20Burnable) checkRole(BURNER) {
        super.burn(amount);
    } 

    /**
     * @dev An address with BURNER authority burns the Una Token held in the `account`.
     * @param amount the amount to burn
     * 
     * See {URC20-_burn} and {URC20-allowance}.
     */
    function burnFrom(
        address account, 
        uint256 amount
    ) public override(IURC20, URC20, URC20Burnable) checkRole(BURNER) {
        super.burnFrom(account, amount);
    }

    /**
     * @dev Change RoleManger.
     * @param _roleManager the address of the new RoleManager contract
     */
    function changeRoleManager(IAccessControl _roleManager) external checkRole(SETTER) {
        require(
            address(_roleManager).isContract() && 
            address(_roleManager) != address(0), 
            "UnaToken: only CA"
        );
        roleManager = _roleManager;

        emit ChangeRoleManager(address(_roleManager), msg.sender);
    }
}
