    "nigga.sol": {
      "content": "pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract NeuralAIRentalAgreement is ReentrancyGuard, Ownable (msg.sender) {

    enum AgreementState { Created, Active, Terminated }
    
    struct RentalAgreement {
        address renter;
        uint rentalDuration;
        uint rentalStartBlock;
        uint rentalCost;
        AgreementState state;
        uint gpuNodeConfiguration;
    }
    
    IERC20 public paymentToken;
    uint public constant agreementFee = 50;
    mapping(uint => RentalAgreement) public agreements;
    uint public nextAgreementId;

    event RentalAgreementCreated(uint agreementId, address renter, uint gpuConfiguration);
    event RentalAgreementActivated(uint agreementId);
    event RentalAgreementTerminated(uint agreementId);
    event PaymentTokenUpdated(address newToken);

    function setPaymentToken(address _newPaymentToken) external onlyOwner {
        require(_newPaymentToken != address(0), "Invalid token address");
        paymentToken = IERC20(_newPaymentToken);
        emit PaymentTokenUpdated(_newPaymentToken);
    }

    function createRentalAgreement(uint _duration, uint _gpuNodeConfiguration, uint _rentalCost) external returns (uint agreementId) {
        agreementId = nextAgreementId++;
        agreements[agreementId] = RentalAgreement(msg.sender, _duration, 0, _rentalCost + agreementFee, AgreementState.Created, _gpuNodeConfiguration);
        emit RentalAgreementCreated(agreementId, msg.sender, _gpuNodeConfiguration);
        return agreementId;
    }
    
    function activateRentalAgreement(uint _agreementId) external nonReentrant {
        RentalAgreement storage agreement = agreements[_agreementId];
        require(agreement.renter == msg.sender, "Only renter can activate");
        require(agreement.state == AgreementState.Created, "Incorrect state");
        require(paymentToken != IERC20(address(0)), "Payment token not set");
        bool paymentSucceeded = paymentToken.transferFrom(msg.sender, address(this), agreement.rentalCost);
        require(paymentSucceeded, "Payment failed");
        agreement.state = AgreementState.Active;
        agreement.rentalStartBlock = block.number;
        emit RentalAgreementActivated(_agreementId);
    }
    
     function terminateRentalAgreement(uint _agreementId) external onlyOwner {
        RentalAgreement storage agreement = agreements[_agreementId];
        require(agreement.state == AgreementState.Active, "Incorrect state");
        agreement.state = AgreementState.Terminated;
        emit RentalAgreementTerminated(_agreementId);
    }
}
