    mapping (address => bool) public  _isBlacklisted;

    constructor() ERC20("WOOF Coin", "WOOF") {

        /*
            % Presale - 60%,    
            % Liquidity Lock - 20% 
            % CEX - 5%
            % Marketing - 5%, 
            % Airdrop - 10%
        */

        //CHANGE ADDRESSES

        /// PRESALE
        _mint(0x47514714Bcc3B94d554540C6bbdCDe2dfC6D2C5e, 5999999999 * 10 ** 18);

        /// LIQUIDITY LOCK
        _mint(0x47514714Bcc3B94d554540C6bbdCDe2dfC6D2C5e, 2000000000 * 10 ** 18);

        /// CEX
        _mint(0x47514714Bcc3B94d554540C6bbdCDe2dfC6D2C5e, 5000000000 * 10 ** 18);

        /// MARKETING
        _mint(0x47514714Bcc3B94d554540C6bbdCDe2dfC6D2C5e, 5000000000 * 10 ** 18);

        /// AIRDROP
        _mint(0x47514714Bcc3B94d554540C6bbdCDe2dfC6D2C5e, 1000000000 * 10 ** 18);

    }

    /// PAUSABLE

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override whenNotPaused {
        require(!_isBlacklisted[from] && !_isBlacklisted[to], "To Or From Address Is Blacklisted!");    
        super._transfer(from, to, amount);
  
    }

    /// BURN
    
    /**
     * @dev Destroys `amount` tokens from the caller.
     *
     * See {ERC20-_burn}.
     */
    function burn(uint256 amount) public virtual {
        _burn(_msgSender(), amount);
    }

