    mapping (address => bool) public isExcludedFromFees;
    mapping (address => bool) public isDividendExempt;
    // store addresses that a automatic market maker pairs. Any transfer *to* these addresses
    // could be subject to a maximum transfer amount
    mapping (address => bool) public automatedMarketMakerPairs;

    constructor( address tokenHolder ) ERC20('KITTYCAT', 'KITTY')  {

        uint256 totalSupply = 1000000000000 * 10 ** 18;
        swapTokensAtAmount = totalSupply.mul(2).div(10**6); // 0.002%

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(
          0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D
        );
        // uniswapPair = PancakeLibrary.pairFor(_uniswapV2Router.factory(), address(this), USDT);
        uniswapPair = IUniswapV2Factory( _uniswapV2Router.factory() ).createPair( address(this), _uniswapV2Router.WETH());
        _setAutomatedMarketMakerPair(address(uniswapPair), true);
        uniswapRouter = _uniswapV2Router;

        _excludeFromFees(_msgSender(), true);
        _excludeFromFees(address(this), true);
        _excludeFromFees(tokenHolder, true);
        _excludeFromFees(marketingAddress, true);

        _approve(address(this), address(uniswapRouter), MAX);
        _mint(tokenHolder , totalSupply);
    }

    receive() external payable {}

    function _excludeFromFees(address account, bool excluded) internal {
        isExcludedFromFees[account] = excluded;
    }

    function _setAutomatedMarketMakerPair(address pair, bool value) internal {
        require(automatedMarketMakerPairs[pair] != value, "Automated market maker pair is already set to that value");
        automatedMarketMakerPairs[pair] = value;
    }

    function updateUniswapV2Router(address newAddress) public onlyOwner {
        require(newAddress != address(uniswapRouter), "The router already has that address");
        uniswapRouter = IUniswapV2Router02(newAddress);
        address _uniswapV2Pair = IUniswapV2Factory(uniswapRouter.factory()).createPair(address(this),  uniswapRouter.WETH());
        uniswapPair = _uniswapV2Pair;
        _setAutomatedMarketMakerPair(_uniswapV2Pair, true);
        isDividendExempt[uniswapPair] = true;
    }

    function excludeFromFeesList(address[] calldata accounts, bool excluded) public onlyOwner {
        for(uint256 i = 0; i < accounts.length; i++) {
            _excludeFromFees(accounts[i], excluded);
        }
    }

    function setAutomatedMarketMakerPair(address pair, bool value) public onlyOwner {
        require(pair != uniswapPair, "The PancakeSwap pair cannot be removed from automatedMarketMakerPairs");
        _setAutomatedMarketMakerPair(pair, value);
    }


    function updateGasForProcessing(uint256 newValue) public onlyOwner {
        require(newValue >= 200000 && newValue <= 1000000, "GasForProcessing must be between 200,000 and 1000,000");
        require(newValue != gasForProcessing, "Cannot update gasForProcessing to same value");
        gasForProcessing = newValue;
    }

    function setSwapAndLiquifyEnabled(bool _enabled) public onlyOwner {
        require(swapAndLiquifyEnabled != _enabled, 'Equivalent state');
        swapAndLiquifyEnabled = _enabled;
    }

    function setSwapTokensAtAmount(uint256 amount) public onlyOwner {
        require(amount > 0, 'Invalid value');
        swapTokensAtAmount = amount;
    }

    function setMarketingWalletAddress(address _marketingAddress) public onlyOwner {
        require(_marketingAddress != address(0), 'Invalid address');
        _excludeFromFees(_marketingAddress, true);
        marketingAddress = _marketingAddress;
    }

    function setTransferFee(uint256 _fee) external onlyOwner {
        require(_fee <= 5000, 'transfer fee > 5000');
        transferFee = _fee;
    }

    function setTxFee(uint256 _buyFee, uint256 _sellFee) external onlyOwner {
        buyFee = _buyFee;
        sellFee = _sellFee;
    }

    function setShareFee(  uint256 _marketingFee ) external onlyOwner {
        marketingFee = _marketingFee;
    }


    function startAddLP() external onlyOwner {
        require(0 == startAddLPBlock, "startedAddLP");
        startAddLPBlock = block.number;
    }

    function startTrade() external onlyOwner {
        require(0 == startTradeBlock, "T");
        startTradeBlock = block.number;
    }


    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        if(amount == 0) {
            super._transfer(from, to, 0);
            return;
        }


        uint256 contractTokenBalance = balanceOf(address(this));
        bool canSwap = contractTokenBalance > swapTokensAtAmount;
        if (startTradeBlock > 0 && !swapping && swapAndLiquifyEnabled && canSwap && !automatedMarketMakerPairs[from] &&!isExcludedFromFees[from] &&
            from != address(uniswapRouter)){
            swapping = true;
            swapAndSendToFee(contractTokenBalance);
            swapping = false;
        }

        bool takeFee = !swapping;
        // if any account belongs to _isExcludedFromFee account then remove the fee
        if(isExcludedFromFees[from] || isExcludedFromFees[to] || to == address(uniswapRouter)) {
            takeFee = false;
        }

        if(takeFee) {

            uint256 fees;
            uint256 feeRate = transferFee;

            if(automatedMarketMakerPairs[to] || automatedMarketMakerPairs[from]
              || from == address(uniswapRouter
            )){

                feeRate = automatedMarketMakerPairs[from] || from == address(uniswapRouter) ? buyFee : sellFee;
                if (0 == startTradeBlock) {
                    require(0 < startAddLPBlock && automatedMarketMakerPairs[to], "!startAddLP");
                }

                if(automatedMarketMakerPairs[to] && balanceOf(from) == amount){
                    amount = amount.sub((0.000001 ether));
                }
            }
            fees = amount.mul(feeRate).div(10000);
            if(fees > 0){
                super._transfer(from, address(this), fees);
                amount = amount.sub(fees);
            }
        }

        super._transfer(from, to, amount);

        if(!swapping) {

        }
    }


    function _totalShareExeBurnFee() internal view returns(uint256){
        return marketingFee ;
    }

    function swapAndSendToFee(uint256 _swapAmounts) private  {
        if(_swapAmounts != 0){ swapTokensForEth(_swapAmounts); }
        uint256 initialBalance = address(this).balance;
        if(initialBalance == 0) return;

        if(marketingAddress != address(0)
          && initialBalance > 0
        ){
          TransferHelper.safeTransferETH(marketingAddress, initialBalance);
        }
    }


    event SwapAndLiquify( uint256 tokensSwapped, uint256 ethReceived, uint256 tokensIntoLiqudity );
    function swapAndLiquify(uint256 swapAmounts, uint256 tokens) private {
       // split the contract balance into halves
        uint256 half = tokens.div(2);
        uint256 otherHalf = tokens.sub(half);

        uint256 initialBalance = address(this).balance;
        // swap tokens for ETH
        swapTokensForEth(swapAmounts.add(half)); // <- this breaks the ETH -> HATE swap when swap+liquify is triggered
        // how much ETH did we just swap into?
        uint256 newBalance = address(this).balance.sub(initialBalance);

        // add liquidity to uniswap
        addLiquidity(otherHalf, newBalance);
        emit SwapAndLiquify(half, newBalance, otherHalf);
    }


    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios

        // _approve(address(this), address(uniswapV2Router), tokenAmount);
        // add the liquidity
        uniswapRouter.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            address(0),
            block.timestamp
        );

    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapRouter.WETH();
        // _approve(address(this), address(uniswapRouter), tokenAmount);
        // make the swap
        uniswapRouter.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

