/// @title  Token Distributor
/// @notice Holds tokens for users to claim.
/// @dev    Unlike a merkle distributor this contract uses storage to record claims rather than a
///         merkle root. This is because calldata on Arbitrum is relatively expensive when compared with
///         storage, since calldata uses L1 gas.
///         After construction do the following
///         1. transfer tokens to this contract
///         2. setRecipients - called as many times as required to set all the recipients
///         3. transferOwnership - the ownership of the contract should be transferred to a new owner (eg DAO) after all recipients have been set
contract CurrentPresaleV2 is Ownable {

    using SafeMath for uint256;

    struct Token{ address tokenAddress; uint256 decimals; bool isNative; }

    /// @notice Token to be distributed
    Token public tokenPay;
    Token public tokenTar;

    uint256 public presalePrice         = 1e18 * 60_00000000;
    uint256 public personalMaxAmount    = 1e18 * 60;
    uint256 public personalMinAmount    = 1e18 * 0;
    uint256 public userMaxAmount        = 1e18 * 1;
    uint256 public userMinAmount        = 1e18 * 5 / 100;
    uint256 public startTime;
    uint256 public endTime;
    uint256 public stakeAmount;

    address public teamAddress = 0xd07bc45C54B9D71dC76EA47Bf31D35a055D106C0;

    struct UserInfo {
      uint256 amount;
      bool isClaim;
      uint256 fallbackAmount;
      uint256 receivedAmount;
    }

    mapping (address => UserInfo) public userInfos;

    uint private unlocked = 1;
    modifier lock() {
          require(unlocked == 1, 'LOCKED');
          unlocked = 0;
          _;
          unlocked = 1;
    }

    event Stake(address indexed sender, uint256 amount);
    event Claim(address indexed sender, uint256 fallbackAmount, uint256 presaleAmount);
    /// @notice Tokens withdrawn
    event Withdrawal(address indexed recipient, uint256 amount);

    // npx hardhat node --fork https://bsc-mainnet.nodereal.io/v1/1579f014059f49778b29af287eff9745
    constructor( address _tokenPayAddress, bool _isNative, address _tokenTarAddress) {

        require(_tokenPayAddress != address(0) || _isNative, '_tokenPayAddress address zero');
        tokenPay = Token({
          tokenAddress: _tokenPayAddress,
          isNative: _isNative,
          decimals: _isNative ? 1e18 : (10 ** IERC20Metadata(_tokenPayAddress).decimals())
        });

        uint256 _decimals = _tokenTarAddress == address(0) ? 1e18 : (10 ** IERC20Metadata(_tokenTarAddress).decimals());
        tokenTar = Token({
          tokenAddress: _tokenTarAddress,
          isNative: false,
          decimals: _decimals
        });
    }

    struct RestState{
        uint256 personalMaxAmount;
        uint256 personalMinAmount;
        uint256 presalePrice;
        uint256 endTime;
        uint256 startTime;
        uint256 stakeAmount;
        uint256 userMaxAmount;
        uint256 userMinAmount;
    }

    function restState() public view returns(RestState memory) {
        return RestState( {
            personalMaxAmount: personalMaxAmount,
            personalMinAmount: personalMinAmount,
            presalePrice: presalePrice,
            endTime: endTime,
            startTime: startTime,
            stakeAmount: stakeAmount,
            userMaxAmount: userMaxAmount,
            userMinAmount: userMinAmount
        });
    }

    receive() external payable {
      require(tokenPay.isNative , 'No is Native');
      stake(0);
    }

    function stake(uint256 amount) public payable lock{

        UserInfo storage user = userInfos[msg.sender];
        uint256 _amount = tokenPay.isNative ? msg.value : amount;

        require(_amount > 0, "stake amount 0");
        require(startTime != 0 && endTime != 0, "close");
        require(block.timestamp >= startTime, "not started");
        require(block.timestamp < endTime, "ended");

        require(user.amount.add(_amount) >= userMinAmount && user.amount.add(_amount) <= userMaxAmount, 'stake amount limit');
        require(stakeAmount < personalMaxAmount, "max");
        //
        if(!tokenPay.isNative){
          pay(tokenPay,  msg.sender , address(this), _amount);
        }
        //
        uint256 fallbackAmount = stakeAmount.add(_amount) > personalMaxAmount ? stakeAmount.add(_amount).sub(personalMaxAmount) : 0;
        if(fallbackAmount > 0){
            _amount = _amount.sub(fallbackAmount);
            pay(tokenPay, address(this), msg.sender , fallbackAmount);
        }
        //
        pay(tokenPay, address(this), teamAddress , _amount);

        user.amount = user.amount.add(_amount);
        stakeAmount = stakeAmount.add(_amount);

        emit Stake(msg.sender, _amount);

    }


    function getPending(address account) public view returns(uint256, uint256, uint256){

        UserInfo memory user = userInfos[account];

        if(stakeAmount > personalMaxAmount){
          uint256 fallbackAmount = stakeAmount.sub(personalMaxAmount).mul(user.amount).div(stakeAmount);
          uint256 buyAmount = user.amount.sub(fallbackAmount).sub(1);
          uint256 _receivedAmount = buyAmount.mul(presalePrice).mul(tokenTar.decimals).div(tokenPay.decimals).div(1e18);
          return ( fallbackAmount, buyAmount, _receivedAmount);
        }

        uint256 receivedAmount = user.amount.mul(
          presalePrice
        ).mul(tokenTar.decimals).div(tokenPay.decimals).div(1e18);

        return ( 0, user.amount, receivedAmount);

    }


    function claim() external lock {

        UserInfo storage user = userInfos[msg.sender];
        require((endTime != 0 && endTime <= block.timestamp), 'to end');
        require(!user.isClaim && user.amount > 0, 'is claim');

        user.isClaim = true;
        (uint256 fallbackAmount,  , uint256 receivedAmount) = getPending(msg.sender);
        if( fallbackAmount != 0){ pay( tokenPay, address(this), msg.sender , fallbackAmount); }
        if( receivedAmount != 0){ pay( tokenTar, address(this), msg.sender , receivedAmount); }
        user.fallbackAmount = fallbackAmount;
        user.receivedAmount = receivedAmount;

        emit Claim(msg.sender, fallbackAmount, receivedAmount);

    }

    function getTokenMetadata() public view returns(uint256, uint256){
      if(tokenTar.tokenAddress != address(0)){
        return  (IERC20Metadata(tokenTar.tokenAddress).decimals(), IERC20(tokenTar.tokenAddress).balanceOf(address(this)));
      }
      return (18 , 0);
    }

    function pay(
          Token memory token,
          address payer,
          address recipient,
          uint256 value
    ) internal {
      if(token.isNative && payer == address(this)){ TransferHelper.safeTransferETH(recipient, value);
      }else if(payer == address(this)){
        TransferHelper.safeTransfer(token.tokenAddress, recipient, value);
      }else {
        TransferHelper.safeTransferFrom(token.tokenAddress, payer, recipient, value);
      }
    }

    function setDatas(uint256 _presalePrice, uint256 _personalMaxAmount, uint256 _personalMinAmount, uint256 _userMaxAmount, uint256 _userMinAmount) external onlyOwner{
        presalePrice = _presalePrice;
        personalMaxAmount = _personalMaxAmount;
        personalMinAmount = _personalMinAmount;
        userMaxAmount = _userMaxAmount;
        userMinAmount = _userMinAmount;
    }

    function setTokenTar(address _tokenTarAddress) external onlyOwner{
        require(_tokenTarAddress != address(0), "claim not started");
        tokenTar =  Token({
          tokenAddress: _tokenTarAddress,
          isNative: false,
          decimals: 10 ** IERC20Metadata(_tokenTarAddress).decimals()
        });
    }

    function withdrawETH(address account) external onlyOwner {
        uint256 amount = address(this).balance;
        payable(account).transfer(amount);
    }

    function withdraw() external onlyOwner {
        uint256 amount = IERC20(tokenTar.tokenAddress).balanceOf(address(this));
        require(IERC20(tokenTar.tokenAddress).transfer(teamAddress, amount), "fail transfer token");
        emit Withdrawal(teamAddress, amount);
    }

    function closeMint() external onlyOwner {
        startTime = 0;
        endTime = 0;
    }

    function startMint()  external onlyOwner {
        startTime = block.timestamp;
        endTime = startTime + 2592000;
    }

    function updateEndTime(uint256 _endTime) external onlyOwner {
        endTime = _endTime;
    }

    function endMint() external onlyOwner {
        endTime = block.timestamp;
    }

}
