    "contracts/core/LiquidityBootstrapPoolFactory.sol": {
      "content": "// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity =0.8.21;

import "solady/src/utils/SafeTransferLib.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

import "../interfaces/IDecimals.sol";
import "../interfaces/ILiquidityBootstrapPool.sol";
import "../interfaces/ILiquidityBootstrapPoolDeployer.sol";

import "../extensions/TokensRescuer.sol";

struct PoolSettings {
    address asset;
    address share;
    address manager;
    address weth;
    address router;
    uint88 virtualAssets;
    uint88 virtualShares;
    uint88 maxSharePrice;
    uint88 maxTotalSharesOut;
    uint88 maxTotalAssetsIn;
    uint64 weightStart;
    uint64 weightEnd;
    uint40 saleStart;
    uint40 saleEnd;
    bool sellingAllowed;
    bytes32 whitelistMerkleRoot;
    uint256 cap;
    address[] arbiters;
}

struct FactorySettings {
    address feeRecipient;
    uint48 platformFee;
    uint48 referrerFee;
    uint48 swapFee;
}

uint256 constant MAX_FEE_BIPS = 0.1e4;

contract LiquidityBootstrapPoolFactory is OwnableUpgradeable, TokensRescuer {
    using SafeTransferLib for *;

    /// @dev Emitted when a new Liquidity Bootstrap Pool is created.
    /// @param pool The address of the newly created Liquidity Bootstrap Pool.
    event PoolCreated(address pool);

    event PoolDeployerSet(address poolDeployer);

    /// @dev Emitted when the fee recipient address is updated.
    /// @param recipient The new fee recipient address.
    event FeeRecipientSet(address recipient);

    /// @dev Emitted when the platform fee is updated.
    /// @param fee The new platform fee value.
    event PlatformFeeSet(uint256 fee);

    /// @dev Emitted when the referrer fee is updated.
    /// @param fee The new referrer fee value.
    event ReferrerFeeSet(uint256 fee);

    /// @dev Emitted when the swap fee is updated.
    /// @param fee The new swap fee value.
    event SwapFeeSet(uint256 fee);

    /// -----------------------------------------------------------------------
    /// Mutable Storage
    /// -----------------------------------------------------------------------

    /// @notice Storage for factory-specific settings.
    FactorySettings public factorySettings;

    address public poolDeployer;

    /// @param _owner The owner of the factory contract.
    /// @param _feeRecipient The address that will receive platform and referrer fees.
    /// @param _platformFee The platform fee, represented as a fraction with a denominator of 10,000.
    /// @param _referrerFee The referrer fee, represented as a fraction with a denominator of 10,000.
    /// @param _swapFee The referrer fee, represented as a fraction with a denominator of 10,000.
    function __LiquidityBootstrapPoolFactory_init(
        address _owner,
        address _feeRecipient,
        uint48 _platformFee,
        uint48 _referrerFee,
        uint48 _swapFee
    ) external initializer {
        __Ownable_init_unchained(_owner);
        __LiquidityBootstrapPoolFactory_init_unchained(
            _feeRecipient,
            _platformFee,
            _referrerFee,
            _swapFee
        );
    }

    /// -----------------------------------------------------------------------
    /// Creation Logic
    /// -----------------------------------------------------------------------

    /// @notice Creates a new Liquidity Bootstrap Pool with the provided settings and parameters.
    /// @param args The PoolSettings struct containing pool-specific parameters.
    /// @param shares The number of shares to transfer to the newly created pool.
    /// @return pool The address of the newly created Liquidity Bootstrap Pool.
    function createLiquidityBootstrapPool(
        PoolSettings memory args,
        uint256 shares,
        uint256 assets
    ) external returns (address pool) {
        if (
            args.share == args.asset ||
            args.share == address(0) ||
            args.asset == address(0)
        ) {
            revert("InvalidAssetOrShare");
        }

        // Check timestamps to ensure the sale will not immediately end.
        if (
            uint40(block.timestamp + 1 days) > args.saleEnd ||
            args.saleEnd - args.saleStart < uint40(1 days)
        ) {
            revert("SalePeriodLow");
        }

        if (
            args.weightStart < 0.01 ether ||
            args.weightStart > 0.99 ether ||
            args.weightEnd > 0.99 ether ||
            args.weightEnd < 0.01 ether
        ) {
            revert("InvalidWeightConfig");
        }

        if (assets == 0 && args.virtualAssets == 0) revert("InvalidAssetValue");

        pool = ILiquidityBootstrapPoolDeployer(poolDeployer).createPool(
            _getSettings(args)
        );

        args.share.safeTransferFrom(msg.sender, pool, shares);
        args.asset.safeTransferFrom(msg.sender, pool, assets);

        ILiquidityBootstrapPoolDeployer(poolDeployer).setAssetsStart(pool);

        emit PoolCreated(pool);
    }

    /// -----------------------------------------------------------------------
    /// Settings Modification Logic
    /// -----------------------------------------------------------------------

    function setPoolDeployer(address _poolDeployer) external onlyOwner {
        poolDeployer = _poolDeployer;

        emit PoolDeployerSet(_poolDeployer);
    }

    /// @notice Sets the fee recipient address.
    /// @param recipient The new fee recipient address.
    function setFeeRecipient(address recipient) external onlyOwner {
        factorySettings.feeRecipient = recipient;

        emit FeeRecipientSet(recipient);
    }

    /// @notice Sets the platform fee percentage.
    /// @param fee The new platform fee value, represented as a fraction with a denominator of 10,000.
    function setPlatformFee(uint48 fee) external onlyOwner {
        if (fee > MAX_FEE_BIPS) revert("MaxFeeExceeded");

        factorySettings.platformFee = fee;

        emit PlatformFeeSet(fee);
    }

    /// @notice Sets the referrer fee percentage.
    /// @param fee The new referrer fee value, represented as a fraction with a denominator of 10,000.
    function setReferrerFee(uint48 fee) external onlyOwner {
        if (fee > MAX_FEE_BIPS) revert("MaxFeeExceeded");

        factorySettings.referrerFee = fee;

        emit ReferrerFeeSet(fee);
    }

    /// @notice Sets the swap fee percentage.
    /// @param fee The new swap fee value, represented as a fraction with a denominator of 10,000.
    function setSwapFee(uint48 fee) external onlyOwner {
        if (fee > MAX_FEE_BIPS) revert("MaxFeeExceeded");

        factorySettings.swapFee = fee;

        emit SwapFeeSet(fee);
    }

    /// @notice Modifies multiple factory settings at once.
    /// @param feeRecipient The new fee recipient address.
    /// @param platformFee The new platform fee value, represented as a fraction with a denominator of 10,000.
    /// @param referrerFee The new referrer fee value, represented as a fraction with a denominator of 10,000.
    /// @param swapFee The new swap fee value, represented as a fraction with a denominator of 10,000.
    function modifySettings(
        address feeRecipient,
        uint48 platformFee,
        uint48 referrerFee,
        uint48 swapFee
    ) external onlyOwner {
        if (platformFee > MAX_FEE_BIPS) revert("MaxFeeExceeded (platformFee)");
        if (referrerFee > MAX_FEE_BIPS) revert("MaxFeeExceeded (referrerFee)");
        if (swapFee > MAX_FEE_BIPS) revert("MaxFeeExceeded (swapFee)");

        factorySettings = FactorySettings(
            feeRecipient,
            platformFee,
            referrerFee,
            swapFee
        );

        emit FeeRecipientSet(feeRecipient);
        emit PlatformFeeSet(platformFee);
        emit ReferrerFeeSet(referrerFee);
        emit SwapFeeSet(swapFee);
    }

    function rescueERC20Token(
        address token,
        uint256 amount,
        address receiver
    ) external onlyOwner {
        _rescueERC20Token(token, amount, receiver);
    }

    function rescueNativeToken(
        uint256 amount,
        address receiver
    ) external onlyOwner {
        _rescueNativeToken(amount, receiver);
    }

    function __LiquidityBootstrapPoolFactory_init_unchained(
        address _feeRecipient,
        uint48 _platformFee,
        uint48 _referrerFee,
        uint48 _swapFee
    ) internal initializer {
        if (_platformFee > MAX_FEE_BIPS) revert("MaxFeeExceeded (platformFee)");
        if (_referrerFee > MAX_FEE_BIPS) revert("MaxFeeExceeded (referrerFee)");
        if (_swapFee > MAX_FEE_BIPS) revert("MaxFeeExceeded (swapFee)");

        factorySettings = FactorySettings(
            _feeRecipient,
            _platformFee,
            _referrerFee,
            _swapFee
        );

        emit FeeRecipientSet(_feeRecipient);
        emit PlatformFeeSet(_platformFee);
        emit ReferrerFeeSet(_referrerFee);
        emit SwapFeeSet(_swapFee);
    }

    function _getSettings(
        PoolSettings memory args
    )
        internal
        view
        returns (ILiquidityBootstrapPool.PoolDeploySettings memory)
    {
        FactorySettings memory settings = factorySettings;

        unchecked {
            return
                ILiquidityBootstrapPool.PoolDeploySettings({
                    platform: settings.feeRecipient,
                    platformFee: uint64(settings.platformFee) * 1e14,
                    referrerFee: uint64(settings.referrerFee) * 1e14,
                    swapFee: uint64(settings.swapFee) * 1e14,
                    asset: args.asset,
                    share: args.share,
                    manager: args.manager,
                    virtualAssets: args.virtualAssets,
                    virtualShares: args.virtualShares,
                    maxSharePrice: args.maxSharePrice,
                    maxTotalSharesOut: args.maxTotalSharesOut,
                    maxTotalAssetsIn: args.maxTotalAssetsIn,
                    weightStart: args.weightStart,
                    weightEnd: args.weightEnd,
                    saleStart: args.saleStart,
                    saleEnd: args.saleEnd,
                    sellingAllowed: args.sellingAllowed,
                    whitelistMerkleRoot: args.whitelistMerkleRoot,
                    cap: args.cap,
                    weth: args.weth,
                    router: args.router,
                    arbiters: args.arbiters
                });
        }
    }

    uint256[142] private __gap;
}
"
    },
    "contracts/extensions/CheckerZeroAddr.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity 0.8.21;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

error OnlyNonZeroAddress();

abstract contract CheckerZeroAddr is Initializable {
    modifier onlyNonZeroAddress(address addr) {
        _onlyNonZeroAddress(addr);
        _;
    }

    function _onlyNonZeroAddress(address addr) internal pure {
        if (addr == address(0)) {
            revert OnlyNonZeroAddress();
        }
    }
}
