      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {INoFomo} from "./interface/INoFomo.sol";

event Stake(address indexed user, uint256 amount);

event Withdraw(address indexed user, uint256 amount);

contract FomosStaking is Ownable {
    INoFomo public noFomo;

    uint256 public periodId;

    constructor(address _noFomo) Ownable(msg.sender) {
        noFomo = INoFomo(_noFomo);
    }

    address public mintingContract;

    uint256 public poolTotalStakedAmount;

    // period => distribution
    mapping(uint256 => uint256) public periodDistribution;

    // user => period
    mapping(address => uint256) public userToPeriodId;

    // user => stakeAmount
    mapping(address => uint256) public userToStakeAmount;

    function setMintingContract(address _mintingContract) external onlyOwner {
        mintingContract = _mintingContract;
    }

    function stake(uint256 amount) external {
        require(noFomo.transferFrom(msg.sender, address(this), amount), "Not enough balance or allowance");
        require(userToStakeAmount[msg.sender] == 0, "Already staked");
        poolTotalStakedAmount += amount;
        userToPeriodId[msg.sender] = periodId;
        userToStakeAmount[msg.sender] = amount;
        emit Stake(msg.sender, amount);
    }

    function withdraw() external {
        require(userToStakeAmount[msg.sender] > 0, "User not staked");
        uint256 claimAmount = getClaimRewards(msg.sender);
        poolTotalStakedAmount -= userToStakeAmount[msg.sender];
        uint256 totalClaimAmount = claimAmount + userToStakeAmount[msg.sender];
        userToStakeAmount[msg.sender] = 0;
        noFomo.transfer(msg.sender, totalClaimAmount);
        emit Withdraw(msg.sender, totalClaimAmount);
    }

    function updateCycle(uint256 _periodId, uint256 distributionAmount) external {
        require(msg.sender == mintingContract, "only minting contract");
        if (poolTotalStakedAmount > 0) {
            periodDistribution[periodId] = (distributionAmount * 1 ether) / poolTotalStakedAmount;
        }
        periodId = _periodId;
    }

    function getClaimRewards(address receipt) public view returns (uint256) {
        uint256 userPeriodId = userToPeriodId[receipt];
        uint256 totalClaimAmount = 0;
        for (uint256 i = userPeriodId; i < periodId; i++) {
            uint256 distribution = periodDistribution[i];
            uint256 stakeAmount = userToStakeAmount[receipt];
            uint256 claimAmount = (distribution * stakeAmount) / 1 ether;
            totalClaimAmount += claimAmount;
        }
        return totalClaimAmount;
    }
}
