      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import {IUniswapV2Pair} from "./interface/IUniswapV2Pair.sol";
import {IUniswapV2Router} from "./interface/IUniswapV2Router.sol";
import {INoFomo} from "./interface/INoFomo.sol";
import {INoFomoNFT} from "./interface/INoFomoNFT.sol";
import {IFomosPrizePool} from "./interface/IFomosPrizePool.sol";
import {IDropRandomPool} from "./interface/IDropRandomPool.sol";
import {IFomosStaking} from "./interface/IFomosStaking.sol";

contract Minting is Ownable, ReentrancyGuard {
    uint256 public currentStoredBlock;
    uint256 public immutable BLOCK_INTERVAL = 30;

    IUniswapV2Pair public unfomosTokenpair;
    IUniswapV2Router public uniswapV2Router;

    address public governAddress;
    address public dropRandomPool;
    address public stakingPool;
    address public fomoPrizePool;
    INoFomo public nofomoToken;
    INoFomoNFT public nofomoNFT;
    address public invationAddress;

    uint256 public currentPeriodId;
    // periodId => user => tokenIds
    mapping(uint256 => mapping(address => uint256[])) public mintedNFTs;
    // periodId => total minted
    mapping(uint256 => uint256) public periodIdToTotalMinted;
    mapping(uint256 => uint256) public periodIdToClaimAmount;
    mapping(uint256 => uint256) public periodIdToStartTokenId;
    mapping(address => uint256) public claimTokenRecord;

    bool public isMinting = true;

    constructor(
        address _goveranceAddress,
        address _nofomoToken,
        address _nofomoNFT,
        address _dropRandomPool,
        address _stakingPool,
        uint256 _startBlockHeight
    ) Ownable(msg.sender) {
        governAddress = _goveranceAddress;
        nofomoToken = INoFomo(_nofomoToken);
        nofomoNFT = INoFomoNFT(_nofomoNFT);
        dropRandomPool = _dropRandomPool;
        stakingPool = _stakingPool;
        currentStoredBlock = _startBlockHeight;
    }

    modifier flushCycle() {
        if (isMinting && block.number > currentStoredBlock + BLOCK_INTERVAL) {
            uint256 periodLength = (block.number - currentStoredBlock) / 30;

            if (periodIdToTotalMinted[currentPeriodId] > 0) {
                addNoFomosLP();
                buyBackAndBurn();
                // transfer 10% received amount to govern address
                uint256 goverancePeriodReceivedAmount = periodIdToTotalMinted[
                    currentPeriodId
                ] * 0.002 ether;
                (bool sent, ) = address(governAddress).call{
                    value: goverancePeriodReceivedAmount
                }("");
                require(sent, "Failed to send Ether to govern address");

                nofomoToken.mint();

                periodIdToClaimAmount[currentPeriodId] = (1200_000 * 10 ** 18) / periodIdToTotalMinted[currentPeriodId];
                nofomoToken.transfer(fomoPrizePool, 400_000 * 10 ** 18);
                nofomoToken.transfer(dropRandomPool, 200_000 * 10 ** 18);
                nofomoToken.transfer(stakingPool, 200_000 * 10 ** 18);

                IFomosStaking(stakingPool).updateCycle(
                    currentPeriodId,
                    periodIdToTotalMinted[currentPeriodId]
                );

                if (periodLength >= 10) {
                    IDropRandomPool(dropRandomPool).lottery(
                        periodIdToStartTokenId[currentPeriodId],
                        periodIdToTotalMinted[currentPeriodId]
                    );
                }
            }

            if (periodLength > 1 && currentPeriodId > 0) {
                IFomosPrizePool(fomoPrizePool).updateTokenInMinting();
            }
            currentPeriodId += periodLength;
            currentStoredBlock += periodLength * BLOCK_INTERVAL;
        }
        _;
    }

    function mintNoFomos() external payable flushCycle nonReentrant {
        require(
            msg.value > 0 &&
                msg.value % 0.02 ether == 0 &&
                msg.value <= 1 ether,
            "FomosToken: invalid amount"
        );
        require(block.number >= currentStoredBlock, "Minting not started");
        require(isMinting, "FomosToken: mint ended");
        uint256 amount = msg.value / 0.02 ether;

        uint256[] memory tokenIds = nofomoNFT.mint(
            msg.sender,
            amount,
            currentPeriodId
        );
        for (uint i = 0; i < tokenIds.length; i++) {
            mintedNFTs[currentPeriodId][msg.sender].push(tokenIds[i]);
        }
        if (periodIdToTotalMinted[currentPeriodId] == 0) {
            periodIdToStartTokenId[currentPeriodId] = tokenIds[0];
        }
        periodIdToTotalMinted[currentPeriodId] += amount;
    }

    function mintByInvitation(address receipt, uint256 amount) external {
        require(
            msg.sender == invationAddress,
            "FomosToken: invalid invation address"
        );
        require(block.number >= currentStoredBlock, "Minting not started");
        require(amount > 0, "FomosToken: invalid amount");
        uint256[] memory tokenIds = nofomoNFT.mint(
            receipt,
            amount,
            currentPeriodId
        );
        for (uint i = 0; i < tokenIds.length; i++) {
            mintedNFTs[currentPeriodId][receipt].push(tokenIds[i]);
        }
        if (periodIdToTotalMinted[currentPeriodId] == 0) {
            periodIdToStartTokenId[currentPeriodId] = tokenIds[0];
        }
        periodIdToTotalMinted[currentPeriodId] += amount;
    }

    function addNoFomosLP() internal {
        uint256 lpEtherAmount = periodIdToTotalMinted[currentPeriodId] *
            0.009 ether;

        uint112 reserveWETH;
        uint112 reserveNofomoToken;
        if (unfomosTokenpair.token0() == address(nofomoToken)) {
            (reserveNofomoToken, reserveWETH, ) = IUniswapV2Pair(
                unfomosTokenpair
            ).getReserves();
        } else {
            (reserveWETH, reserveNofomoToken, ) = IUniswapV2Pair(
                unfomosTokenpair
            ).getReserves();
        }
        // use 1.01 for fully slippage protection
        uint256 amountTokenDesired = (uniswapV2Router.quote(
            lpEtherAmount,
            reserveWETH,
            reserveNofomoToken
        ) * 101) / 100;
        require(
            nofomoToken.approve(address(uniswapV2Router), amountTokenDesired),
            "approve nofomo token failed"
        );
        if (nofomoToken.liquidityPoolMint(amountTokenDesired)) {
            (uint amountToken, uint amountETH, uint liquidity) = uniswapV2Router
                .addLiquidityETH{value: lpEtherAmount}(
                address(nofomoToken),
                amountTokenDesired,
                0,
                lpEtherAmount,
                address(0),
                block.timestamp + 1000
            );
        } else {
            stopMintingPeriod();
            revert("NoFomoToken: mint ended");
        }
    }

    function buyBackAndBurn() internal {
        uint256 buyBackAndBurnAmount = periodIdToTotalMinted[currentPeriodId] *
            0.009 ether;

        uint112 reserveWETH;
        uint112 reserveNofomoToken;

        if (unfomosTokenpair.token0() == address(nofomoToken)) {
            (reserveNofomoToken, reserveWETH, ) = IUniswapV2Pair(
                unfomosTokenpair
            ).getReserves();
        } else {
            (reserveWETH, reserveNofomoToken, ) = IUniswapV2Pair(
                unfomosTokenpair
            ).getReserves();
        }
        uint256 amountOut = (uniswapV2Router.getAmountOut(
            buyBackAndBurnAmount,
            reserveWETH,
            reserveNofomoToken
        ) * 99) / 100;
        address[] memory swapPath = new address[](2);
        swapPath[0] = uniswapV2Router.WETH();
        swapPath[1] = address(nofomoToken);
        uint[] memory amounts = uniswapV2Router.swapExactETHForTokens{
            value: buyBackAndBurnAmount
        }(amountOut, swapPath, address(this), block.timestamp + 1000);

        nofomoToken.burn(amounts[1]);
    }

    function getClaimTokenAmount(address receipt) external returns (uint256) {
        uint256 claimAmount = 0;
        uint256 startPeriodId = claimTokenRecord[msg.sender];
        for (uint256 i = startPeriodId; i < currentPeriodId; i++) {
            uint256[] memory tokenIds = mintedNFTs[i][msg.sender];
            claimAmount += periodIdToClaimAmount[i] * tokenIds.length;
        }
        return claimAmount;
    }

    function claimNoFomosToken() external {
        uint256 claimAmount = 0;
        uint256 startPeriodId = claimTokenRecord[msg.sender];
        for (uint256 i = startPeriodId; i < currentPeriodId; i++) {
            uint256[] memory tokenIds = mintedNFTs[i][msg.sender];
            claimAmount += periodIdToClaimAmount[i] * tokenIds.length;
        }
        claimTokenRecord[msg.sender] = currentPeriodId;
        require(claimAmount > 0, "FomosToken: no claimable amount");
        require(
            nofomoToken.transfer(msg.sender, claimAmount),
            "FomosToken: transfer failed"
        );
    }

    function setUnfomoTokenPair(address _unfomoTokenPair) external onlyOwner {
        require(
            _unfomoTokenPair != address(0),
            "FomosToken: unfomoTokenPair is the zero address"
        );
        unfomosTokenpair = IUniswapV2Pair(_unfomoTokenPair);
    }

    function setUniswapV2Router(address _uniswapV2Router) external onlyOwner {
        require(
            _uniswapV2Router != address(0),
            "FomosToken: uniswapV2Router is the zero address"
        );
        uniswapV2Router = IUniswapV2Router(_uniswapV2Router);
    }

    function setFomosPrizePool(address _fomosPrizePool) external onlyOwner {
        require(
            _fomosPrizePool != address(0),
            "FomosToken: fomosPrizePool is the zero address"
        );
        fomoPrizePool = _fomosPrizePool;
    }

    function setInvitationAddress(address _invationAddress) external onlyOwner {
        require(
            _invationAddress != address(0),
            "FomosToken: invationAddress is the zero address"
        );
        invationAddress = _invationAddress;
    }

    function setFomosStakingPool(address _stakingPool) external onlyOwner {
        require(
            _stakingPool != address(0),
            "FomosToken: stakingPool is the zero address"
        );
        stakingPool = _stakingPool;
    }

    function stopMintingPeriod() internal {
        isMinting = false;
        IFomosPrizePool(fomoPrizePool).endMintingPeriod(
            currentPeriodId,
            currentStoredBlock,
            msg.sender
        );
    }

    function changeMintingStatus() public onlyOwner {
        isMinting = !isMinting;
    }
}
