import "./IHiddenFolderCore.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

contract HiddenFolderAuction is AccessControl, ReentrancyGuard {
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    using Address for address payable;

    IHiddenFolderCore hiddenFolderCoreContract;

    uint256 public constant MIN_BID_INCREMENT = 1e15; // 0.001 ETH in wei
    uint256 public constant TOKEN_ID = 0;

    uint256 public auctionStartTimestamp; // Unix timestamp in seconds
    uint256 public auctionEndTimestamp; // Unix timestamp in seconds
    uint256 public highestBidValue;
    address public highestBidder;
    uint256 public amountParticipantsReceive;

    address[] public openCallParticipants;
    mapping(address => uint256) public balanceOwed;
    mapping(address => uint256) public bids;

    event BidPlaced(uint256 bidValue, address indexed bidder);
    event BidRefunded(uint256 bidValue, address indexed bidder);
    event AuctionSettled(uint256 indexed tokenId);
    event TransferFailed(address indexed participantAddress, uint256 value);

    constructor(address payable hiddenFolderCoreContractAddress) {
        hiddenFolderCoreContract = IHiddenFolderCore(hiddenFolderCoreContractAddress);
        _grantRole(ADMIN_ROLE, msg.sender);
    }

    // Function to add open call participant wallets
    function addParticipants(address[] calldata participantAddresses) external onlyRole(ADMIN_ROLE) {
        for (uint i = 0; i < participantAddresses.length; i++) {
            openCallParticipants.push(participantAddresses[i]);
        }
    }

    // Function to remove open call participant wallet
    function removeParticipant(address participantToRemove) external onlyRole(ADMIN_ROLE) {
        int256 participantIndex = -1;
        for (uint i = 0; i < openCallParticipants.length; i++) {
            if (openCallParticipants[i] == participantToRemove) {
                participantIndex = int256(i);
                break;
            }
        }

        if (participantIndex >= 0) {
            openCallParticipants[uint256(participantIndex)] = openCallParticipants[openCallParticipants.length - 1];
            openCallParticipants.pop();
        } else {
            revert("Participant not found");
        }
    }

    function placeBid() external payable nonReentrant {
        require(auctionStartTimestamp != 0 && block.timestamp >= auctionStartTimestamp, "Auction not live yet");
        require(block.timestamp <= auctionEndTimestamp, "Auction is over");

        uint256 minRequiredBid = highestBidValue + MIN_BID_INCREMENT;
        require(msg.value >= minRequiredBid, "Bid too low");

        uint256 refundAmount = highestBidValue;
        address refundAddress = highestBidder;
        // Update the auction's state
        highestBidValue = msg.value;
        highestBidder = msg.sender;
        // Refund the previous highest bidder
        if (refundAmount != 0) {
            (bool success, ) = payable(refundAddress).call{value: refundAmount}("");
            require(success, "Failed to refund previous highest bidder");
            emit BidRefunded(refundAmount, refundAddress);
        }
        // Emit an event for the new bid
        emit BidPlaced(msg.value, msg.sender);
    }

    function configureAuction(uint256 _auctionTimestampStart) external onlyRole(ADMIN_ROLE) {
        require(block.timestamp <= _auctionTimestampStart, "Choose a future timestamp");
        require(auctionStartTimestamp == 0, "Auction already configured");

        auctionStartTimestamp = _auctionTimestampStart;
        auctionEndTimestamp = _auctionTimestampStart + 3 days;
    }

    function settleAuction() external onlyRole(ADMIN_ROLE) {
        require(
            auctionEndTimestamp != 0 && block.timestamp > auctionEndTimestamp,
            "Auction not over yet"
        );
        require(highestBidder != address(0), "No winning bidder");
        require(hiddenFolderCoreContract.auctionContractAddress() == address(this), 
                "HiddenFolderAuction contract is not authorized");
        require(hiddenFolderCoreContract.ownerOf(0) == msg.sender, "Caller does not own the token");

        // Transfer the token to the winning bidder
        try hiddenFolderCoreContract.safeTransferFrom(msg.sender, highestBidder, 0) {
            emit AuctionSettled(0);
        } catch {
            revert("Token transfer failed");
        }
    }

    function calculateFinalDistributionAmount() external onlyRole(ADMIN_ROLE) nonReentrant {
        require(openCallParticipants.length > 0, "Participant list is empty");
        uint256 totalBalance = address(this).balance;
        uint256 amountPerParticipant = totalBalance / openCallParticipants.length;
        require(amountPerParticipant > 0, "Not enough balance to distribute");

        amountParticipantsReceive = amountPerParticipant;

        for (uint256 i = 0; i < openCallParticipants.length; i++) {
            balanceOwed[openCallParticipants[i]] = amountPerParticipant;
        }
    }

    function distributeAuctionFunds() external onlyRole(ADMIN_ROLE) nonReentrant {
        require(openCallParticipants.length > 0, "No participants");

        for (uint i = 0; i < openCallParticipants.length; i++) {
            uint256 balanceToDistribute = balanceOwed[openCallParticipants[i]];
            if (balanceToDistribute > 0) {
                (bool success, ) = payable(openCallParticipants[i]).call{value: balanceToDistribute}("");
                if (success) {
                    balanceOwed[openCallParticipants[i]] = 0;
                } else {
                    emit TransferFailed(openCallParticipants[i], balanceToDistribute);
                }
            }
        }
    }

    // Fallback to allow admin to withdraw
    function withdraw() external onlyRole(ADMIN_ROLE) {
        uint256 balance = address(this).balance;
        require(balance > 0, "No funds to withdraw");

        (bool success, ) = msg.sender.call{value: balance}("");
        require(success, "Transfer failed.");
    }

    receive() external payable {}
}
