    "contracts/IsekaiBattleStake.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.17;

//v0.0.2

import {AccessControlEnumerable} from '@openzeppelin/contracts/access/AccessControlEnumerable.sol';
import '@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol';
import '@openzeppelin/contracts/security/ReentrancyGuard.sol';
import '@openzeppelin/contracts/utils/Context.sol';
import '@openzeppelin/contracts/utils/math/Math.sol';
import {IsekaiBattleWeapon} from './IsekaiBattleWeapon.sol';
import {IsekaiBattleArmor} from './IsekaiBattleArmor.sol';
import {IsekaiBattleBag} from './IsekaiBattleBag.sol';
import './interface/IIsekaiBattleSeeds.sol';
import {IsekaiBattle} from './IsekaiBattle.sol';
import {Random} from './Random.sol';

contract IsekaiBattleStake is IERC721Receiver, ReentrancyGuard, Context, AccessControlEnumerable {
    bytes32 public constant INFO_SETTER_ROLE = keccak256('INFO_SETTER_ROLE');

    //events
    event Stake(address indexed user, uint64 regionId, uint256[] tokenId);
    event Unstake(address indexed user, uint64 regionId, uint256[] tokenId);
    event ClaimWeapons(address indexed user, uint64 regionId, uint256[] detailsCount);
    event ClaimArmors(address indexed user, uint64 regionId, uint256[] detailsCount);
    event ClaimSeed(address indexed user, uint64 regionId, uint256 tokenId);
    event ClaimComplete(address indexed user);

    struct ClaimInfo {
        uint256 tokenId;
        uint256[] ratio;
    }

    struct RegionInfo {
        uint256[] WeaponCounts;
        uint256[] ArmorCounts;
        uint256[] SeedPers;
        ClaimInfo[] WeaponDetails;
        ClaimInfo[] ArmorDetails;
        uint256 WeaponInterval;
        uint256 ArmorInterval;
        uint256 SeedInterval;
        uint256 MaxStakingCharacters;
        bool active;
    }

    struct StakingRequest {
        uint64 regionId;
        uint256[] tokenIds;
    }

    struct StakingInfo {
        uint256 claimTimes;
        uint256 stakingCharactorCounts;
        uint64[] stakingRegionIds;
    }

    struct ActionCount {
        uint64 claim;
        uint64 claimPlus;
        uint64 stake;
        uint64 wpnClaim;
        uint64 armClaim;
        uint64 seedClaim;
        uint64 wpn;
        uint64 arm;
        uint64 seed;
    }

    RegionInfo[] public regionInfos;
    mapping(address => mapping(uint64 => uint256[])) public stakingTokenIds;
    mapping(address => StakingInfo) public stakingInfos;
    mapping(uint256 => address) public tokenOwners;
    mapping(address => ActionCount) public actionCounts;

    IsekaiBattle public immutable ISB;
    IIsekaiBattleSeeds public immutable SED;
    IsekaiBattleArmor public immutable AMR;
    IsekaiBattleWeapon public immutable WPN;
    IsekaiBattleBag public immutable BAG;
    Random public immutable RDM;

    uint256 public maxStakingCharacters = 15;
    uint256 public minGetSeedCharacters = 3;

    constructor(
        IsekaiBattle _ISB,
        IIsekaiBattleSeeds _SED,
        IsekaiBattleArmor _AMR,
        IsekaiBattleWeapon _WPN,
        IsekaiBattleBag _BAG,
        Random _RDM
    ) {
        ISB = _ISB;
        SED = _SED;
        AMR = _AMR;
        WPN = _WPN;
        BAG = _BAG;
        RDM = _RDM;
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(INFO_SETTER_ROLE, _msgSender());
    }

    function addRegionInfo(RegionInfo memory info) public virtual onlyRole(INFO_SETTER_ROLE) {
        regionInfos.push(info);
    }

    function setRegionInfo(uint256 index, RegionInfo memory info) public virtual onlyRole(INFO_SETTER_ROLE) {
        regionInfos[index] = info;
    }

    function setMaxStakingCharacters(uint256 value) public virtual onlyRole(INFO_SETTER_ROLE) {
        maxStakingCharacters = value;
    }

    function setMinGetSeedCharacters(uint256 value) public virtual onlyRole(INFO_SETTER_ROLE) {
        minGetSeedCharacters = value;
    }

    function getRegionInfosLength() public view virtual returns (uint256) {
        return regionInfos.length;
    }

    function stakingTokenIdsLength(address account, uint64 regionId) public view virtual returns (uint256) {
        return stakingTokenIds[account][regionId].length;
    }

    function stake(StakingRequest[] memory _stakingInfo) public virtual {
        require(ISB.isApprovedForAll(_msgSender(), address(this)), 'not approved');
        actionCounts[_msgSender()].stake++;

        if (stakingInfos[_msgSender()].stakingCharactorCounts > 0) {
            claim();
            for (uint256 i = 0; i < stakingInfos[_msgSender()].stakingRegionIds.length; i++) {
                _unstake(stakingTokenIds[_msgSender()][stakingInfos[_msgSender()].stakingRegionIds[i]]);
                emit Unstake(
                    _msgSender(),
                    stakingInfos[_msgSender()].stakingRegionIds[i],
                    stakingTokenIds[_msgSender()][stakingInfos[_msgSender()].stakingRegionIds[i]]
                );
            }
        }

        delete stakingInfos[_msgSender()].stakingRegionIds;
        uint256 allTokenCount = 0;

        for (uint256 i = 0; i < _stakingInfo.length; i++) {
            allTokenCount += _stakingInfo[i].tokenIds.length;
            require(allTokenCount <= maxStakingCharacters, 'max character');
            for (uint256 j = 0; j < _stakingInfo[i].tokenIds.length; j++) {
                ISB.safeTransferFrom(_msgSender(), address(this), _stakingInfo[i].tokenIds[j]);
            }
            stakingInfos[_msgSender()].stakingRegionIds.push(_stakingInfo[i].regionId);
            stakingTokenIds[_msgSender()][_stakingInfo[i].regionId] = _stakingInfo[i].tokenIds;
            emit Stake(_msgSender(), _stakingInfo[i].regionId, _stakingInfo[i].tokenIds);
        }
        stakingInfos[_msgSender()].stakingCharactorCounts = allTokenCount;
        stakingInfos[_msgSender()].claimTimes = block.timestamp;
    }

    function claim() public virtual nonReentrant {
        require(stakingInfos[_msgSender()].stakingRegionIds.length > 0, 'not stake');
        uint256 elapsed = block.timestamp - stakingInfos[_msgSender()].claimTimes;
        uint256 stakingCharactorCount = stakingInfos[_msgSender()].stakingCharactorCounts;
        ActionCount memory origin = actionCounts[_msgSender()];

        for (uint256 i = 0; i < stakingInfos[_msgSender()].stakingRegionIds.length; i++) {
            uint64 regionId = stakingInfos[_msgSender()].stakingRegionIds[i];
            RegionInfo memory targetRegionInfo = regionInfos[regionId];
            if (!targetRegionInfo.active) continue;
            uint256[] memory tokenIds = stakingTokenIds[_msgSender()][regionId];
            uint256 ratioIndex = Math.min(tokenIds.length, 5);
            (uint256 weaponCount, uint256 armorCount, , , , ) = getEstimate();
            uint256 seedPer = (elapsed > targetRegionInfo.SeedInterval)
                ? targetRegionInfo.SeedPers[stakingCharactorCount]
                : 0;
            stakingInfos[_msgSender()].claimTimes = block.timestamp;

            if (
                (weaponCount > 0 || armorCount > 0 || seedPer > 0) &&
                origin.claimPlus == actionCounts[_msgSender()].claimPlus
            ) {
                actionCounts[_msgSender()].claimPlus++;
            }

            if (weaponCount > 0) {
                uint256[] memory detailsCount = new uint256[](targetRegionInfo.WeaponDetails.length);
                for (uint256 j = 0; j < weaponCount; j++) {
                    uint256 randomNumber = RDM.getRandomNumber() % 1000000;
                    uint256 ratioCount = 0;
                    for (uint256 k = 0; k < targetRegionInfo.WeaponDetails.length; k++) {
                        ratioCount += targetRegionInfo.WeaponDetails[k].ratio[ratioIndex];
                        if (ratioCount > randomNumber) {
                            detailsCount[k]++;
                            break;
                        }
                    }
                }
                for (uint256 j = 0; j < detailsCount.length; j++) {
                    if (detailsCount[j] > 0) {
                        WPN.mint(_msgSender(), targetRegionInfo.WeaponDetails[j].tokenId, detailsCount[j], '');
                    }
                }
                if (origin.wpnClaim == actionCounts[_msgSender()].wpnClaim) {
                    actionCounts[_msgSender()].wpnClaim++;
                }
                actionCounts[_msgSender()].wpn += uint64(weaponCount);
                emit ClaimWeapons(_msgSender(), regionId, detailsCount);
            }
            if (armorCount > 0) {
                uint256[] memory detailsCount = new uint256[](targetRegionInfo.ArmorDetails.length);
                for (uint256 j = 0; j < armorCount; j++) {
                    uint256 randomNumber = RDM.getRandomNumber() % 1000000;
                    uint256 ratioCount = 0;
                    for (uint256 k = 0; k < targetRegionInfo.ArmorDetails.length; k++) {
                        ratioCount += targetRegionInfo.ArmorDetails[k].ratio[ratioIndex];
                        if (ratioCount > randomNumber) {
                            detailsCount[k]++;
                            break;
                        }
                    }
                }
                for (uint256 j = 0; j < detailsCount.length; j++) {
                    if (detailsCount[j] > 0) {
                        AMR.mint(_msgSender(), targetRegionInfo.ArmorDetails[j].tokenId, detailsCount[j], '');
                    }
                }
                if (origin.armClaim == actionCounts[_msgSender()].armClaim) {
                    actionCounts[_msgSender()].armClaim++;
                }
                actionCounts[_msgSender()].arm += uint64(armorCount);
                emit ClaimArmors(_msgSender(), regionId, detailsCount);
            }
            if (stakingCharactorCount >= minGetSeedCharacters && seedPer > 0) {
                uint256 length = ISB.getStatusMastersLength();
                for (uint256 j = 0; j < tokenIds.length; j++) {
                    uint256 random = RDM.getRandomNumber() % 1000000;
                    if (random < seedPer) {
                        SED.mint(_msgSender(), (random % length) * 10, 1, '');
                        actionCounts[_msgSender()].seed++;
                        if (origin.seedClaim == actionCounts[_msgSender()].seedClaim) {
                            actionCounts[_msgSender()].seedClaim++;
                        }
                        emit ClaimSeed(_msgSender(), regionId, (random % length) * 10);
                    }
                }
            }
        }
        actionCounts[_msgSender()].claim++;
        emit ClaimComplete(_msgSender());
    }

    function getEstimate()
        public
        view
        virtual
        returns (
            uint256,
            uint256,
            bool,
            bool,
            uint256,
            uint256
        )
    {
        uint256 weaponCount = 0;
        uint256 armorCount = 0;
        uint256 stakingCharactorCount = stakingInfos[_msgSender()].stakingCharactorCounts;
        uint256 elapsed = block.timestamp - stakingInfos[_msgSender()].claimTimes;
        uint256 wpnBagCount = BAG.getWpnBagCount(_msgSender());
        uint256 armBagCount = BAG.getArmBagCount(_msgSender());
        for (uint256 i = 0; i < stakingInfos[_msgSender()].stakingRegionIds.length; i++) {
            RegionInfo memory targetRegionInfo = regionInfos[stakingInfos[_msgSender()].stakingRegionIds[i]];
            if (!targetRegionInfo.active) continue;
            weaponCount +=
                ((elapsed / targetRegionInfo.WeaponInterval) * targetRegionInfo.WeaponCounts[stakingCharactorCount]) /
                10000;
            armorCount +=
                ((elapsed / targetRegionInfo.ArmorInterval) * targetRegionInfo.ArmorCounts[stakingCharactorCount]) /
                10000;
            if (
                WPN.balanceOfAll(_msgSender()) + weaponCount > wpnBagCount &&
                AMR.balanceOfAll(_msgSender()) + armorCount > armBagCount
            ) {
                break;
            }
        }
        return (
            WPN.balanceOfAll(_msgSender()) + weaponCount > wpnBagCount
                ? (wpnBagCount > WPN.balanceOfAll(_msgSender()) ? wpnBagCount - WPN.balanceOfAll(_msgSender()) : 0)
                : weaponCount,
            AMR.balanceOfAll(_msgSender()) + armorCount > armBagCount
                ? (armBagCount > AMR.balanceOfAll(_msgSender()) ? armBagCount - AMR.balanceOfAll(_msgSender()) : 0)
                : armorCount,
            WPN.balanceOfAll(_msgSender()) + weaponCount > wpnBagCount,
            AMR.balanceOfAll(_msgSender()) + armorCount > armBagCount,
            weaponCount,
            armorCount
        );
    }

    function unstake(uint256[] memory tokenIds) external virtual onlyRole(DEFAULT_ADMIN_ROLE) {
        for (uint256 i = 0; i < tokenIds.length; i++) {
            address owner = tokenOwners[tokenIds[i]];
            tokenOwners[tokenIds[i]] = address(0);
            ISB.safeTransferFrom(address(this), owner, tokenIds[i]);
        }
    }

    function _unstake(uint256[] memory tokenIds) internal virtual {
        for (uint256 i = 0; i < tokenIds.length; i++) {
            require(tokenOwners[tokenIds[i]] == _msgSender());
            tokenOwners[tokenIds[i]] = address(0);
            ISB.safeTransferFrom(address(this), _msgSender(), tokenIds[i]);
        }
    }

    function onERC721Received(
        address operator,
        address from,
        uint256 tokenId,
        bytes memory
    ) public virtual override returns (bytes4) {
        require(address(this) == operator, 'not me');
        tokenOwners[tokenId] = from;
        return this.onERC721Received.selector;
    }
}
"
    },
    "contracts/IsekaiBattleStatusManagerV2.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.17;

import '@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol';
import {AccessControlEnumerableUpgradeable} from '@openzeppelin/contracts-upgradeable/access/AccessControlEnumerableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/utils/ContextUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/utils/math/MathUpgradeable.sol';

import {IsekaiBattle} from './IsekaiBattle.sol';
import {IsekaiBattleSeeds} from './IsekaiBattleSeeds.sol';
import {IsekaiBattleMagicCircle} from './IsekaiBattleMagicCircle.sol';
import {Random} from './Random.sol';
import {Staking} from './Staking.sol';
import {ISGData, IISBStaticData} from './extensions/ISGData.sol';
import {IIsekaiBattleStatusManager} from './interface/IIsekaiBattleStatusManager.sol';
import {IsekaiBattleStake} from './IsekaiBattleStake.sol';

contract IsekaiBattleStatusManagerV2 is
    Initializable,
    ReentrancyGuardUpgradeable,
    ContextUpgradeable,
    PausableUpgradeable,
    AccessControlEnumerableUpgradeable,
    IIsekaiBattleStatusManager
{
    address payable public withdrawAddress;

    IsekaiBattle public ISB;
    Staking public STK;
    IsekaiBattleSeeds public SED;
    IsekaiBattleMagicCircle public MGC;
    Random public RDM;

    uint256 public maxSeedLength;
    uint256 public useSeedGen0Cost;
    uint256 public useSeedGen05Cost;
    uint256 public useSeedGen1Cost;

    uint256 public mixSeedGen0Cost;
    uint256 public mixSeedGen05Cost;
    uint256 public mixSeedGen1Cost;

    uint128[2][] public magicCircleExtraRatio;

    IsekaiBattleStake[] public oldStakes;

    function initialize(
        IsekaiBattle _ISB,
        Staking _STK,
        IsekaiBattleSeeds _SED,
        IsekaiBattleMagicCircle _MGC,
        Random _RDM
    ) public initializer {
        __ReentrancyGuard_init();
        __Context_init();
        __Pausable_init();
        __AccessControlEnumerable_init();

        ISB = _ISB;
        STK = _STK;
        SED = _SED;
        MGC = _MGC;
        RDM = _RDM;

        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());

        withdrawAddress = payable(0xbbaF7550c32634f22E989252CD9070b38eFABa42);

        maxSeedLength = 10;
        useSeedGen0Cost = 0.0045 ether;
        useSeedGen05Cost = 0.00475 ether;
        useSeedGen1Cost = 0.005 ether;

        mixSeedGen0Cost = 0.009 ether;
        mixSeedGen05Cost = 0.0095 ether;
        mixSeedGen1Cost = 0.01 ether;

        //      +2,   +1
        // 0:  10%, 20%
        // 1:  20%, 30%
        // 2:  30%, 40%
        // 3:  40%, 50%
        // 4:  50%, 50%
        magicCircleExtraRatio = [
            [uint128(100000), uint128(200000)],
            [uint128(200000), uint128(300000)],
            [uint128(300000), uint128(400000)],
            [uint128(400000), uint128(500000)],
            [uint128(500000), uint128(500000)]
        ];
    }

    function oldStakesLength() external view returns (uint256) {
        return oldStakes.length;
    }

    function setOldStakes(IsekaiBattleStake[] calldata _oldStakes) external onlyRole(DEFAULT_ADMIN_ROLE) {
        oldStakes = _oldStakes;
    }

    function isTokenOwnedBy(uint256 tokenId, address addr) internal view returns (bool) {
        if (ISB.ownerOf(tokenId) == addr || STK.tokenOwners(tokenId) == addr) {
            return true;
        }
        for (uint256 i = 0; i < oldStakes.length; i++) {
            if ((oldStakes[i].tokenOwners)(tokenId) == addr) {
                return true;
            }
        }
        return false;
    }

    function isOwnedToken(uint256 tokenId) external view returns (bool) {
        return isTokenOwnedBy(tokenId, _msgSender());
    }

    function setMaxSeedLength(uint256 _maxSeedLength) external override onlyRole(DEFAULT_ADMIN_ROLE) {
        maxSeedLength = _maxSeedLength;
        emit MaxSeedLengthChanged(_msgSender(), _maxSeedLength);
    }

    function setUseSeedCosts(
        uint256 _useSeedGen0Cost,
        uint256 _useSeedGen05Cost,
        uint256 _useSeedGen1Cost
    ) external override onlyRole(DEFAULT_ADMIN_ROLE) {
        useSeedGen0Cost = _useSeedGen0Cost;
        useSeedGen05Cost = _useSeedGen05Cost;
        useSeedGen1Cost = _useSeedGen1Cost;
        emit UseSeedGasCostsChanged(_msgSender(), _useSeedGen0Cost, _useSeedGen05Cost, _useSeedGen1Cost);
    }

    function setMixSeedCosts(
        uint256 _mixSeedGen0Cost,
        uint256 _mixSeedGen05Cost,
        uint256 _mixSeedGen1Cost
    ) external override onlyRole(DEFAULT_ADMIN_ROLE) {
        mixSeedGen0Cost = _mixSeedGen0Cost;
        mixSeedGen05Cost = _mixSeedGen05Cost;
        mixSeedGen1Cost = _mixSeedGen1Cost;
        emit MixSeedGasCostsChanged(_msgSender(), _mixSeedGen0Cost, _mixSeedGen05Cost, _mixSeedGen1Cost);
    }

    function setMagicCircleExtraRatio(uint128[2][] memory _magicCircleExtraRatio)
        external
        override
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        magicCircleExtraRatio = _magicCircleExtraRatio;
        emit MagicCircleExtraRatioChanged(_msgSender());
    }

    function getUseSeedPrice(uint256 tokenId) external view override returns (uint256) {
        IISBStaticData.Generation gen = ISB.getGenneration(tokenId);
        if (gen == IISBStaticData.Generation.GEN0) {
            return useSeedGen0Cost;
        } else if (gen == IISBStaticData.Generation.GEN05) {
            return useSeedGen05Cost;
        } else {
            return useSeedGen1Cost;
        }
    }

    function useSeed(uint256 tokenId, uint256 seedId) external payable override nonReentrant whenNotPaused {
        require(isTokenOwnedBy(tokenId, _msgSender()), 'wrong token owner');
        require(SED.balanceOf(_msgSender(), seedId) > 0, 'insufficient balance of seed');

        IISBStaticData.Status[] memory seedHistory = ISB.getSeedHistory(tokenId);
        require(seedHistory.length < maxSeedLength, 'seedHistory is above level');

        IISBStaticData.Generation gen = ISB.getGenneration(tokenId);
        if (gen == IISBStaticData.Generation.GEN0) {
            require(msg.value == useSeedGen0Cost, 'need GEN0 cost');
        } else if (gen == IISBStaticData.Generation.GEN05) {
            require(msg.value == useSeedGen05Cost, 'need GEN0.5 cost');
        } else {
            require(msg.value == useSeedGen1Cost, 'need GEN1 cost');
        }

        (uint256 statusId, uint256 seedLevel, ) = SED.SeedInfos(seedId);
        IISBStaticData.Status memory seed = IISBStaticData.Status(statusId, uint16(seedLevel));

        ISB.setLevel(tokenId, 10);
        ISB.addSeed(tokenId, seed);
        SED.burnAdmin(_msgSender(), seedId, 1);

        emit UseSeed(_msgSender(), address(ISB), tokenId, seedId);
    }

    function getMixSeedPrice(uint256 tokenId) external view override returns (uint256) {
        IISBStaticData.Generation gen = ISB.getGenneration(tokenId);
        if (gen == IISBStaticData.Generation.GEN0) {
            return mixSeedGen0Cost;
        } else if (gen == IISBStaticData.Generation.GEN05) {
            return mixSeedGen05Cost;
        } else {
            return mixSeedGen1Cost;
        }
    }

    function getMagicCircleExtraRatioLength() external view override returns (uint256) {
        return magicCircleExtraRatio.length;
    }

    function _getMagicCircleExtraRatioForUser(address user) internal view virtual returns (uint128, uint128) {
        uint256 i = MathUpgradeable.min(MGC.balanceOf(user, 0), 4);
        return (magicCircleExtraRatio[i][0], magicCircleExtraRatio[i][1]);
    }

    function getMagicCircleExtraRatioForUser(address user) external view override returns (uint128, uint128) {
        return _getMagicCircleExtraRatioForUser(user);
    }

    function calcExtraSeedNum(address user) internal virtual returns (uint256) {
        (uint128 gen0Ratio, uint128 gen1Ratio) = _getMagicCircleExtraRatioForUser(user);
        uint256 random = RDM.getRandomNumber() % 1000000;
        uint256 ratio = uint256(gen0Ratio);
        if (random < ratio) {
            return 2;
        } else if (random < ratio + uint256(gen1Ratio)) {
            return 1;
        } else {
            return 0;
        }
    }

    function mixSeed(
        uint256 tokenId,
        uint256 seedId,
        uint256 mixNum
    ) external payable override nonReentrant whenNotPaused {
        require(isTokenOwnedBy(tokenId, _msgSender()), 'wrong token owner');
        require(SED.balanceOf(_msgSender(), seedId) >= mixNum, 'insufficient balance of seed');

        (, uint256 seedLevel, ) = SED.SeedInfos(seedId);
        require(seedLevel == 1, 'wrong seed level');
        require(mixNum >= 2, 'less than minimum mixNum');
        require(mixNum <= 8, 'over than maximum mixNum');

        IISBStaticData.Generation gen = ISB.getGenneration(tokenId);
        if (gen == IISBStaticData.Generation.GEN0) {
            require(msg.value == mixSeedGen0Cost, 'need GEN0 cost');
        } else if (gen == IISBStaticData.Generation.GEN05) {
            require(msg.value == mixSeedGen05Cost, 'need GEN0.5 cost');
        } else {
            require(msg.value == mixSeedGen1Cost, 'need GEN1 cost');
        }

        uint256 mixedSeedId = seedId + MathUpgradeable.min(mixNum - 1 + calcExtraSeedNum(_msgSender()), 9);
        SED.mint(_msgSender(), mixedSeedId, 1, '');
        SED.burnAdmin(_msgSender(), seedId, mixNum);

        emit MixSeed(_msgSender(), address(ISB), tokenId, seedId, mixNum, mixedSeedId);
    }

    function pause() external override whenNotPaused onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    function unpause() external override whenPaused onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    function setWithdrawAddress(address payable _withdrawAddress) external override onlyRole(DEFAULT_ADMIN_ROLE) {
        withdrawAddress = _withdrawAddress;
        emit WithdrawAddressChanged(_msgSender(), withdrawAddress);
    }

    function withdraw() external override onlyRole(DEFAULT_ADMIN_ROLE) {
        uint256 amount = address(this).balance;
        (bool os, ) = withdrawAddress.call{value: address(this).balance}('');
        require(os);
        emit Withdraw(_msgSender(), withdrawAddress, amount);
    }
}
"
    },
    "contracts/IsekaiBattleWeapon.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.17;

import '@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Burnable.sol';
import {AccessControlEnumerable} from '@openzeppelin/contracts/access/AccessControlEnumerable.sol';
import {Ownable} from '@openzeppelin/contracts/access/Ownable.sol';
import './interface/IIsekaiBattleWeapon.sol';
import '@openzeppelin/contracts/utils/Strings.sol';
import '@openzeppelin/contracts/utils/Base64.sol';
import 'operator-filter-registry/src/DefaultOperatorFilterer.sol';

contract IsekaiBattleWeapon is
    ERC1155Burnable,
    AccessControlEnumerable,
    Ownable,
    IIsekaiBattleWeapon,
    DefaultOperatorFilterer
{
    using Strings for uint256;
    bytes32 public constant MINTER_ROLE = keccak256('MINTER_ROLE');
    bytes32 public constant BURNER_ROLE = keccak256('BURNER_ROLE');
    bytes32 public constant INFO_SETTER_ROLE = keccak256('INFO_SETTER_ROLE');
    string public constant name = 'Isekai Battle Weapons';
    string public constant symbol = 'WPN';

    IIsekaiBattle public immutable override ISB;

    WeaponInfo[] public override WeaponInfos;

    constructor(string memory _uri, IIsekaiBattle _ISB) ERC1155(_uri) {
        ISB = _ISB;
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());

        _setupRole(MINTER_ROLE, _msgSender());
        _setupRole(BURNER_ROLE, _msgSender());
        _setupRole(INFO_SETTER_ROLE, _msgSender());
    }

    function mint(
        address to,
        uint256 id,
        uint256 amount,
        bytes memory data
    ) public virtual override onlyRole(MINTER_ROLE) {
        _mint(to, id, amount, data);
    }

    function mintBatch(
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) public virtual override onlyRole(MINTER_ROLE) {
        _mintBatch(to, ids, amounts, data);
    }

    function burnAdmin(
        address account,
        uint256 id,
        uint256 value
    ) public virtual override onlyRole(BURNER_ROLE) {
        _burn(account, id, value);
    }

    function burnBatchAdmin(
        address account,
        uint256[] memory ids,
        uint256[] memory values
    ) public virtual override onlyRole(BURNER_ROLE) {
        _burnBatch(account, ids, values);
    }

    function addWeaponInfo(WeaponInfo memory info) public virtual override onlyRole(INFO_SETTER_ROLE) {
        WeaponInfos.push(info);
    }

    function setWeaponInfo(uint256 index, WeaponInfo memory info) public virtual override onlyRole(INFO_SETTER_ROLE) {
        WeaponInfos[index] = info;
    }

    function getWeaponInfosLength() public virtual override returns (uint256) {
        return WeaponInfos.length;
    }

    function uri(uint256 tokenId) public view virtual override returns (string memory) {
        WeaponInfo memory info = WeaponInfos[tokenId];
        string memory weaponText = ISB.staticData().weaponTypeText(info.weaponType);

        bytes memory dataURI = abi.encodePacked(
            '{"name": "',
            weaponText,
            ' Lv',
            info.level.toString(),
            '","description": "A Weapon used for attacking other players in the fully on-chain game \\"Isekai Battle\\".  \\n  \\nIsekai Battle (https://isekai-battle.xyz/)","image": "',
            info.image,
            '","attributes": [{"trait_type":"Lv","value":',
            info.level.toString(),
            '},{"trait_type":"Type","value":"',
            weaponText,
            '"}]}'
        );

        return string(abi.encodePacked('data:application/json;base64,', Base64.encode(dataURI)));
    }

    function balanceOfAll(address account) public view virtual returns (uint256) {
        uint256 count = 0;
        for (uint256 i = 0; i < WeaponInfos.length; i++) {
            count += balanceOf(account, i);
        }
        return count;
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC1155, AccessControlEnumerable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId,
        uint256 amount,
        bytes memory data
    ) public override onlyAllowedOperator(from) {
        super.safeTransferFrom(from, to, tokenId, amount, data);
    }

    function safeBatchTransferFrom(
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) public virtual override onlyAllowedOperator(from) {
        super.safeBatchTransferFrom(from, to, ids, amounts, data);
    }

    function setApprovalForAll(address operator, bool approved) public override onlyAllowedOperatorApproval(operator) {
        super.setApprovalForAll(operator, approved);
    }
}
"
    },
    "contracts/Random.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.17;

import '@openzeppelin/contracts-upgradeable/access/AccessControlEnumerableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/utils/ContextUpgradeable.sol';
import '@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol';

contract Random is Initializable, ContextUpgradeable, AccessControlEnumerableUpgradeable {
    bytes32 public constant INFO_SETTER_ROLE = keccak256('INFO_SETTER_ROLE');
    uint256 internal nonce;

    struct RandomFeed {
        AggregatorV3Interface rateFeed;
        address balanceFeed;
    }

    RandomFeed public feed;

    function initialize(address _rateFeed, address _balanceFeed) public initializer {
        __AccessControlEnumerable_init();
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(INFO_SETTER_ROLE, _msgSender());
        nonce = 0;
        feed = RandomFeed({
            rateFeed: AggregatorV3Interface(_rateFeed),
            balanceFeed: _balanceFeed
        });
    }

    function setFeed(RandomFeed memory value) public virtual onlyRole(INFO_SETTER_ROLE) {
        feed = value;
    }

    function getRandomNumber() public returns (uint256) {
        return _getRandomNumber();
    }

    function _getRandomNumber() internal returns (uint256) {
        uint256 n1 = feed.balanceFeed.balance;
        (, int256 n2, , , ) = feed.rateFeed.latestRoundData();
        uint256 n3 = block.timestamp;
        uint256 n4 = nonce++;

        return uint256(keccak256(abi.encodePacked(n1, n2, n3, n4)));
    }

    // for test
    function getRandomNumberView(uint256 denominator) public view returns (uint256) {
        return _getRandomNumberView() % denominator;
    }

    function _getRandomNumberView() internal view returns (uint256) {
        uint256 n1 = feed.balanceFeed.balance;
        (, int256 n2, , , ) = feed.rateFeed.latestRoundData();
        uint256 n3 = block.timestamp;
        uint256 n4 = nonce;

        return uint256(keccak256(abi.encodePacked(n1, n2, n3, n4)));
    }

}
"
    },
    "contracts/Staking.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.17;

import {AccessControlEnumerable} from '@openzeppelin/contracts/access/AccessControlEnumerable.sol';
import '@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol';
import '@openzeppelin/contracts/utils/Context.sol';
import {IsekaiBattle} from './IsekaiBattle.sol';

contract Staking is IERC721Receiver, Context, AccessControlEnumerable {
    bytes32 public constant STAKER_ROLE = keccak256('STAKER_ROLE');

    mapping(uint256 => address) public tokenOwners;

    IsekaiBattle public immutable ISB;

    constructor(IsekaiBattle _ISB) {
        ISB = _ISB;
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(STAKER_ROLE, _msgSender());
    }

    function stake(address user, uint256[] memory tokenIds) public virtual onlyRole(STAKER_ROLE) {
        for (uint256 i = 0; i < tokenIds.length; i++) {
            ISB.safeTransferFrom(user, address(this), tokenIds[i]);
        }
    }

    function unstake(uint256[] memory tokenIds) public virtual onlyRole(STAKER_ROLE) {
        for (uint256 i = 0; i < tokenIds.length; i++) {
            address owner = tokenOwners[tokenIds[i]];
            tokenOwners[tokenIds[i]] = address(0);
            ISB.safeTransferFrom(address(this), owner, tokenIds[i]);
        }
    }

    function onERC721Received(
        address operator,
        address from,
        uint256 tokenId,
        bytes memory
    ) public virtual override returns (bytes4) {
        require(address(this) == operator, 'not me');
        tokenOwners[tokenId] = from;
        return this.onERC721Received.selector;
    }
}
"
    },
    "erc721a/contracts/ERC721A.sol": {
      "content": "// SPDX-License-Identifier: MIT
