      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @title ExchangeNFTETH
 * @author ClownPunkX
 * @notice This contract manages the exchange of NFTs with ETH, that allows NFT holders to transfer their NFT to the contract and receive ETH.
 */

/// @custom:security-contact https://clownpunkx.xyz
contract ExchangeNFTETH is Ownable, ERC1155Holder, AccessControl, ReentrancyGuard {
    /// Reference Interface for ERC721
    IERC721 public nftContract;

    /// The amount of ETH to be exchanged with an NFT
    uint256 public payoutAmount;

    /// The timestamp of deployment of the contract
    uint256 public deployTime;

    /// Number of days until the owner can withdraw funds (3 years)
    uint256 public withdrawLimit = 1095 days;

    /// List of NFT contracts valid for this exchnage
    mapping(address => bool) validNFTContracts;

    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    event NFTExchanged(address indexed user, uint256 tokenId, uint256 payoutAmount);
    event PayoutAmountUpdated(uint256 newAmount);
    event ETHWithdrawn(uint256 amount);
    event ERC20Withdrawn(address tokenAddress, uint256 amount);
    event ERC721Withdrawn(address tokenAddress, uint256 tokenId);
    event ERC1155Withdrawn(address tokenAddress, uint256 tokenId, uint256 amount);
    event Received(address, uint);

    constructor(address _nftContract, uint256 _initialPayoutAmount) Ownable(msg.sender) {
        validNFTContracts[_nftContract] = true;
        payoutAmount = _initialPayoutAmount;
        deployTime = block.timestamp;
        _setRoleAdmin(ADMIN_ROLE, DEFAULT_ADMIN_ROLE);
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
    }

    /**
     * @notice Exchanges an NFT (from a valid/authorised) with ETH
     * @param _validNFT is the contract address of ERC721 NFT
     * @param tokenId is the ID of the NFT msg.sender holds, and wants to exchange with ETH
     */
    function exchangeNFT(address _validNFT, uint256 tokenId) external nonReentrant {
        /// Check if the NFT contract is valid/authorised by the Exchange
        require(validNFTContracts[_validNFT] == true, "NFT Not Supported");

        /// Check if the contract has any ETH to offer in exchange
        require(address(this).balance >= payoutAmount, "Not enough ETH in contract");

        nftContract = IERC721(_validNFT);

        /// Transfer the NFT from msg.sender to the owner of the contract
        nftContract.safeTransferFrom(msg.sender, owner(), tokenId);

        /// Pay ETH to message.sender
        payable(msg.sender).transfer(payoutAmount);
        emit NFTExchanged(msg.sender, tokenId, payoutAmount);
    }

    /**
     * @notice Add a valid ERC721 contract to enable the exchange
     * @param _validNFT is the contract address of ERC721 NFT
     */
    function addValidNFTContract(address _validNFT) external onlyOwner onlyAdmin {

        /// Check if the contract is already authorised or not
        require(validNFTContracts[_validNFT] == false, "Contract already in the list");

        validNFTContracts[_validNFT] = true;
    }

    /**
     * @notice Remove a valid ERC721 contract from the list of authorised contracts
     * @param _validNFT is the contract address of ERC721 NFT
     */
    function removeValidContract(address _validNFT) external onlyOwner onlyAdmin{

        /// Check if the contract is already authorised or not
        require(validNFTContracts[_validNFT] == true, "Contract is not in the list");

        validNFTContracts[_validNFT] = false;
    }

    /**
     * @notice Checks if an ERC721 contract is authorised or not
     * @param _validNFT is the contract address of ERC721 NFT
     * @return Returns the boolean 
     */
    function contractValidity(address _validNFT) external view returns (bool) {
        return validNFTContracts[_validNFT];
    }

    /**
     * @notice Set the payout amount (in ETH)
     * @param newAmount to set the amount of ETH to be exchanged
     */
    function setPayoutAmount(uint256 newAmount) external onlyOwner onlyAdmin {
        payoutAmount = newAmount;
        emit PayoutAmountUpdated(newAmount);
    }

    /**
     * @notice Getter of Payout amount
     * @return The payout amount in ETH
     */
    function getPayoutAmount() external view returns (uint256) {
        return payoutAmount;
    }

    /**
     * @notice Withdrawal of ETH from the contract, only to be called by the owner
    */
    function withdrawETH() external nonReentrant onlyOwner onlyAdmin {
        /// Check if the withdrawLimit time is passed or not
        require((block.timestamp - deployTime >= withdrawLimit), "Withdraw limit not over yet");

        payable(owner()).transfer(address(this).balance);
        emit ETHWithdrawn(address(this).balance);
    }

    /**
     * @notice Withdrawal of (any) ERC20 from the contract, only to be called by the owner
     * @param tokenAddress The contract address of ERC20 token
    */
    function withdrawERC20(address tokenAddress) external nonReentrant onlyOwner onlyAdmin {
        IERC20 token = IERC20(tokenAddress);
        uint256 balance = token.balanceOf(address(this));

        /// Check if the balance of the ERC20 token is greater than zero
        require(balance > 0, "No ERC20 tokens to withdraw");

        token.transfer(owner(), balance);
        emit ERC20Withdrawn(tokenAddress, balance);
    }

    /**
     * @notice Withdrawal of (any) ERC721 from the contract, only to be called by the owner
     * @param tokenAddress The contract address of ERC721 token
     * @param tokenId The ID of the NFT needs to be withdrawn
    */
    function withdrawERC721(address tokenAddress, uint256 tokenId) external nonReentrant onlyOwner onlyAdmin {
        IERC721(tokenAddress).safeTransferFrom(address(this), owner(), tokenId);
        emit ERC721Withdrawn(tokenAddress, tokenId);
    }

    /**
     * @notice Withdrawal of (any) ERC1155 from the contract, only to be called by the owner
     * @param tokenAddress The contract address of ERC1155 token
     * @param tokenId The ID of the NFT needs to be withdrawn
     * @param amount The amount of token needs to be withdrawn
    */
    function withdrawERC1155(address tokenAddress, uint256 tokenId, uint256 amount) external nonReentrant onlyOwner onlyAdmin {
        IERC1155(tokenAddress).safeTransferFrom(address(this), owner(), tokenId, amount, "");
        emit ERC1155Withdrawn(tokenAddress, tokenId, amount);
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC1155Holder, AccessControl) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    function onERC721Received(address, address, uint256, bytes calldata) external pure returns (bytes4) {
        return IERC721Receiver.onERC721Received.selector;
    }

    modifier onlyAdmin() {
        require( isAdmin(msg.sender) == true, "Not an admin");
        _;
    }

    function isAdmin(address account) public virtual view returns (bool) {
        return hasRole(ADMIN_ROLE, account);
    }

    /**
     * @notice Adds an account as an admin that can perform onlyAdmin functions.
     * @param account is an address of a new Admin.
     */
    function addAdmin(address account) public virtual onlyOwner {
        grantRole(ADMIN_ROLE, account);
    }

    /**
     * @notice Removes an account as an admin.
     * @param account is an address of the admin to be removed.
     */
    function removeAdmin(address account) public virtual onlyOwner {
        revokeRole(ADMIN_ROLE, account);
    }

    receive() external payable {
        emit Received(msg.sender, msg.value);
    }
}"
