/**
 * @title Shared logic for managing templates and creating contract.
 */
abstract contract ArtFactorySharedTemplate is
  Context,
  Ownable
{
  using Clones for address;

  address public implementation;
  uint32 public templateVersion;

  error NFTCollectionFactorySharedTemplates_Collection_Requires_Symbol();
  error NFTCollectionFactorySharedTemplates_Invalid_Collection_Type();
  error NFTCollectionFactorySharedTemplates_Upgrade_Implementation_Already_Set();
  error NFTCollectionFactorySharedTemplates_Upgrade_Implementation_Not_A_Contract();
  error NFTCollectionFactorySharedTemplates_Upgrade_Inputs_Must_Be_The_Same_Length();

  event PRINT_ADDR(address addr);
  event PRINT_STR(string str);

  /**
   * @notice Emitted when the implementation used
   * @param implementation The new implementation contract address.
   * @param templateVersion The version of the new implementation, auto-incremented.
   */
  event TemplateUpdated(
    address indexed implementation,
    uint256 indexed templateVersion
  );

  /**
   * @notice Allows admins to update a template.
   * @param _implementation The new implementation contract address.
   * @dev New templates will start with version 1, others will auto-increment from their current version.
   */
  function updateTemplate(
    address _implementation
  ) external onlyOwner {
    if (implementation == _implementation) {
        revert NFTCollectionFactorySharedTemplates_Upgrade_Implementation_Already_Set();
    }
    implementation = _implementation;
    templateVersion = ++templateVersion;

    emit TemplateUpdated(implementation, templateVersion);
  }

  /**
   * @notice A helper for creating contract of a given type.
   */
  function _createContract(
    string memory _name,
    string memory _symbol,
    address _creator,
    address _operator,
    uint96 _nonce
  ) internal returns (address contract_, uint256 version_) {
    // This reverts if the NFT was previously created using this implementation version + msg.sender + nonce
    contract_ = Clones.cloneDeterministic(implementation, _getSalt(_creator, _nonce));
    ArtNft(contract_).init(_name, _symbol, _creator, _operator, templateVersion);
    version_ = templateVersion;
  }

  /**
   * @notice Returns the address of an ArtNft given the current
   * implementation version, creator, and nonce.
   * @param creator The creator of the ArtNft.
   * @param nonce An arbitrary value used to allow a creator to mint multiple contracts with a counterfactual address.
   * @return contractAddress_ The address of the contract that would be created by this nonce.
   * @dev This will return the same address whether the ArtNft has already been created or not.
   */
  function predictCollectionAddress(
    address creator,
    uint96 nonce
  ) public view returns (address contractAddress_) {
    contractAddress_ = implementation.predictDeterministicAddress(_getSalt(creator, nonce));
  }

  /**
   * @dev Salt is address + nonce packed.
   */
  function _getSalt(address creator, uint96 nonce) private pure returns (bytes32) {
    return bytes32((uint256(uint160(creator)) << 96) | uint256(nonce));
  }

  /**
   * @notice This empty reserved space is put in place to allow future versions to add new
   * variables without shifting down storage in the inheritance chain.
   * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
   * @dev This mixin is a total of 1,000 slots.
   */
  uint256[999] private __gap;
}

// File: contracts/ArtNft.sol

pragma solidity ^0.8.0;

contract ArtNft is ERC721Upgradeable, CreatorOwnable {
    address private operator;
    uint256 public totalMinted;
    uint256 public version;
    string public baseURI;
    uint256 public burned;
    event BaseURIChanged(string _baseURI);
    event SetOperator(address operator);

    modifier onlyOwnerOrOperator {
        require(msg.sender == owner() || msg.sender == operator, "Owner or artist only");
        _;
    }

    function init(
        string memory _name,
        string memory _symbol,
        address _artist,
        address _operator,
        uint256 _version
    ) initializer external {
        // require(!initialized, "Already Initialized");
        __ERC721_init(_name, _symbol);
        operator = _operator;
        transferOwnership(_artist);
        version = _version;
    }
    
    function mint(address _to) external onlyOwnerOrOperator {
        _mint(_to, ++totalMinted);
    }
    
    function mintBatch(address _to, uint256 _number) external onlyOwnerOrOperator {
        for (uint i; i < _number; i++)
            _mint(_to, ++totalMinted);
    }

    function burn(uint256 tokenId) public {
        require(_isApprovedOrOwner(_msgSender(), tokenId), "ERC721: caller is not token owner or approved");
        _burn(tokenId);
        burned++;
    }

    function setBaseURI(string memory _uri) external {
        require(operator == msg.sender, "!operator");
        baseURI = _uri;
        emit BaseURIChanged(baseURI);
    }

    function _baseURI() internal view override returns (string memory) {
        return baseURI;
    }

    function transferOperator(address newOperator) external {
        require(operator == msg.sender, "!operator");
        require(newOperator != address(0), "!!0");
        operator = newOperator;

        emit SetOperator(operator);
    }

    function transferOwnership(address newOwner) public override onlyOwnerOrOperator {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        _transferOwnership(newOwner);
    }

    function totalSupply() public view returns (uint256) {
        return (totalMinted - burned);
    }
}

