        address receiver;
        uint256 feeBips;
    }
    function setCreatorFee(FeeRecipients[] memory _splitInfo, address _tokenAddress, uint256 _tokenId) external;
    function getCreatorFee(address _tokenAddress, uint256 _tokenId) view external returns (FeeRecipients[] memory);
    function getTradeFeeBips(address _tokenAddress, uint256 _tokenId) view external returns (uint256 sum);
    function settleTradeFee(address _tokenAddress, uint256 _tokenId, address _referral) payable external;
    function withdrawBalance() external;
    function transferWithdrawFee() payable external;
}

interface IArtMarket {
    function listingAsset(address _payTo, address _tokenAddress, uint256 _tokenId, uint256 _price) external;
    function cancelListing(address _tokenAddress, uint256 _tokenId) external;
    function setListingPrice(address _tokenAddress, uint256 _tokenId, uint256 _price) external;
    function buyNow(address _tokenAddress, uint256 _tokenId, address _referral) payable external;
    function buyBatchNow(address[] calldata _tokenAddresses, uint256[] calldata _tokenIds, address[] calldata _referrals) payable external;
    function biddingAsset(address _tokenAddress, uint256 _tokenId, uint256 _expirationTime, address _referral) payable external;
    function cancelBidding(address _tokenAddress, uint256 _tokenId, uint256 _price, uint256 _expirationTime) external;
    function withdrawBalance() external;
}

interface IArtworkFactoryV2 {
    function migrationToV3(address _v3, uint256 _v1From, uint256 _v1To, uint256 _v2From, uint256 _v2To) external;
    function transferOwnership(address newOwner) external;
    function setOperator(address _tokenAddress, address _operator) external;
    function nftList(uint256 index) view external returns (ArtNft);
    function nftAddress(address _artist) view external returns (address nftAddr);
    function createContractAndArtwork(
        string memory _contractName,
        string memory _contractSymbol,
        IFeeManager.FeeRecipients[] memory _creatorFee,
        uint256[] memory _listingPrices
    ) external;
    function createContract(
        string memory _contractName,
