    ) external;
}

pragma solidity ^0.8.0;

contract ArtworkFactoryV3 is Ownable, ArtFactorySharedTemplate {
    IFeeManager public feeManager;
    IArtMarket public artMarket;

    ArtNft[] public nftList;
    mapping(address => address) public nftAddress;
    mapping(address => bool) public operators;

    bool isInitialized;
    string public baseURI; // "https://api.2r2.io/nft/"

    event ContractCreated(address indexed user);
    event ArtworkCreated(address indexed creator, address indexed nftAddress, uint256 nftId);
    event OperatorUpdated(address indexed operator, bool indexed status);
    event NftArtistMigrated(address indexed nftAddress, address oldArtist, address newArtist);
    event Migrated(address nftAddress, address newOperator);
    event catchErr(string _name, string _err);

    modifier onlyOperator() {
        require(operators[msg.sender] == true, "Operator account only");
        _;
    }

    function init(
        address payable _feeManager,
        address _artMarket,
        string memory _baseURI
    ) external {
        require(!isInitialized, "Already initialized");
        isInitialized = true;

        feeManager = IFeeManager(_feeManager);
        artMarket = IArtMarket(_artMarket);
        baseURI = _baseURI;

        _transferOwnership(msg.sender);
    }

    function migration(address _v1, uint256 _v1From, uint256 _v1To, address _v2, uint256 _v2From, uint256 _v2To) external onlyOwner {
        for (uint256 i = _v1From; i <= _v1To; i++) {
            ArtNft nft = IArtworkFactoryV2(_v1).nftList(i);
            nftList.push(nft);
            address artist = nft.owner();
            nftAddress[artist] = address(nft);
        }
        for (uint256 i = _v2From; i <= _v2To; i++) {
            ArtNft nft = IArtworkFactoryV2(_v2).nftList(i);
            nftList.push(nft);
            address artist = nft.owner();
            nftAddress[artist] = address(nft);
        }
    }

    function createContractAndArtwork(
        string memory _contractName,
        string memory _contractSymbol,
        IFeeManager.FeeRecipients[] memory _creatorFee,
        uint256[] memory _listingPrices
    ) external {
        createContract(_contractName, _contractSymbol);
        createArtwork(_creatorFee, _listingPrices);
    }

    function createContract(
        string memory _contractName,
        string memory _contractSymbol
    ) public {
        require(getNftAddress(msg.sender) == address(0), "Your contract has been created");
        uint96 nonce = 0;
        (address nftAddr, ) = _createContract(_contractName, _contractSymbol, address(msg.sender), address(this), nonce);
        ArtNft nft = ArtNft(nftAddr);
        nftAddress[msg.sender] = address(nft);
        nftList.push(nft);
        
        string memory tokenUri = string(abi.encodePacked(baseURI, Strings.toHexString(uint160(address(nft)), 20), "/"));
        nft.setBaseURI(tokenUri);

        emit ContractCreated(msg.sender);
    }

    function createArtwork(
        IFeeManager.FeeRecipients[] memory _creatorFee,
        uint256[] memory _listingPrices   
    ) public {
        _createArtwork(_creatorFee, _listingPrices, msg.sender, msg.sender);
    }

    function createArtwork(
        IFeeManager.FeeRecipients[] memory _creatorFee,
        uint256[] memory _listingPrices,
        address _creator,
        address _receiver
    ) external onlyOperator {
        _createArtwork(_creatorFee, _listingPrices, _creator, _receiver);
    }

    function _createArtwork(
        IFeeManager.FeeRecipients[] memory _creatorFee,
        uint256[] memory _listingPrices,
        address _creator,
        address _receiver
    ) internal {
        ArtNft nft;
        require(getNftAddress(_creator) != address(0), "Create your contract first");
        nft = ArtNft(getNftAddress(_creator));

        for (uint i = 0; i < _listingPrices.length; i++) {
            // listing
            if (_listingPrices[i] > 0) {
                require(address(artMarket) != address(0), "Marketplace contract is not set");
                nft.mint(address(this));
                nft.approve(address(artMarket), nft.totalSupply());
                artMarket.listingAsset(_receiver, address(nft), nft.totalSupply(), _listingPrices[i]);
            } else {
                nft.mint(address(_receiver));
            }

            emit ArtworkCreated(_creator, address(nft), nft.totalSupply());

            // Creator fee 설정
            if (_creatorFee.length > 0) {
                require(address(feeManager) != address(0), "FeeManager contract is not set");
                feeManager.setCreatorFee(_creatorFee, address(nft), nft.totalSupply());
            }
        }
    }

    function getNftAddress(address _artist) public view returns (address) {
        if (nftAddress[_artist] != address(0))
            return nftAddress[_artist];
        return address(0);
    }

    function setFeeManager(address payable _feeManager) external onlyOwner {
        feeManager = IFeeManager(_feeManager);
    }

    function setArtMarket(address _artMarket) external onlyOwner {
        artMarket = IArtMarket(_artMarket);
    }

    function setArtNftOperator(address _tokenAddress, address _operator) public onlyOwner {
        ArtNft nft = ArtNft(_tokenAddress);
        nft.transferOperator(_operator);
    }

    function setNftBaseUri(string memory _baseURI) external onlyOwner {
        baseURI = _baseURI;
        ArtNft nft;
        for(uint i; i < nftList.length; i++) {
            nft = nftList[i];
            string memory tokenUri = string(abi.encodePacked(baseURI, Strings.toHexString(uint160(address(nft)), 20), "/"));
            nft.setBaseURI(tokenUri);
        }
    }

    function setArtistOfNft(address _oldArtist, address _newArtist) external onlyOwner {
        address nftAddr = getNftAddress(_oldArtist);
        Ownable(nftAddr).transferOwnership(_newArtist);
        nftAddress[_oldArtist] = address(0);
        nftAddress[_newArtist] = nftAddr;

        emit NftArtistMigrated(nftAddr, _oldArtist, _newArtist);
    }

    function updateOperator(address _operator, bool _status) external onlyOwner {
        operators[_operator] = _status;
        emit OperatorUpdated(_operator, _status);
    }
}
