contract ZEROEXENN is ERC20, Ownable {

    INonfungiblePositionManager positionManager = INonfungiblePositionManager(0xC36442b4a4522E871399CD717aBDD847Ab11FE88);
    address constant WETH = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2; // mainnet
    address devWallet = msg.sender;
    address teamWallet = 0xD5386B953B0a60718262C58547B96E0c3B656A73;

    uint public liquiditySupply = 940_000 * 10 ** decimals();
    uint immutable teamSupply = 60_000 * 10 ** decimals();

    uint24 fee = 10000;
    uint160 sqrtPriceX96;
    int24 minTick;
    int24 maxTick;
    address public lppair;
    address token0;
    address token1;
    uint amount0Desired;
    uint amount1Desired;
    uint256 lpTokenId;

    constructor() ERC20("0xN", "0XN") {
        _mint(address(this), liquiditySupply);
        _mint(teamWallet, teamSupply);
        initializePrice();
        lppair = positionManager.createAndInitializePoolIfNecessary(token0, token1, fee, sqrtPriceX96);
    }

    function initializePrice() private {
        if (address(this) < WETH) {
            token0 = address(this);
            token1 = WETH;
            sqrtPriceX96 = 214378254239014451612221440;
        } else {
            token0 = WETH;
            token1 = address(this);
            sqrtPriceX96 = 29280496558145393920261350227968;
        }
    }

    function setRanges(int24 _mintick, int24 _maxtick, uint _amount0, uint _amount1) external onlyOwner {
        minTick = _mintick;
        maxTick = _maxtick;
        amount0Desired = _amount0;
        amount1Desired = _amount1;
    }

    function updateWallets(address _devWallet, address _teamWallet) external onlyOwner {
        devWallet = _devWallet;
        teamWallet = _teamWallet;
    }

     function addLiquidity() external {
         require(msg.sender == devWallet || msg.sender == teamWallet, "Unauthorized to add liquidity");
                IERC20(address(this)).approve(address(positionManager), liquiditySupply);
                (lpTokenId, , , ) = positionManager.mint(INonfungiblePositionManager.MintParams({
                    token0: token0,
                    token1: token1,
                    fee: fee,
                    tickLower: minTick,
                    tickUpper: maxTick,
                    amount0Desired: amount0Desired,
                    amount1Desired: amount1Desired,
                    amount0Min: 0,
                    amount1Min: 0,
                    recipient: address(this),
                    deadline: block.timestamp + 1200
                }));
    }

    function sendFees() external returns (uint256 amount0, uint256 amount1) {
        require(msg.sender == devWallet || msg.sender == teamWallet, "Not authorized to collect fees");
        INonfungiblePositionManager.CollectParams memory params =
            INonfungiblePositionManager.CollectParams({
                tokenId: lpTokenId,
                recipient: msg.sender,
                amount0Max: type(uint128).max,
                amount1Max: type(uint128).max
            });
        (amount0, amount1) = positionManager.collect(params);
    }
}
