    "contracts/interfaces/utils/IGasReimbursement.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface IGasReimbursement {
        // **************************************************
    // ****************** PUBLIC REGION *****************
    // **************************************************
    /// @notice Topup the contract with some gas coins
    /// @dev This is a public method that users should execute for topping up the contract with gas coins
    function topup() external payable;

    // **************************************************
    // ****************** BRIDGE REGION *****************
    // **************************************************
    /// @notice Reimburse the user with gas coins
    /// @dev This is a public method that bridge should execute for reimbursing the user with gas coins
    /// @param tokenAddress_ Address of the token that user is bridging
    /// @param reciever_ Address where the gas coins should be sent
    function reimburse(address tokenAddress_, address reciever_) external;

    // **************************************************
    // ***************** MODERATOR REGION ***************
    // **************************************************
    /// @notice Set the amount of gas coins that should be reimbursed per one token bridged
    /// @dev This is a public method that moderators should execute for setting the amount of gas coins that should be reimbursed per one token bridged
    /// @param tokenAddress_ Address of the token that user is bridging
    /// @param amount_ Amount of gas coins that should be reimbursed per one token bridged
    function setReimbursementAmount(address tokenAddress_, uint256 amount_) external;

    // **************************************************
    // *************** DEFAULT_ADMIN REGION *************
    // **************************************************
    /// @notice Initialize the contract
    /// @dev This is a public method that should be executed only once to initialize the contract
    /// @param defaultAdminAddress_ Address of the default admin
    /// @param moderatorAddress_ Address of the moderator
    /// @param bridgeAddresses_ Array of addresses of the bridges
    /// @param reinbursementAmounts_ Array of amounts of gas coins that should be reimbursed per one token bridged
    function initialize(
        address defaultAdminAddress_,
        address moderatorAddress_,
        address[] memory bridgeAddresses_,
        uint256[] memory reinbursementAmounts_
    ) external;


    // **************************************************
    // ************** PUBLIC GETTERS REGION *************
    // **************************************************
    /// @notice Returns if the contract has been initialised
    function isInitialized() external view returns (bool);

    /// @notice Returns the amount of gas coins that should be reimbursed per one token bridged
    /// @param tokenAddress_ Address of the token that user is bridging
    function reimbursementAmount(address tokenAddress_) external view returns (uint256);

    /// @notice Returns the balance of the contract
    function balance() external view returns (uint256);

    // **************************************************
    // ****************** EVENTS REGION *****************
    // **************************************************

    event Topup(address indexed from, uint256 value);
    event ReimbursementSuccess(address indexed to, uint256 value);
    event ReimbursementFailed(address indexed tokenAddress, address indexed to);
    event ReimbursementAmountSet(address indexed tokenAddress, uint256 value);
}
