    "contracts/TokenBridgeContract.sol": {
      "content": "// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.4;

// import "hardhat/console.sol";

import "@openzeppelin/contracts/access/extensions/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "./interfaces/utils/IGasReimbursement.sol";

/// @title TokenBridgeContract
/// @dev Centralised Bridge contract for migrating tokens
contract TokenBridgeContract is AccessControlEnumerable, ReentrancyGuard {

    using SafeERC20 for IERC20;

    bytes32 public constant MODERATOR_ROLE = keccak256("MODERATOR_ROLE");
    bytes32 public constant BRIDGEKEEPER_ROLE = keccak256("BRIDGEKEEPER_ROLE");

    bool private _initialised;
    uint private _chainId;
    uint private _migrationCounter;

    mapping (string => bool) private _whitelistedChains;
    mapping (string => uint) private _minimumBridgingAmounts;

    uint private _maxHotBalance;
    uint private _withdrawalTreshold;

    bool private _feeActivatedOnIn;
    bool private _feeActivatedOnOut;
    uint private _fee;
    uint private _feeDenominator = 10000;

    bool private _gasReimbursementActivated;

    IERC20 _tokenInstance;
    IGasReimbursement private _gasReimbursementInstance;
    address private _coldAddress;
    address private _feeAddress;

    constructor () {
        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());
    }

    // **************************************************
    // ****************** PUBLIC REGION *****************
    // **************************************************
    /// @notice Executes the migration process from this chain to external chain
    /// @dev This is a public method that users should execute for migration, after they approve the contract
    /// @param chainId_ The id of the chain where tokens will be migrated
    /// @param to_ Recieving address on the destination chain
    /// @param amount_ Amount of tokens you want to migrate
    function migrateOut(string memory chainId_, string memory to_, uint amount_ ) public {

        require(_whitelistedChains[chainId_], "ChainId is not whitelisted!");
        require(amount_ > getMinimumBridgingAmount(chainId_), "Transfer amount must be greater than minimum!");

        uint depositingAmount = amount_;
        uint feeAmount = 0;

        if (_feeActivatedOnOut) {
            feeAmount = (amount_ * _fee) / _feeDenominator;
            depositingAmount = amount_ - feeAmount;
            _transfer(_msgSender(), _feeAddress, feeAmount);
            emit FeeSent(_feeAddress, feeAmount);
        }
        _transfer(_msgSender(), address(this), depositingAmount);
        emit DepositReceived(_msgSender(), depositingAmount);
        emit MigrationOut(_migrationCounter, chainId_, _msgSender(), to_, depositingAmount, feeAmount);
        _migrationCounter++;

        if (getBalance() > _withdrawalTreshold){
            uint overflowAmount = getBalance() - _maxHotBalance;
            _transfer(address(this), _coldAddress, overflowAmount);
            emit OverflowManaged(_coldAddress, overflowAmount);
        }
    }

    /// @notice this method should be disabled because there is no way to send native coins back to the contract
    receive() external payable {
        revert("This contract does not accept direct value transfers!");
    }

    // Fallback function is called when msg.data is not empty
    fallback() external payable {
        revert("This contract does not accept direct value transfers!");
    }

    // **************************************************
    // *************** BRIDGEKEEPER REGION **************
    // **************************************************
    /// @notice Executes the migration process from external EVM chain to EVM chain from Bridgekeeper account
    /// @dev This is a public method that Bridgekeeper should execute for migrating funds into the chain
    /// @param chainId_ ChainId from where we are migrating
    /// @param from_ Sender of the tokens on the source chain
    /// @param to_ Reciever of tokens on the destination chain
    /// @param amount_ Amount of tokens to be migrated
    function migrateIn( 
        string memory chainId_, 
        uint migrationId_, 
        string memory from_, 
        address payable to_, 
        uint amount_
    ) public nonReentrant() onlyRole(BRIDGEKEEPER_ROLE) {
        uint recivingAmount = amount_;
        uint feeAmount = 0;

        if (_feeActivatedOnIn) {
            feeAmount = (amount_ * _fee) / _feeDenominator;
            _transfer(address(this), _feeAddress, feeAmount);
            emit FeeSent(_feeAddress, feeAmount);
            recivingAmount = amount_ - feeAmount;
        }

        _transfer(address(this), to_, recivingAmount);
        emit FundsSent(to_, recivingAmount);
        emit MigrationIn(chainId_, migrationId_, from_, to_, recivingAmount, feeAmount);

        if (_gasReimbursementActivated) {
            _gasReimbursementInstance.reimburse(address(this), to_);
        }
    }

    // **************************************************
    // *************** COLDSTORAGE REGION ***************
    // **************************************************
    function topUp(uint amount_) public onlyRole(MODERATOR_ROLE) {
        _transfer(_msgSender(), address(this), amount_);
        emit DepositReceived(_msgSender(), amount_);
    }

    // **************************************************
    // **************** MODERATOR REGION ****************
    // **************************************************
    /// @notice Sets new value for Chains that can be migrated to
    /// @dev This method is used if MODERATOR wants to add new migration path
    /// @param chainId_ Id of the chain that is being added to the whitelist
    /// @param newValue_ New value for the chainId
    function setWhitelistedChain(string memory chainId_, bool newValue_) public onlyRole(MODERATOR_ROLE) {
        require(_whitelistedChains[chainId_] != newValue_);
        emit WhitelistedChainChanged(chainId_, _whitelistedChains[chainId_], newValue_);
        _whitelistedChains[chainId_] = newValue_;
    }
    /// @notice Sets new value for the minimumBridgingAmount
    /// @dev This method is used if MODERATOR wants to change the minimum amount of tokens that can be bridged
    /// @param chainId_ Id of the destination chain to which the limit applies
    /// @param amount_ The smallest amount that users can bridge throug this bridge
    function setMinimumBridgingAmount(string memory chainId_, uint amount_) public onlyRole(MODERATOR_ROLE) {
        require(_minimumBridgingAmounts[chainId_] != amount_);
        emit MinimumBridgingAmountChanged(chainId_, _minimumBridgingAmounts[chainId_], amount_);
        _minimumBridgingAmounts[chainId_] = amount_;
    }

    /// @notice Sets new value for the maxHotBalance
    /// @dev This method is used if MODERATOR wants to change how many tokens are kept on the bridge
    /// @param maxHotBalance_ New value for the maxHotBalance
    function setMaxHotBalance(uint maxHotBalance_) public onlyRole(MODERATOR_ROLE) {
        require(_maxHotBalance != maxHotBalance_);
        require(_withdrawalTreshold > maxHotBalance_, "MaxHotBalance cannot be bigger than withdrawalTreshold!");
        emit MaxHotBalanceChanged(_maxHotBalance, maxHotBalance_);
        _maxHotBalance = maxHotBalance_;
    }
    /// @notice Sets new value for the withdrawalTreshold
    /// @dev This method is used if MODERATOR wants to change when the withdrawal to cold address happens
    /// @param withdrawalTreshold_ New value for the withdrawalTreshold
    function setWithdrawalTreshold(uint withdrawalTreshold_) public onlyRole(MODERATOR_ROLE) {
        require(_withdrawalTreshold != withdrawalTreshold_);
        require(withdrawalTreshold_ > _maxHotBalance, "WithdrawalTreshold must be bigger than maxHotBalance!");
        emit WithdrawalTresholdChanged(_withdrawalTreshold, withdrawalTreshold_);
        _withdrawalTreshold = withdrawalTreshold_;
    }

    /// @notice Activates or deactivates the fee on migrating in
    /// @dev This method is used if MODERATOR wants to change if the fee is active or disabled in migrateIn
    /// @param feeActivatedOnIn_ New value for the _feeActivatedOnIn
    function setFeeActivatedOnIn(bool feeActivatedOnIn_) public onlyRole(MODERATOR_ROLE) {
        require(_feeActivatedOnIn != feeActivatedOnIn_);
        emit FeeActivatedOnInChanged(_feeActivatedOnIn, feeActivatedOnIn_);
        _feeActivatedOnIn = feeActivatedOnIn_;
    }

    /// @notice Activates or deactivates the fee on migrating out
    /// @dev This method is used if MODERATOR wants to change if the fee is active or disabled in migrateOut
    /// @param feeActivatedOnOut_ New value for the _feeActivatedOnIn
    function setFeeActivatedOnOut(bool feeActivatedOnOut_) public onlyRole(MODERATOR_ROLE) {
        require(_feeActivatedOnOut != feeActivatedOnOut_);
        emit FeeActivatedOnOutChanged(_feeActivatedOnOut, feeActivatedOnOut_);
        _feeActivatedOnOut = feeActivatedOnOut_;
    }

    /// @notice Sets new value for the fee that is taken from the bridged tokens
    /// @dev This method is used if MODERATOR wants to change the amount of fee that is taken from the bridged tokens, amount is divided by divisor which means that 10000 is 100%
    /// @param fee_ New value for the fee field
    function setFee(uint fee_) public onlyRole(MODERATOR_ROLE) {
        require(_fee != fee_);
        require(fee_ < _feeDenominator, "Fee cannot be bigger than 100%!");
        emit FeeChanged(_fee, fee_);
        _fee = fee_;
    }

    /// @notice Activates or deactivates the gasReimbursement on the bridge
    /// @dev This method is used if MODERATOR wants to change if the gasReimbursement is active or disabled
    /// @param gasReimbursementActivated_ New value for the gasReimbursementActivated
    function setGasReimbursementActivated(bool gasReimbursementActivated_) public onlyRole(MODERATOR_ROLE) {
        require(_gasReimbursementActivated != gasReimbursementActivated_);
        emit GasReimbursementActivatedChanged(_gasReimbursementActivated, gasReimbursementActivated_);
        _gasReimbursementActivated = gasReimbursementActivated_;
    }

    /// @notice Sets new gasReimbursementInstance
    /// @dev This method is used if MODERATOR wants to change the gasReimbursementInstance
    /// @param gasReimbursementAddress_ Address of the contract where new GasReimbursementContract is deployed
    function setGasReimbursementInstance(address gasReimbursementAddress_) public onlyRole(MODERATOR_ROLE) {
        require(address(_gasReimbursementInstance) != gasReimbursementAddress_);
        emit GasReimbursementInstanceChanged(address(_gasReimbursementInstance), gasReimbursementAddress_);
        _gasReimbursementInstance = IGasReimbursement(gasReimbursementAddress_);
    }
    /// @notice Sets new value for the coldAddress
    /// @dev This method is used if MODERATOR wants to change the address where the tokens are sent when the withdrawalTreshold is reached
    /// @param coldAddress_ New value for the coldAddress
    function setColdAddress(address coldAddress_) public onlyRole(MODERATOR_ROLE) {
        require(_coldAddress != coldAddress_);
        emit ColdAddressChanged(_coldAddress, coldAddress_);
        _coldAddress = coldAddress_;
    }
    /// @notice Sets new value for the feeAddress
    /// @dev This method is used if MODERATOR wants to change the address where the fee is sent
    /// @param feeAddress_ New value for the feeAddress
    function setFeeAddress(address feeAddress_) public onlyRole(MODERATOR_ROLE) {
        require(_feeAddress != feeAddress_);
        emit FeeAddressChanged(_feeAddress, feeAddress_);
        _feeAddress = feeAddress_;
    }

    // **************************************************
    // *************** DEFAULT ADMIN REGION *************
    // **************************************************
    /// @notice Sets all the reqiered values for the contract to work
    /// @dev This method is used only once after the deploy to set up everything
    /// @param defaultAdminAddress_ Address of the default admin
    /// @param moderatorAddress_ Address of the moderator
    /// @param bridgeKeeperAddress_ Address of the bridgekeeper
    /// @param chainId_ Id of the chain where the contract is deployed
    /// @param maxHotBalance_ Max amount of tokens that can be kept on the bridge
    /// @param withdrawalTreshold_ Amount of tokens that must be on the bridge before the withdrawal to cold address happens
    /// @param coldAddress_ Address where the tokens are sent when the withdrawalTreshold is reached
    function init(
		address defaultAdminAddress_,
		address moderatorAddress_,
        address bridgeKeeperAddress_,
        uint chainId_,
        uint maxHotBalance_,
        uint withdrawalTreshold_,
        address coldAddress_,
        address tokenAddress_
	) public onlyRole(DEFAULT_ADMIN_ROLE) {
		require(!_initialised, "Contract is already initialised!");

		_grantRole(DEFAULT_ADMIN_ROLE, defaultAdminAddress_);
		_grantRole(MODERATOR_ROLE, defaultAdminAddress_);
		_grantRole(MODERATOR_ROLE, moderatorAddress_);
        _grantRole(BRIDGEKEEPER_ROLE, bridgeKeeperAddress_);

        _chainId = chainId_;

        _maxHotBalance = maxHotBalance_;
        _withdrawalTreshold = withdrawalTreshold_;

        _coldAddress = coldAddress_;
        _tokenInstance = IERC20(tokenAddress_);

		_initialised = true;
		_revokeRole(DEFAULT_ADMIN_ROLE, _msgSender());
	}

    // **************************************************
    // ************** INTERNAL REGION *******************
    // **************************************************
    function _transfer(address from_, address to_, uint amount_) internal {
        if (from_ == address(this)) {
            _tokenInstance.transfer(to_, amount_);
        } else {
            _tokenInstance.transferFrom(from_, to_, amount_);
        }
    }

    /// @notice Transfer tokens from the contract to desiered address
    /// @dev This method should be used if users accedentaly sends tokens to our contract address
    /// @param tokenAddress_ Token address of the token that we want to salvage
    /// @param to_ Destination where salvaged tokens will be sent
    /// @param amount_ Amount of tokens we want to salvage
    /// @return default return True after everything is processed
    function salvageTokensFromContract(address tokenAddress_, address to_, uint amount_) public returns (bool){
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender));
        IERC20(tokenAddress_).transfer(to_, amount_);
        return true;
    }

	// **************************************************
	// ************** PUBLIC GETTERS REGION *************
	// **************************************************
    /// @notice Returns if the contract has been initialised
	function isInitialised() public view returns (bool) {
		return _initialised;
	}
    /// @notice Returns the chainId of this chain
    function getChainId() public view returns (uint) {
        return _chainId;
    }
    /// @notice Returns the number of migrations that have been executed by users
    function getMigrationCounter() public view returns (uint) {
        return _migrationCounter;
    }

    /// @notice Returns if the chainId has been whitelised
    /// @param chainId_ ChainId that we want to check
    function getIsChainWhitelisted(string memory chainId_) public view returns(bool) {
        return _whitelistedChains[chainId_];
    }
    /// @notice Returns minimum amount of coins for bridging to another chain
    /// @param chainId_ ChainId that we want to check
    function getMinimumBridgingAmount(string memory chainId_) public view returns (uint) {
        return _minimumBridgingAmounts[chainId_];
    }

    /// @notice Returns the maxHotBalance
    function getMaxHotBalance() public view returns(uint) {
        return _maxHotBalance;
    }
    /// @notice Returns the withdrawalTreshold
    function getWithdrawalTreshold() public view returns(uint) {
        return _withdrawalTreshold;
    }

    /// @notice Returns true if fees are active on migrate in
    function feeActivatedOnIn() public view returns (bool) {
        return _feeActivatedOnIn;
    }
    /// @notice Returns true if fees are active on migrate out
    function feeActivatedOnOut() public view returns (bool) {
        return _feeActivatedOnOut;
    }
    /// @notice Returns fee and its denominator if you divide you get the % fee
    function getFee() public view returns (uint, uint) {
        return(_fee, _feeDenominator);
    }

    /// @notice Returns true if gas reimbursement is active
    function getGasReimbursementActivated() public view returns (bool) {
        return _gasReimbursementActivated;
    }

    /// @notice Returns the gas reimbursement address
    function getGasReimbursementAddress() public view returns(address) {
        return address(_gasReimbursementInstance);
    }
    /// @notice Returns the coldAddress
    function getColdAddress() public view returns(address) {
        return _coldAddress;
    }
    /// @notice Returns the feeAddress
    function getFeeAddress() public view returns(address) {
        return _feeAddress;
    }
    /// @notice Returns the address of the token contract
    function getTokenAddress() public view returns(address) {
         return address(_tokenInstance);
    }
    /// @notice Returns amount of tokens that are on the contract
    function getBalance() public view returns(uint) {
        return _tokenInstance.balanceOf(address(this));
    }

    // **************************************************
	// ****************** EVENTS REGION *****************
	// **************************************************

    event FeeSent(address indexed to, uint amount);
    event DepositReceived(address indexed from, uint amount);
    event MigrationOut(uint migrationId, string indexed chainId, address indexed from, string to, uint amount, uint feeAmount);
    event OverflowManaged(address indexed to, uint amount);

    event FundsSent(address indexed to, uint amount);
    event MigrationIn(string indexed chainId, uint migrationId, string indexed from, address indexed to, uint amount, uint feeAmount);

    event WhitelistedChainChanged(string chainId, bool oldValue, bool newValue);
    event MinimumBridgingAmountChanged(string chainId, uint oldValue, uint newValue);

    event MaxHotBalanceChanged(uint oldValue, uint newValue);
    event WithdrawalTresholdChanged(uint oldValue, uint newValue);

    event FeeActivatedOnInChanged(bool oldValue, bool newValue);
    event FeeActivatedOnOutChanged(bool oldValue, bool newValue);
    event FeeChanged(uint oldValue, uint newValue);

    event GasReimbursementActivatedChanged(bool oldValue, bool newValue);
    event GasReimbursementInstanceChanged(address oldValue, address newValue);

    event ColdAddressChanged(address oldAddress, address newAddress);
    event FeeAddressChanged(address oldAddress, address newAddress);
}
"
    }
