interface ERC20 {
    function balanceOf(address who) external view returns (uint256);
    function transfer(address to, uint256 value) external;
    function transferFrom(address from, address to, uint256 value) external;
}

contract Coin98MultiSend {
    // Transfer multi NFTs
    function transferMultiNFTs(address[] calldata tokenAddress, uint256[] calldata tokenID, address[] calldata receivers) public {
        require(receivers.length != 0 && receivers.length == tokenAddress.length && receivers.length == tokenID.length);

        for (uint i = 0; i < receivers.length; i++) {
             IERC721 meta = IERC721(tokenAddress[i]);
             meta.transferFrom(msg.sender, receivers[i],tokenID[i]);
        }
    }

    // Transfer multi ETH
    function transferMulti(address payable [] calldata receivers, uint256[] calldata amounts) public payable {
        require(msg.value > 0 && msg.value == getTotalSendingAmount(amounts));
        for (uint256 i = 0; i < amounts.length; i++) {
            receivers[i].transfer(amounts[i]);
        }
    }
    
    // Transfer multi token ERC20
    function transferMultiToken(address tokenAddress, address[] calldata receivers, uint256[] calldata amounts) public {
        require(receivers.length == amounts.length && receivers.length != 0);
        ERC20 token = ERC20(tokenAddress);

        for (uint i = 0; i < receivers.length; i++) {
            require(amounts[i] > 0);
            token.transferFrom(msg.sender, receivers[i], amounts[i]);
        }
    }
    
    function getTotalSendingAmount(uint256[] calldata _amounts) private pure returns (uint totalSendingAmount) {
        for (uint i = 0; i < _amounts.length; i++) {
            require(_amounts[i] > 0);
            totalSendingAmount += _amounts[i];
        }
    }
}
